INSERT INTO `dive_activity` VALUES ('49cd09de-740e-4e76-b516-d62485a70530', '2016-02-04 12:24:22', '', '马来西亚', '2017-04-10', '遨游锤头鲨之乡--拉央拉央', '上海、北京', '2016-04-05', null, 'AT02', '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', null, null, null, null, '', '', null, '0', 'uploadfiles/activity/activity_YbAX1454562260752.jpg', '<p style=\"text-align:center;\">\r\n	<span style=\"font-size:18px;\"><strong><span style=\"color:#003399;font-family:NSimSun;font-size:18px;\"><em>遨游锤头鲨之乡--拉央拉央</em></span></strong></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:18px;\"><strong><span style=\"color:#003399;font-family:NSimSun;font-size:18px;\"><span style=\"color:#FFE500;font-size:16px;\">☆</span><span style=\"color:#FFE500;font-size:16px;\"></span><br />\r\n</span></strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">拉央拉央岛 Pulau Layang-Layang ——弹丸礁虽处于偏僻孤立的地理位置，却幸运地拥有一个简易机</span><span style=\"font-family:NSimSun;font-size:14px;line-height:1.5;color:#666666;\">场，为欲前往拉央拉央岛的游客提供往返亚庇的固定航班服务。占据优势的地理、未受污染的珊瑚</span><span style=\"font-family:NSimSun;font-size:14px;line-height:1.5;color:#666666;\">礁、极佳的水底能见度，2000公尺深的峭壁加上远洋景观灯优越条件，造就了拉央拉央岛被冠为“世</span><span style=\"font-family:NSimSun;font-size:14px;line-height:1.5;color:#666666;\">界十大潜水胜地之一”的佳誉!</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:14px;\"><img alt=\"\" src=\"/attached/image/2016/02/04/0eb9ea26-5888-45eb-8e40-72304a08bc58.jpg\" /><span style=\"font-family:NSimSun;\"></span><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:16px;\"><img alt=\"\" src=\"/attached/image/2016/02/04/a219f569-9236-490b-b252-732dfdd625d5.jpg\" /><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;\"></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<table class=\"MsoNormalTable ke-zeroborder\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n		<tbody>\r\n			<tr>\r\n				<td width=\"121\" valign=\"top\" style=\"border:1pt solid #984806;\" rowspan=\"2\">\r\n					<p align=\"left\" class=\"MsoNormal\">\r\n						<span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">&nbsp; &nbsp;</span><span style=\"line-height:1.5;font-family:NSimSun;font-size:14px;color:#666666;\">出发时间</span> \r\n					</p>\r\n					<p align=\"left\" class=\"MsoNormal\">\r\n						<span style=\"font-family:宋体;font-size:12pt;\"></span> \r\n					</p>\r\n				</td>\r\n				<td width=\"185\" valign=\"top\" style=\"border:1pt solid #984806;\">\r\n					<p align=\"left\" class=\"MsoNormal\">\r\n						<span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">&nbsp;2016</span><span style=\"font-family:宋体;\"><span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">年</span><span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">4</span><span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">月</span><span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">5</span><span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">日</span><span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">-10</span><span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">日（</span><span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">6</span><span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">天</span><span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">5</span><span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">晚）</span></span><span style=\"font-family:宋体;font-size:12pt;\"></span> \r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td width=\"185\" valign=\"top\" style=\"border:1pt solid #984806;\">\r\n					<p align=\"left\" class=\"MsoNormal\">\r\n						<span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">&nbsp;</span><span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">2016</span><span style=\"line-height:1.5;font-family:宋体;\"><span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">年</span><span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">4</span><span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">月</span><span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">12</span><span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">日</span><span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">-17</span><span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">日（</span><span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">6</span><span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">天</span><span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">5</span><span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">晚）</span></span> \r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td width=\"306\" valign=\"top\" style=\"border:1pt solid #984806;\" colspan=\"2\">\r\n					<p align=\"center\" class=\"MsoNormal\" style=\"text-align:left;margin-left:0cm;\">\r\n						<span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">&nbsp;</span><span style=\"font-family:宋体;\"><span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">费用：</span><span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">RMB15200\\</span><span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">人</span></span><span style=\"font-family:宋体;font-size:12pt;\"></span> \r\n					</p>\r\n				</td>\r\n			</tr>\r\n			<tr>\r\n				<td width=\"306\" valign=\"top\" style=\"border:1pt solid #984806;\" colspan=\"2\">\r\n					<p align=\"center\" class=\"MsoNormal\" style=\"text-align:left;margin-left:0cm;\">\r\n						<span style=\"font-family:宋体;\"><span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">&nbsp;每期</span><span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">10</span><span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">人</span></span><span style=\"font-family:宋体;font-size:12pt;\"></span> \r\n					</p>\r\n				</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n</p>\r\n<div style=\"text-align:left;\">\r\n	<span style=\"line-height:1.5;\"></span><span style=\"color:#E56600;font-family:NSimSun;font-size:16px;\">集合点</span><span style=\"font-family:NSimSun;font-size:16px;\"> ：</span><span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">亚庇机场第一终站(Terminal 1)</span> \r\n</div>\r\n<p>\r\n	<span style=\"color:#E56600;line-height:1.5;font-family:NSimSun;font-size:16px;\">集合时间：</span> \r\n</p>\r\n<span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">行程1: 2016年4月5日早上5点</span><br />\r\n<p>\r\n	<span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">行程2: 2016年4月12日早上5点</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;\"> </span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<span style=\"color:#E56600;font-family:NSimSun;font-size:16px;\">离开时间&nbsp;</span><span style=\"font-family:NSimSun;font-size:16px;\">：&nbsp;</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:NSimSun;font-size:14px;color:#666666;\"><span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">第6天早上8点拉央拉央起飞，预计早上9点到亚庇机场解散。</span></span> \r\n</p>\r\n<span style=\"color:#99BB00;\"><span style=\"color:#64451D;font-family:NSimSun;font-size:14px;\"><strong>拉</strong></span><span style=\"color:#333333;font-family:NSimSun;\"><strong><span style=\"font-size:14px;color:#64451D;\">央拉央一般可以预期遇到的海洋生物有</span><span style=\"color:#64451D;\">：</span></strong></span></span><br />\r\n<span style=\"color:#DFC5A4;font-family:NSimSun;font-size:14px;\"><strong>锤头鲨、蝠鲼、鹰鳐、魔鬼鱼、白/黑鳍鲨、海狼、杰克鱼、隆头鹦哥、豆丁海马、海豚</strong></span><br />\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><img alt=\"\" src=\"/attached/image/2016/02/04/2300e51f-1a37-4bbd-bb66-d5efe7d8fd6f.jpg\" /></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><img alt=\"\" src=\"/attached/image/2016/02/04/ce06fdcb-94a9-41e0-b94b-0c141966059b.jpg\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><img alt=\"\" src=\"/attached/image/2016/02/04/a06175bd-f8fa-4061-8735-e156ed455076.jpg\" /><br />\r\n</span> \r\n</p>\r\n<br />\r\n<span style=\"color:#E56600;font-family:NSimSun;font-size:16px;\"><strong><em><u>费用包括：</u></em></strong></span><br />\r\n<p>\r\n	<span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">★马来西亚沙巴州亚庇来回拉央拉央包机费用。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">★拉央拉央度假村双人拼房。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">★1天5餐。</span> \r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span style=\"line-height:1.5;font-family:NSimSun;font-size:14px;color:#666666;\">★</span><span style=\"line-height:1.5;font-family:NSimSun;font-size:14px;color:#666666;\">1</span><span style=\"line-height:1.5;font-family:NSimSun;font-size:14px;color:#666666;\">2次船潜，包配重和气瓶，不包装备。第1天只有2次船潜，第2至第4天每天3次船潜， 第5天只有早</span><span style=\"line-height:1.5;font-family:NSimSun;font-size:14px;color:#666666;\">上1次潜水就开始倒数禁飞时间18小时。如果有不可避免的情况，度假村可随时更改潜水时间和次</span><span style=\"line-height:1.5;font-family:NSimSun;font-size:14px;color:#666666;\">数。</span><span style=\"color:#666666;font-family:NSimSun;font-size:14px;line-height:1.5;\">因为大部分都是深潜，每人鼓励要有自己的潜水电脑。参加这次行程的潜友可以优惠价格购买Mares</span><span style=\"color:#666666;font-family:NSimSun;font-size:14px;line-height:1.5;\">&nbsp;Smart潜水电脑表，每人只限一套！</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">★每人行李额25公斤，包括手提行李。超额每公斤马币15。</span> \r\n</p>\r\n<span style=\"font-family:NSimSun;font-size:14px;\"><span style=\"color:#666666;\">★10%高峰期附加费和6%消费税</span>。</span><br />\r\n<div style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><img alt=\"\" src=\"/attached/image/2016/02/04/75a8b4ee-cb42-4d4b-a941-580986a434bb.jpg\" /></span> \r\n</div>\r\n<p>\r\n	<span style=\"color:#E56600;font-family:NSimSun;font-size:16px;\"><strong><em><u>费用不包括：</u></em></strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">★国内到马来来回机票费用</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">★潜水装备租赁</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">★酒精饮料，其他饮料和矿泉⽔</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">★潜水/旅游保险</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">★度假村小费</span> \r\n</p>\r\n<span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">★签证费用</span><br />\r\n<br />\r\n<div style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><img alt=\"\" src=\"/attached/image/2016/02/04/0dd5c852-5b42-4757-8b2c-0b899e9f9e0c.jpg\" /></span> \r\n</div>\r\n<br />\r\n<span style=\"color:#E56600;font-family:NSimSun;font-size:16px;\"><strong><em>备注：</em></strong></span><br />\r\n<p>\r\n	<span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">1、预定需要支付50%的定金，出发前2个月支付全款。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">2、缴纳款项不退，可自行转让已缴费的名额。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">3、潜水员必须购买旅游和潜水保险。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">4、参加此次活动需有相关潜水证书：OW、AOW证书及以上。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:NSimSun;font-size:14px;color:#666666;\">5、遇特殊情况客人因潜水受伤，需要个人承担费用。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:NSimSun;font-size:14px;color:#666666;\"> </span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<span style=\"color:#337FE5;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;line-height:15px;\">☆ 感兴趣的小伙伴们欢迎来询/潜程小秘书“童童”（微信号：boyes127 或电话：18001661552)</span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:16px;\"><img alt=\"\" src=\"/attached/image/2016/02/04/59115a6b-ec8e-4a8c-8f93-ac2cdac45287.jpg\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:16px;\"><img alt=\"\" src=\"/attached/image/2016/02/04/5a3d21aa-25c6-48a5-bbd5-3813207e4db1.jpg\" /><br />\r\n</span> \r\n</p>');
INSERT INTO `dive_activity` VALUES ('4eca7326-25bb-41cb-bcac-f3dea6afc9df', '2015-11-24 22:04:57', '随着潜水运动风靡全球，潜水技巧知识的普及，走进水中世界已不再是一个童话般的心愿，而是一份令您惊喜不已的浪漫。零基础体验潜水，便可以在模拟水域里感受到潜水的新奇，想象一下当您徐徐潜入清凉明澈的水中，阳光被水折射成无数个星星，在眼前不断地闪烁、耀动，成串的气泡，欢快地漂过耳际，您会惊奇地发现您正轻松地扇动脚蹼，自如地悬浮于水中，真心体味水中世界的奇妙！人鱼时代即将开启！', '上海', '2017-01-31', '零基础体验潜水课程“第二期”', '上海', '2015-12-04', null, 'AT02', '6a040c09-5758-41f8-8910-bf556692e824', null, null, null, null, '', '', null, '0', 'uploadfiles/activity/activity_Cyhg1448373896964.jpg', '<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;font-size:14px;\"><span style=\"color:#4C33E5;font-size:18px;\"><strong><em>零</em></strong></span><span style=\"color:#4C33E5;font-size:18px;\"><strong><em>基</em></strong></span></span><span style=\"color:#337FE5;line-height:1.5;font-size:14px;\"></span><span style=\"line-height:1.5;font-size:14px;\"><span style=\"color:#003399;font-size:16px;\"><strong><span style=\"color:#4C33E5;font-size:18px;\"><em>础体验潜水课程</em></span><span style=\"color:#4C33E5;font-size:18px;\"><em>〖</em></span><span style=\"color:#4C33E5;\"></span></strong></span><span style=\"color:#003399;font-size:16px;\"><strong><span style=\"color:#4C33E5;font-size:18px;\"><em>第二期</em></span><span style=\"color:#4C33E5;font-size:18px;\"><em>〗</em></span></strong></span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;font-size:24px;\"><span style=\"color:#003399;font-size:24px;\"><strong><span style=\"color:#4C33E5;font-size:24px;\"><span style=\"color:#B8D100;font-size:24px;\">☼</span></span></strong></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#999999;\"><span style=\"color:#000000;\"><span style=\"font-size:14px;\">&nbsp;</span><span style=\"font-size:14px;color:#666666;\">随着潜水运动风靡全球，潜水技巧知识的普及，走进水中世界已不再是一个童话般的心愿，而是</span></span></span><span style=\"font-size:14px;line-height:1.5;color:#666666;\">一份令您惊喜不已的浪漫。零基础体验潜水，便可以在模拟水域里感受到潜水的新奇，想象一下当您</span><span style=\"font-size:14px;line-height:1.5;color:#666666;\">徐徐潜入清凉明澈的水中，阳光被水折射成无数个星星，在眼前不断地闪烁、耀动，成串的气泡，欢</span><span style=\"font-size:14px;line-height:1.5;color:#666666;\">快地漂过耳际，您会惊奇地发现您正轻松地扇动脚蹼，自如地悬浮于水中，真心体味水中世界的奇</span><span style=\"font-size:14px;line-height:1.5;color:#666666;\">妙！人鱼时代即将开启！</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#999999;font-size:14px;\"><img alt=\"\" src=\"/attached/image/2015/11/24/415bdfe3-238f-4888-b4bf-03e5c4034367.jpg\" /></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#999999;font-size:14px;\"><span style=\"color:#00D5FF;\"></span><span style=\"color:#000000;\"><span style=\"color:#4C33E5;font-size:14px;\">【体验时间】</span><span style=\"font-size:14px;color:#666666;\">2015年12月4日（星期五下午18：30-21：00 ）</span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span><br />\r\n</span> <span style=\"color:#000000;\"><span style=\"color:#4C33E5;font-size:14px;\">【体验地点】</span><span style=\"font-size:14px;color:#666666;\">杨浦潜水中心泳池（上海杨浦区翔殷路800号）</span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span><br />\r\n</span> <span style=\"color:#000000;\"><span style=\"color:#4C33E5;font-size:14px;\">【本期人数】</span><span style=\"font-size:14px;color:#666666;\">6人（先报先体验人满截止）</span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span><br />\r\n</span> <span style=\"color:#000000;\"><span style=\"color:#4C33E5;font-size:14px;\">【费用】</span><span style=\"font-size:14px;color:#666666;\">460∕人&nbsp;</span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#666666;font-size:14px;\">费用包括：潜水馆使用费</span><br />\r\n<span style=\"color:#666666;font-size:14px;\">气瓶费、教练费（SSI专业潜水教练）、潜水BC装备费用</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span><br />\r\n</span> <span style=\"color:#4C33E5;font-size:14px;\">【体验内容】</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;\"> <span style=\"color:#666666;font-size:14px;\">30分钟岸上拉伸、潜水准备工作</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;\"> <span style=\"color:#666666;font-size:14px;\">30分钟理论讲解</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;\"> <span style=\"color:#666666;font-size:14px;\">10分钟安全讲解</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;\"> <span style=\"color:#666666;font-size:14px;\">40分钟水下体验</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#666666;font-size:14px;\"><br />\r\n</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;\"> <span style=\"color:#000000;\"><span style=\"color:#4C33E5;font-size:14px;\">【温馨提示】</span><span style=\"font-size:14px;color:#666666;\">自备游衣游裤、换洗衣服、毛巾、拖鞋、清洗护肤用品等私人物品;本次体验潜水所用装</span></span></span><span style=\"color:#666666;font-size:14px;line-height:1.5;\">备均由SSI提供!</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;\"> <br />\r\n<span><img alt=\"\" src=\"/attached/image/2015/11/24/76bc6c3f-78c1-4c7f-b8a5-1bcc441902d5.jpg\" /></span><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<img alt=\"\" src=\"/attached/image/2015/11/24/a8345074-a2c6-498d-b63f-72bc329f90c6.jpg\" /> \r\n</p>\r\n<p>\r\n	<span style=\"color:#337FE5;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;line-height:15px;\">☆ 感兴趣的小伙伴们欢迎来询/潜程小秘书“童童”（微信号：boyes127 或电话：18001661552)</span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#337FE5;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;line-height:15px;\"><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\">|</span>\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><br />\r\n</span>\r\n</p>');
INSERT INTO `dive_activity` VALUES ('571ff9f6-d0c2-45d1-8ace-659d6b6dce9f', '2016-03-29 19:12:40', '', '菲律宾 杜马盖地', '2016-07-31', '【OW初级考证行程】•杜马盖地站', '上海', '2016-05-07', null, 'AT02', '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', null, null, null, null, '', '', null, '0', 'uploadfiles/activity/activity_OZlW1459250608225.jpg', '<p style=\"text-align:center;\">\r\n	<span style=\"font-size:12px;font-family:NSimSun;color:#666666;\"><strong><em><span style=\"font-size:18px;color:#003399;\">【OW初级考证行程】•杜马盖地站</span></em></strong><strong><em><span style=\"font-size:16px;color:#00D5FF;\"></span></em></strong><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:12px;font-family:NSimSun;color:#666666;\"><strong><em><span style=\"font-size:16px;color:#00D5FF;\"><span style=\"color:#FFE500;\">＆</span><span style=\"color:#FFE500;\"></span><br />\r\n</span></em></strong></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:12px;font-family:NSimSun;color:#666666;\"><img src=\"/attached/image/2016/03/29/cd4fb9ed-d9e0-4773-9e30-f4895bc85870.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span style=\"color:#666666;font-family:NSimSun;line-height:1.5;font-size:14px;\"><span style=\"font-size:14px;\">杜马盖地（Dumaguete）也称为杜马格特，位于菲律宾第五大岛——内格罗斯岛（Negros）的东南部，</span></span><span style=\"font-size:14px;color:#666666;font-family:NSimSun;line-height:1.5;\">是东内格罗斯省的省会。整个城市不大，却有浓浓的书卷气和清新感。杜马盖地市中心的大部分地区</span><span style=\"color:#666666;font-family:NSimSun;font-size:14px;line-height:1.5;\">坐落着当地高校，除亚洲最古老的美式大学Siliman University外，还有东内格罗州立大学等多所高</span><span style=\"color:#666666;font-family:NSimSun;font-size:14px;line-height:1.5;\">校，这座城市还得名“大学城”。此外，杜马盖地距离APO岛、苏米龙岛（Sumilon），以及可以与鲸</span><span style=\"color:#666666;font-family:NSimSun;font-size:14px;line-height:1.5;\">鲨“共舞”的Oslob都很近，海底生物丰富，交通便利！</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/03/29/d3882a71-8da3-44b4-b546-9779baa1242b.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/03/29/da35861c-8be7-4305-8548-066919e05d18.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:NSimSun;color:#666666;\">菲律宾的水温暖干净，因此有世界上最独特和最美丽的海洋生物。杜马盖地有超过3000种鱼和448种珊</span><span style=\"color:#666666;font-family:NSimSun;font-size:14px;line-height:1.5;\">瑚（与整个加勒比海的700种鱼和100种珊瑚相比）。</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/03/29/e93c2150-57e8-4ebd-be92-982573accc27.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:NSimSun;color:#666666;\">与嘈杂喧嚣的宿雾（菲律宾第二大城市）相比，杜马格特不仅具有田园般的悠闲，也有很浓的书卷</span><span style=\"color:#666666;font-family:NSimSun;font-size:14px;line-height:1.5;\">气，人间烟火与世外桃源在这座小城得以完美结合，这也是大批西方人在此定居的原因之一。</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:12px;font-family:NSimSun;color:#666666;\"><img src=\"/attached/image/2016/03/29/8daf0244-4620-4696-940e-92d9530bb6cf.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<br />\r\n<p>\r\n	<span style=\"font-size:12px;font-family:NSimSun;\"><strong><span style=\"color:#DFC5A4;font-size:16px;\">行程时间</span></strong><span style=\"font-size:14px;color:#666666;\">：</span><span style=\"color:#666666;font-size:14px;\">5月、6月、7月 （ 4人起出发）</span></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:NSimSun;\"><strong><span style=\"color:#DFC5A4;font-size:16px;\">行程安排</span></strong><span style=\"font-size:14px;color:#666666;\">：</span><span style=\"color:#666666;font-size:14px;\">5天4晚&nbsp;（2人一间，单人补房差310RMB/晚）</span></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;font-family:NSimSun;color:#DFC5A4;\"><strong>行程费用</strong></span> \r\n</p>\r\n<span style=\"font-size:12px;font-family:NSimSun;\"><span style=\"color:#666666;font-size:14px;\">非潜水员</span><span style=\"font-size:14px;color:#666666;\">：</span><span style=\"color:#666666;font-size:14px;\">5480 RMB（含OW证书）、潜水员：3950元 RMB（9潜）</span></span><br />\r\n<br />\r\n<span style=\"font-size:16px;font-family:NSimSun;color:#DFC5A4;\"><strong>潜水度假村介绍</strong></span><br />\r\n<p>\r\n	<span style=\"font-size:14px;font-family:NSimSun;color:#666666;\">Sta. Monica Beach Club酒店距离杜马格特机场（Dumaguete Airport）有10分钟车程，设有室外游泳</span><span style=\"color:#666666;font-family:NSimSun;font-size:14px;line-height:1.5;\">池和覆盖公共区域的免费无线网络连接。 客房配有天井，提供空调、可收看有线频道的平面电视、私</span><span style=\"color:#666666;font-family:NSimSun;font-size:14px;line-height:1.5;\">人保险箱和衣柜。连接浴室配有热水淋浴设施和洗浴用品。 Lanai餐厅供应国际美食，而池畔酒吧提</span><span style=\"color:#666666;font-family:NSimSun;font-size:14px;line-height:1.5;\">供各式小吃。 前台可为客人提供按摩服务和机场班车服务，客人还可以使用会议/宴会区。酒店设有</span><span style=\"color:#666666;font-family:NSimSun;font-size:14px;line-height:1.5;\">免费的私人停车场。 酒店距离Siliman大学有10分钟车程，距离Sidlakang Negros有15分钟车程，距</span><span style=\"color:#666666;font-family:NSimSun;font-size:14px;line-height:1.5;\">离普朗巴托瀑布35分钟车程！</span> \r\n</p>\r\n<br />\r\n<img src=\"/attached/image/2016/03/29/58a171f9-d5eb-4cc0-8cc7-16525bad61e2.jpg\" alt=\"\" /><br />\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:12px;font-family:NSimSun;color:#337FE5;\"><strong><img src=\"/attached/image/2016/03/29/1e7cb575-58ba-4891-8b58-6450033fd851.jpg\" alt=\"\" /><br />\r\n</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:NSimSun;color:#337FE5;\"><strong><br />\r\n</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;font-family:NSimSun;color:#337FE5;\"><strong>费用包含</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:NSimSun;color:#666666;\"><span style=\"color:#000000;font-size:14px;\">★</span><span style=\"font-size:14px;\">机场到度假中心的来回接送</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:NSimSun;color:#666666;\"><span style=\"color:#000000;font-size:14px;\">★</span><span style=\"font-size:14px;\">潜水员包含：9次潜水，含气瓶，配重，潜导，前往Apo岛、Sumilo岛、Oslob岛往返船费及当日午</span></span><span style=\"font-size:14px;color:#666666;font-family:NSimSun;line-height:1.5;\">餐“便当”</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:NSimSun;color:#666666;\"><span style=\"color:#000000;font-size:14px;\">★</span><span style=\"font-size:14px;\">公共区域免费WiFi</span></span><span style=\"font-size:14px;color:#666666;font-family:NSimSun;line-height:1.5;\">&nbsp;</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:NSimSun;color:#666666;\"><span style=\"color:#000000;font-size:14px;\">★</span><span style=\"font-size:14px;\">4晚标准间，两人一间</span></span> \r\n</p>\r\n<span style=\"font-size:12px;font-family:NSimSun;color:#666666;\"><span style=\"color:#000000;font-size:14px;\">★</span><span style=\"font-size:14px;\">每日早餐</span></span><br />\r\n<p>\r\n	<span style=\"font-size:12px;font-family:NSimSun;color:#666666;\"><span style=\"color:#000000;font-size:14px;\">★</span><span style=\"font-size:14px;\">非潜水员（OW考证）包含：证书、教练、气瓶、配重</span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:12px;font-family:NSimSun;\"><img src=\"/attached/image/2016/03/29/347e69eb-3a5b-4216-a780-bd5bd4be383d.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<br />\r\n<span style=\"font-size:16px;font-family:NSimSun;color:#337FE5;\"><strong>费用不包含</strong></span><br />\r\n<p>\r\n	<span style=\"font-size:12px;font-family:NSimSun;\"><span style=\"font-size:14px;\">★</span><span style=\"color:#666666;font-size:14px;\">所有午、晚餐不包括（除前往小岛潜水便当外、酒店附近布满了大大小小的餐厅，人均300-</span></span><span style=\"color:#666666;font-size:14px;font-family:NSimSun;line-height:1.5;\">600PHP，酒店晚餐自助餐500PHP/人，选择多样）</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:NSimSun;\"><span style=\"font-size:14px;\">★</span><span style=\"color:#666666;font-size:14px;\">所有机票费用不包含</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:NSimSun;\"><span style=\"font-size:14px;\">★</span><span style=\"color:#666666;font-size:14px;\">海岸公园及环境保育费：道因地区：PHP100/潜，APO岛：PHP300/天，Sumilo岛：PHP200/天，Oslob</span></span><span style=\"color:#666666;font-size:14px;font-family:NSimSun;line-height:1.5;\">观鲸鲨：PHP1500/潜</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:NSimSun;\"><span style=\"font-size:14px;\">★</span><span style=\"color:#666666;font-size:14px;\">小费</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:NSimSun;\"><span style=\"font-size:14px;\">★</span><span style=\"color:#666666;font-size:14px;\">装备租赁费用 （整套500披索/天/人）</span></span> \r\n</p>\r\n<span style=\"font-size:12px;font-family:NSimSun;\"><span style=\"font-size:14px;\">★</span><span style=\"color:#666666;font-size:14px;\">酒精和软饮料</span></span><br />\r\n<p>\r\n	<span style=\"font-size:12px;font-family:NSimSun;\"><span style=\"font-size:14px;\">★</span><span style=\"color:#666666;font-size:14px;\">菲律宾签证、保险费用</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;font-family:NSimSun;\"><span style=\"color:#666666;\"><br />\r\n</span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:12px;font-family:NSimSun;\"><span style=\"color:#666666;\"><img src=\"/attached/image/2016/03/29/d179b338-abff-4a37-b397-451fc51fa844.jpg\" alt=\"\" /><br />\r\n</span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:12px;font-family:NSimSun;\"><span style=\"color:#666666;\"><img src=\"/attached/image/2016/03/29/55ffe5e2-98ad-46bd-bf97-7b48bec3e363.jpg\" alt=\"\" /><br />\r\n</span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:12px;font-family:NSimSun;\"><span style=\"color:#666666;\"><img src=\"/attached/image/2016/03/29/15c15eb5-f9e0-4db7-b9e4-f8bfc209aa5a.jpg\" alt=\"\" /><br />\r\n</span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:12px;font-family:NSimSun;\"><span style=\"color:#666666;\"></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#337FE5;\">感兴趣的童鞋们欢迎咨询【潜程】小秘书：boyes127 微信 或致电：18001661552！</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#DFC5A4;font-size:9px;\"><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#DFC5A4;font-size:9px;\"><span style=\"color:#000000;\">|</span><span style=\"color:#000000;\"></span><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#DFC5A4;font-size:9px;\"><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:9px;\"><span style=\"color:#64451D;\"><strong></strong></span><span style=\"color:#64451D;font-size:10px;\"><strong><em>邂逅</em></strong></span><strong><span>&nbsp;</span><span style=\"color:#333333;\">☼</span><span>&nbsp;</span></strong><span style=\"color:#FF9900;font-family:NSimSun;\"><strong><em>Dumaguete</em></strong></span><strong><span style=\"color:#003399;\"></span></strong><br />\r\n</span> \r\n</p>\r\n<div style=\"text-align:center;\">\r\n	<br />\r\n</div>');
INSERT INTO `dive_activity` VALUES ('5b4235a0-971b-45e2-a51b-16f23e4de7fd', '2016-02-03 21:43:31', '', '菲律宾 图巴塔哈', '2017-04-05', '2016走进“大货“天堂- - 图巴塔哈', '上海', '2016-03-30', null, 'AT02', '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', null, null, null, null, '', '', null, '0', 'uploadfiles/activity/activity_lxg41454567865335.jpg', '<p style=\"text-align:center;\">\r\n	<span style=\"color:#003399;line-height:1.5;font-size:18px;\"><em><strong>【201</strong></em></span><span style=\"color:#003399;line-height:1.5;font-size:18px;\"><em><strong>6走进“大货“天堂-&nbsp;-&nbsp;图巴塔哈】</strong></em></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#003399;line-height:1.5;font-size:18px;\"><strong><span style=\"color:#E53333;\">☆</span></strong></span> \r\n</p>\r\n<span style=\"color:#64451D;font-size:16px;\"><strong>图巴塔哈群礁概况</strong></span><br />\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">菲律宾图巴塔哈群礁国家公园位于巴拉望岛公主港以东180公里处，面积达332平方公里。由于距离陆</span><span style=\"color:#666666;font-size:14px;line-height:1.5;\">地非常遥远，只有船宿才能够到达那里。尽管图巴塔哈潜水只能够通过船宿的方式，但由于这里珊瑚</span><span style=\"color:#666666;font-size:14px;line-height:1.5;\">礁极其壮观，从而吸引了大批潜水者，因此这里的船宿潜水的座席常常供不应求。每年有不超过200</span><span style=\"color:#666666;font-size:14px;line-height:1.5;\">名中国籍潜水员来到这里，感受图巴塔哈水下的自然奇迹。</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<div style=\"text-align:center;\">\r\n	<img alt=\"\" src=\"/attached/image/2016/03/29/00f6d723-12e5-4480-96cd-1caedcd5c33f.jpg\" /><br />\r\n</div>\r\n<p>\r\n	<span style=\"color:#64451D;font-size:12px;\"><strong><br />\r\n</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#64451D;font-size:14px;\"><strong>鲸鲨、锤头鲨、各种鲨，大货天堂</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">图巴塔哈群礁是看“海洋大货”的天堂，11种鲨鱼以及13种鲸豚生活在这里。锤头鲨、鲸鲨、Manta都</span><span style=\"font-size:14px;line-height:1.5;color:#666666;\">是这里的常住居民，此外这里经常可以看到成群的鹦鹉鱼、白鳍鲨、黑鳍鲨、杰克风暴、海狼风暴、</span><span style=\"font-size:14px;line-height:1.5;color:#666666;\">玳瑁等。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;font-size:16px;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">图巴塔哈群礁被公认为是东南亚最好的潜水地点，曾提名竞选“世界自然新七大奇迹”，在由CNN评选</span><span style=\"font-size:14px;line-height:1.5;color:#666666;\">的世界最佳50个潜水点中排名第八。</span> \r\n</p>\r\n<div style=\"text-align:center;\">\r\n	<img alt=\"\" src=\"/attached/image/2016/03/29/ca70f1d8-118a-414d-b1f5-4271a0e22b63.jpg\" /><br />\r\n</div>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:16px;\"><img alt=\"\" src=\"/attached/image/2016/03/29/dafab5a6-5ddb-42ab-86fe-41b11228ea22.jpg\" /><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">菲律宾1000元比索背面就印的图巴塔哈国家公园地图！</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:16px;\"><img alt=\"\" src=\"/attached/image/2016/03/29/bd98b83b-2658-4fb7-a75e-fb38703d0b86.jpg\" /><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#64451D;font-size:12px;\"><strong><br />\r\n</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#64451D;font-size:16px;\"><strong>豪华游轮，新船首航</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">Discovery&nbsp;Adventure是Discovery&nbsp;Fleet公司在2015年最新推出的豪华船宿游轮。按亚洲人审美习惯精</span><span style=\"font-size:14px;line-height:1.5;color:#666666;\">心设计，全力呈现豪华和舒适的旅程体验。船上住宿、卫浴、休闲沙龙等设施媲美5星级豪华酒店。</span><span style=\"color:#666666;font-size:14px;line-height:1.5;\">全长47米，宽度8.50米，稳性舒适度极高。</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img alt=\"\" src=\"/attached/image/2016/02/03/82a84f43-08b0-4ec6-89c1-40ca26625339.jpg\" /> \r\n</p>\r\n<div style=\"text-align:center;\">\r\n	<span style=\"line-height:24px;font-size:16px;\"></span> \r\n</div>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">船舱内标准房8间，可容纳16人，上层豪华景观房6间，可容纳12人，上层豪华景观房使用面积更达</span><span style=\"color:#666666;font-size:14px;line-height:1.5;\">18&nbsp;-&nbsp;22平方米，全船可接待28位尊贵的客人。</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img alt=\"\" src=\"/attached/image/2016/02/03/e365d7b1-06d0-4d53-870e-c5dad525a324.jpg\" /> \r\n</p>\r\n<div style=\"text-align:center;\">\r\n	<span style=\"line-height:24px;font-size:16px;\"></span> \r\n</div>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">此外，船内有20-32名船员和服务人员提供高水准的各项服务（包含潜导4-5名）。公共区域配备空调</span><span style=\"font-size:14px;color:#666666;\">沙龙，摄影设备室，阳台，甲板餐厅等。船公司多年运营船宿行程，经验丰富。</span><span style=\"line-height:1.5;font-size:16px;\"></span><img alt=\"\" src=\"/attached/image/2016/02/03/ae88aca1-595d-47a9-a7d7-7710918b275a.jpg\" /> \r\n</p>\r\n<div style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"></span> \r\n</div>\r\n<span style=\"color:#64451D;font-size:16px;\">Discovery&nbsp;Adventure&nbsp;图巴塔哈7天6晚船宿(船宿价格)</span><br />\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">出行日期：2016年3月30日—4月5日</span> \r\n</p>\r\n<span style=\"font-size:16px;\"><span style=\"font-size:14px;color:#666666;\">标准仓位/位：</span><span style=\"line-height:24px;font-size:14px;color:#666666;\">$2450</span></span><br />\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">豪华舱位/位：</span><span style=\"font-size:14px;color:#666666;\">$2750</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<strong><span style=\"color:#64451D;font-size:16px;\">费用包括：</span></strong> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">★公主港机场及码头来回接送（限登船日/离船日当天有效）</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">★6晚船宿</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">★每日三餐</span> \r\n</p>\r\n<span style=\"font-size:14px;color:#666666;\">★每日白天3潜+1次夜潜，下潜包括气瓶，配重及潜导</span> \r\n<p>\r\n	<span style=\"color:#64451D;font-size:16px;\"><strong>费用不包括：</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">★所有机票费用</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">★潜水装备租赁</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">★酒精饮料，其他饮料和矿泉水</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">★潜水/旅游保险</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">★Tubbataha公园门票每人USD75</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">★高氧</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">★全体船员小费</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">★签证费用</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<span style=\"color:#003399;\"><strong><span style=\"font-size:16px;\">行程安排</span> \r\n<hr />\r\n</strong></span>\r\n<p>\r\n	<span style=\"color:#00D5FF;\">3月30</span><span style=\"color:#00D5FF;line-height:1.5;\">号</span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#666666;font-size:14px;\">14：00—16：00登船</span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#666666;font-size:14px;\"><span style=\"color:#666666;font-size:14px;\">由专车免费从酒店或者机场接至公主港港口</span></span><span style=\"color:#666666;font-size:14px;\">3月31日</span>\r\n</p>\r\n<span><span style=\"color:#666666;font-size:14px;\">Tubbataha&nbsp;Reef&nbsp;每日4潜</span> \r\n<hr />\r\n</span> \r\n<p>\r\n	<span style=\"color:#00D5FF;font-size:14px;\">4月1</span><span style=\"color:#00D5FF;line-height:1.5;font-size:14px;\">日</span>\r\n</p>\r\n<span><span style=\"color:#666666;font-size:14px;\">Tubbataha&nbsp;Reef&nbsp;每日4潜</span> \r\n<hr />\r\n</span>\r\n<p>\r\n	<span style=\"color:#00D5FF;font-size:14px;\">4月2</span><span style=\"color:#00D5FF;font-size:14px;line-height:1.5;\">日</span>\r\n</p>\r\n<span><span style=\"color:#666666;font-size:14px;\">Tubbataha&nbsp;Reef&nbsp;每日4潜</span> \r\n<hr />\r\n</span>\r\n<p>\r\n	<span style=\"color:#00D5FF;font-size:14px;\">4月3</span><span style=\"color:#00D5FF;font-size:14px;line-height:1.5;\">日</span>\r\n</p>\r\n<span><span style=\"color:#666666;font-size:14px;\">Tubbataha&nbsp;Reef&nbsp;每日4潜</span> \r\n<hr />\r\n</span>\r\n<p>\r\n	<span style=\"color:#00D5FF;font-size:14px;\">4月4</span><span style=\"color:#00D5FF;font-size:14px;line-height:1.5;\">日</span>\r\n</p>\r\n<span><span style=\"color:#666666;font-size:14px;\">Tubbataha&nbsp;Reef&nbsp;每日4潜</span> \r\n<hr />\r\n</span>\r\n<p>\r\n	<span style=\"color:#00D5FF;font-size:14px;\">4月5</span><span style=\"color:#00D5FF;font-size:14px;line-height:1.5;\">日</span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#666666;font-size:14px;\">8：00到达公主港，解散</span>\r\n</p>\r\n<span><span style=\"color:#666666;font-size:14px;\">由专车免费从公主港港口送至酒店或者机场</span> \r\n<hr />\r\n</span><br />\r\n<span style=\"color:#003399;font-size:16px;\"><strong>-&nbsp;-&nbsp;-特色之旅：只开三个月，再看等一年</strong></span><br />\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">菲律宾国家海洋公园之有“大货天堂”之称的图巴塔哈，每年仅在在3月底至6月初开放。每逢“图巴塔哈</span><span style=\"font-size:14px;line-height:1.5;color:#666666;\">潜水季”，持政府许可运营的十余艘潜水船往往提前半年就被来自世界各国的潜水员预订一空！每年有</span><span style=\"font-size:14px;line-height:1.5;color:#666666;\">不超过200名中国籍潜水员来到这里，感受图巴塔哈水下的自然奇迹&nbsp;！</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:12px;\"><img alt=\"\" src=\"/attached/image/2016/03/29/716fa8bd-a269-46fc-9d5e-eaaeb85c021b.jpg\" /><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"color:#003399;font-size:16px;\"><strong>备注</strong></span><br />\r\n<span style=\"font-size:14px;color:#666666;\">1、参与此船宿需至少有40瓶潜水记录(AOW证书以上)。&nbsp;</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">2、缴纳款项不退，可自行转让已缴费的名额。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">3、潜水员必须购买旅游和潜水保险。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">4、潜水保险及潜水证书作为必须条件需在启航之前交送副本。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">5、船潜潜水员必须要有潜水电脑才能潜水，而且必须了解自己所使用的潜水电脑，如果碰到潜水电</span><span style=\"font-size:14px;line-height:1.5;color:#666666;\">脑锁死的情况，不允许参与潜水。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"color:#666666;\">6、遇特殊情况客人因潜水受伤，需要个人承担费用</span>。</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:16px;\"><img alt=\"\" src=\"/attached/image/2016/03/29/fc55a9f0-9d40-43e3-b0dc-6d9d4a958cf2.jpg\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:16px;\"><img alt=\"\" src=\"/attached/image/2016/03/29/d14f7d11-d3cd-4530-aa1d-a554ee3cb84c.jpg\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:16px;\"><img alt=\"\" src=\"/attached/image/2016/03/29/7f8c1e1f-7c2e-4d49-a225-a6b7fe8540a3.jpg\" /><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#337FE5;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;line-height:15px;\">☆ 感兴趣的小伙伴们欢迎来询/潜程小秘书“童童”（微信号：boyes127 或电话：18001661552)</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#337FE5;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;line-height:15px;\"><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\">|</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>');
INSERT INTO `dive_activity` VALUES ('5c36a2b8-713e-41bf-b206-03e04da4e645', '2016-04-27 20:23:40', '寻找 『伊甸园』', '菲律宾 巴拉望 科隆岛', '2016-10-30', '〖潜水行程〗·巴拉望科隆站', '上海', '2016-06-10', null, 'AT01', '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', null, null, null, null, '', '', null, '0', 'uploadfiles/activity/activity_q6By1461762138035.jpg', '<p style=\"text-align:center;\">\r\n	<em><span style=\"color:#E56600;font-size:18px;font-family:NSimSun;\"><strong>寻找伊甸园 ·巴拉望科隆站岛</strong></span></em> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<strong><span style=\"color:#666666;font-size:18px;\"><em>＆</em></span></strong> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/04/27/e0d54b19-7174-44c9-b444-6704f55aea05.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">在菲律宾西南部的南海与苏禄海之间，一个狭长窈窕的海岛如彩虹般横卧于海天尽头，飘渺如仙山海</span><span style=\"color:#666666;font-size:14px;line-height:1.5;\">市，它就是有“海边乌托邦”之称的巴拉望岛，菲律宾的最后一块生态处女地。巴拉望是菲律宾最大的</span><span style=\"font-size:14px;color:#666666;\">省，由1800个岛屿组成，科隆就是其</span><span style=\"font-size:10px;\"></span><span style=\"font-size:14px;color:#666666;\">中一颗闪耀的星星。</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><img src=\"/attached/image/2016/04/27/f94d5498-7844-45f9-aead-c8487f79b109.jpg\" alt=\"\" /></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><img src=\"/attached/image/2016/04/27/e3ce28df-2a9d-42c0-8158-9c07a97ab9dd.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#003399;font-size:16px;\"><strong>美景</strong></span><span style=\"color:#003399;font-size:16px;\">：</span><span><span style=\"font-size:14px;\">&nbsp;</span><span style=\"color:#666666;font-size:14px;\">“Lonely Planet孤独星球” 评选为2013年世界最佳旅游目的地－巴拉望科隆岛， “现代伊甸</span></span><span style=\"color:#666666;font-size:14px;line-height:1.5;\">园”“菲律宾最后一片净土”。科隆岛汇集了成千上万座耀眼夺目、此起彼伏的岛屿，环绕一条2,000公</span><span style=\"color:#666666;font-size:14px;line-height:1.5;\">里长的原始海岸线。极有价值的喀斯特海上石林地貌，经过亿万年地质的变化，形成大、小石林组成</span><span style=\"color:#666666;font-size:14px;line-height:1.5;\">的石灰崖奇峰异洞和隐密的泻湖，且又有洁白细腻的沙滩，蕴含了得天独厚、山水兼备、络绎不绝于</span><span style=\"color:#666666;font-size:14px;line-height:1.5;\">途的奇景。 来到科隆岛的海上，一个个独立的小岛好像海上桂林，又像越南下龙湾，但科隆岛更胜一</span><span style=\"color:#666666;font-size:14px;line-height:1.5;\">筹的是它的水，碧绿湛蓝，堪比九寨沟。</span> \r\n</p>\r\n<br />\r\n<div style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><img src=\"/attached/image/2016/04/27/ecf2e8e7-301d-4839-b21c-d432f8acd4a2.jpg\" alt=\"\" /></span> \r\n</div>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><img src=\"/attached/image/2016/04/27/a1206072-a31d-4dde-8dc0-63e2eb95898b.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;font-size:9px;color:#666666;\">谍影重重4取景地</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;font-size:9px;color:#666666;\"><img src=\"/attached/image/2016/04/27/ab475a6d-2b53-4991-9583-c7e565e95c9a.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><img src=\"/attached/image/2016/04/27/df8cc6cf-30a8-41cf-ad43-cbe3aac80811.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><img src=\"/attached/image/2016/04/27/53860dfc-9435-4ca5-8d25-454d009f77ef.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"></span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span style=\"line-height:1.5;\"><span><span style=\"color:#337FE5;font-size:16px;\">行程时间</span><span style=\"font-size:14px;\"><span style=\"font-size:16px;\">：</span><span style=\"color:#666666;\">6月、7月、8月 （ 5人起出发）</span></span></span></span> \r\n</p>\r\n<p>\r\n	<span><span style=\"color:#337FE5;font-size:16px;\">行程安排</span><span style=\"color:#337FE5;\"><span style=\"font-size:16px;\">：</span><span style=\"color:#666666;font-size:14px;\">5天4晚</span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;line-height:21px;\"><span style=\"color:#337FE5;\">行程费用</span>：</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;line-height:1.5;color:#666666;\">非潜水员：7980（含OW证书）、潜水员：5660元（9潜）</span> \r\n</p>\r\n<p>\r\n	<span><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><img src=\"/attached/image/2016/04/27/bb021129-fcde-40ee-9ab9-32b848a32313.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><img src=\"/attached/image/2016/04/27/4043972e-2a87-40fa-8210-7c34902f9150.jpg\" alt=\"\" /></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<strong><span style=\"color:#003399;\">费用包含</span></strong> \r\n</p>\r\n<p>\r\n	<span style=\"color:#666666;font-size:14px;\">★机场到度假酒店的来回接送</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#666666;line-height:1.5;font-size:14px;\">★潜水员包含：9次出海潜水，含气瓶，配重，潜导</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#666666;font-size:14px;\"> ★包含出海潜水：装备（不含手电）、出海午餐“便当”</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#666666;font-size:14px;\"> ★4晚标准间，两人一间</span> \r\n</p>\r\n<span style=\"color:#666666;font-size:14px;\"> ★非潜水员（OW考证）包含：证书、教练、气瓶、 &nbsp; 配重</span><br />\r\n<br />\r\n<span style=\"color:#003399;font-size:16px;\"><strong>费用不包含</strong></span><br />\r\n<p>\r\n	<span style=\"color:#666666;font-size:14px;\"> ★所有早、午、晚餐不包括（除出海潜水“便当”外、酒店附近布满了大大小小的餐厅，选择多样）</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#666666;font-size:14px;\"> ★所有机票费用不包含</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#666666;font-size:14px;\"> ★科隆岛线门票：450\\P\\人</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#666666;font-size:14px;\"> ★小费</span> \r\n</p>\r\n<span style=\"color:#666666;font-size:14px;\"> ★菲律宾签证、保险费用</span><br />\r\n<br />\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><img src=\"/attached/image/2016/04/27/0f450850-3ae3-416b-abdf-00e97dfad942.jpg\" alt=\"\" /></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><img src=\"/attached/image/2016/04/27/8e1c9df1-2e04-470b-9928-4d23a0fa6f84.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<strong><span style=\"color:#003399;\"><br />\r\n</span></strong> \r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<strong><span style=\"color:#003399;font-size:16px;\">沉船</span></strong><span style=\"line-height:1.5;\"><span style=\"font-size:16px;\">：</span><span style=\"color:#666666;font-size:14px;\">Coron科隆是世界级的沉船潜水（Wreck Diving)胜地，1944年9月24日上午，著名的美国麦克</span></span><span style=\"color:#666666;font-size:14px;line-height:1.5;\">阿瑟将军指挥西南太平洋军队18、19、31空军大队共145架舰载机轰炸了科隆湾的24艘日本军舰和商</span><span style=\"color:#666666;font-size:14px;line-height:1.5;\">船，现在共发现了其中的13艘，如今这些安眠在海底的庞大的战争残骸，成为举世闻名的沉船潜水胜</span><span style=\"color:#666666;font-size:14px;line-height:1.5;\">地。经过几十年的海洋环境“同化”，这些船体大多覆盖上了美丽的珊瑚，并且成为了海洋生物栖息的</span><span style=\"color:#666666;font-size:14px;line-height:1.5;\">天堂。在沉船里和周围，可以看到各式各样的海洋生物：石斑鱼、金枪鱼、狮子鱼、蝎子鱼、海龟、</span><span style=\"color:#666666;font-size:14px;line-height:1.5;\">管口鱼、蝙蝠鱼，还有各种珊瑚，美不胜收...........</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/04/27/abde49b5-0a29-4478-a8ce-82ff6e774aab.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/04/27/643ad53c-6a4c-4ce2-a773-58e00622a1f1.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<img src=\"/attached/image/2016/04/27/db6ec792-5583-49e9-9943-c18b040839b9.jpg\" alt=\"\" /><span style=\"font-size:10px;line-height:1.5;\"></span> \r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span style=\"font-size:10px;line-height:1.5;\"><span style=\"color:#003399;\"><strong><br />\r\n</strong></span></span> \r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span style=\"line-height:1.5;\"><span style=\"color:#003399;font-size:16px;\"><strong>海底“寻找海牛”</strong></span><span style=\"font-size:14px;\">：</span><span style=\"color:#666666;font-size:14px;\">科隆北部有一个观看海牛又名“儒艮”的聚集地，这里是儒艮的家园，也是独一无二的的潜点之一！</span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><img src=\"/attached/image/2016/04/27/b3f77dec-50a9-4da6-9776-7e38f328146a.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><img src=\"/attached/image/2016/04/27/834debca-b5f3-4433-9573-0093d775963a.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p>\r\n	<span><span style=\"color:#003399;font-size:16px;\"><strong>美食</strong></span><span style=\"font-size:16px;\">：</span><span style=\"color:#666666;font-size:14px;\">巴拉望有茂密的红树林，生长在水中的红树林吸引了众多的鱼虾在此栖息觅食，所以在巴拉</span></span><span style=\"color:#666666;font-size:14px;line-height:1.5;\">望，鱼和虾是绝对的鲜美无比，只要不是海鲜过敏，在这里都可以大快朵颐。菜市Coron Public</span><span style=\"color:#666666;font-size:14px;line-height:1.5;\">&nbsp;Market海鲜便宜又美味，找家店加工就可享用！</span> \r\n</p>\r\n<span> \r\n<div>\r\n</div>\r\n</span> <br />\r\n<div style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><img src=\"/attached/image/2016/04/27/27811811-8ed3-4cb4-8230-bd5065dde398.jpg\" alt=\"\" /></span> \r\n</div>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/04/27/20ad06f1-2c71-420b-bf92-09166efa83cb.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/04/27/abe4f1a1-a8b7-43a3-8c83-5b000791d9f4.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:10px;line-height:1.5;\"><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span style=\"line-height:1.5;\"><strong><span style=\"color:#003399;\"></span></strong><strong><span style=\"color:#003399;font-size:16px;\">人文：</span></strong><span style=\"color:#666666;\"><span style=\"font-size:14px;\">菲律宾</span><span style=\"font-size:14px;\">是亚洲仅有的以基督教为主的国家，有占人口90%的是基督教徒，有超过80% 的天主教徒，同时还有5%的伊斯兰教，民风淳朴！</span></span></span> \r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span style=\"font-size:10px;line-height:1.5;\"><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:10px;line-height:1.5;\"><img src=\"/attached/image/2016/04/27/02cf7dbf-0a80-451b-96aa-36dda5a835fd.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:10px;line-height:1.5;\"><img src=\"/attached/image/2016/04/27/43d5fdff-4d0f-4d6a-8a0a-cb00b0cbd607.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:10px;line-height:1.5;\"><img src=\"/attached/image/2016/04/27/e1d748e5-f72d-476b-89e0-d9ddda756031.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:10px;line-height:1.5;\"><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span style=\"line-height:1.5;\"><span style=\"color:#003399;\"><strong></strong></span><span style=\"color:#003399;font-size:14px;\"><strong>交通</strong></span><span style=\"font-size:14px;\">：</span><span style=\"color:#666666;font-size:14px;\">国内各大城市飞往马尼拉，然后马尼拉飞往BUSUANGA机场，航程1小时到达（科隆是busuanga岛上的一个镇，下飞机乘坐bus就可到达科隆镇上，车程30分钟）</span></span> \r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span style=\"font-size:10px;line-height:1.5;\"><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><img src=\"/attached/image/2016/04/27/c7f548fe-13c5-472b-b928-71c68a7eb510.jpg\" alt=\"\" /></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><img src=\"/attached/image/2016/04/27/4f0dc574-d348-450c-9e9b-babd18a5d73c.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><img src=\"/attached/image/2016/04/27/d6e7c46b-b6fb-4953-b597-34224d9b292c.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span style=\"font-size:12px;color:#337FE5;\">注：参加沉船潜需AOW以上水平,</span><span style=\"font-size:12px;color:#337FE5;\">感兴趣的童鞋们欢迎咨询【潜程】小秘书：boyes127 微信 或致电：18001661552！</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	|\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><span style=\"font-size:9px;\"><em><strong><span style=\"color:#E56600;font-size:14px;\"><span style=\"color:#E56600;font-size:14px;\">发现&nbsp;</span></span></strong></em><em><strong><span style=\"color:#64451D;font-size:14px;\"><span style=\"color:#E56600;font-size:14px;\"> <span style=\"color:#666666;\">&amp;</span></span><span style=\"font-size:14px;color:#333333;\">&nbsp;</span></span><span style=\"color:#EE33EE;font-size:14px;\">CORON</span></strong></em><em><strong><span style=\"color:#337FE5;\"></span></strong></em><br />\r\n</span></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>');
INSERT INTO `dive_activity` VALUES ('6edb9112-a2b0-44ec-9a73-8b1e1312c15c', '2016-06-15 10:53:27', '', '泰国  普吉岛', '2016-08-12', '大手牵小手，梦幻普吉岛！', '广州', '2016-08-06', null, 'AT01', '371ae1ce-aec7-4066-b623-54c5fa81fbd8', null, null, null, null, '', '', null, '5', 'uploadfiles/activity/activity_Ovxn1465959404893.jpg', '<p class=\"MsoNormal\" style=\"text-align:center;text-indent:0pt;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#003399;font-size:14px;\"><span style=\"color:#003399;font-size:18px;\"><strong><em>【暑期档】大手牵小手，梦幻普吉岛！</em></strong></span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;text-indent:0pt;\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;text-indent:0pt;\">\r\n	|\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;text-indent:0pt;\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;text-indent:0pt;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#F2301A;font-size:14px;\">阳光&nbsp;</span><span style=\"font-family:\'Helvetica Neue\';color:#3E3E3E;font-size:14px;\">，</span><span style=\"font-family:\'Helvetica Neue\';color:#1AF2ED;font-size:14px;\">海水</span><span style=\"font-family:\'Helvetica Neue\';color:#3E3E3E;font-size:14px;\">，</span><span style=\"font-family:\'Helvetica Neue\';color:#FF8124;font-size:14px;\">沙滩</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;text-indent:0pt;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#666666;font-size:14px;\">大自然所赠予人类的宝物，</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;text-indent:0pt;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#666666;font-size:14px;\">被虔诚的泰国人奉为最珍贵的财产。</span><b><span class=\"16\" style=\"font-family:\'Helvetica Neue\';color:#666666;font-size:14px;\">&nbsp;</span></b> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;text-indent:0pt;\">\r\n	<b><span class=\"16\" style=\"font-family:\'Helvetica Neue\';color:#666666;font-size:14px;\"><img src=\"/attached/image/2016/06/15/f7f1c676-36cd-4569-84fa-e1566653e151.jpg\" alt=\"\" /><br />\r\n</span></b> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#FFFFFF;font-size:12pt;background-color:#999999;\"><span style=\"color:#FFFFFF;font-size:14px;background-color:#999999;\"><strong> </strong></span></span> \r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:16px;color:#60D978;background-color:#FFFFFF;\">普吉岛简介</span></strong> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#3E3E3E;font-size:12pt;\"><span style=\"color:#666666;font-size:14px;\">普吉岛位于泰国西南方，安达曼海东南部海面之上，是一座南北较长(最长处48.7千米)、东西稍窄(最宽处21.3千米)的狭长状岛屿，北以巴帕海峡与泰国本土的攀牙府相邻，而东侧则是隔着攀牙湾与对岸的甲米府呼应，西岸及南岸则都濒临安达曼海。普吉岛是泰国最大的海岛，也是最小的一个府，以其迷人的热带风光和丰富的旅游资源被称为\"安达曼海上的一颗明珠\"，自然资源十分丰富，有\"珍宝岛\"、\"金银岛\"</span><span style=\"color:#666666;font-size:14px;\">的美称，盛产橡胶、海产和各种水果。泰国是佛教治国，大多数泰国人信<span style=\"color:#666666;\">奉四面佛。</span></span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"color:#00D5FF;\"><span style=\"font-size:14px;line-height:21px;color:#00D5FF;\">....................................................................................................</span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;text-indent:0pt;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#337FE5;font-size:12pt;\"><span style=\"color:#337FE5;font-size:14px;\"><span style=\"color:#337FE5;\">小贴士</span></span></span> \r\n</p>\r\n<div style=\"text-align:center;\">\r\n	<span style=\"color:#666666;font-size:12px;font-family:\'Helvetica Neue\';line-height:1.5;\">语言：泰语</span> \r\n</div>\r\n<span style=\"font-family:\'Helvetica Neue\';color:#3E3E3E;font-size:12pt;\"> \r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#666666;font-size:12px;\">货币：泰铢</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:12px;color:#666666;\">签证：落地签</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:12px;color:#666666;\">时区：北京时间-1小时</span> \r\n</p>\r\n</span><span style=\"font-family:\'Helvetica Neue\';color:#666666;font-size:14px;\"> \r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;font-size:12px;\">国家电话区号：+66</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><img src=\"/attached/image/2016/06/15/598a94aa-9dfa-42d3-bc96-40a9fc8a70ab.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n</span> \r\n<p class=\"MsoNormal\" style=\"text-align:center;text-indent:0pt;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#FFFFFF;font-size:16px;background-color:#999999;\"><strong>活动主题</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;text-indent:0pt;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#3E3E3E;font-size:14px;\"><img src=\"/attached/image/2016/06/15/bfbbf6c3-0afa-4107-a92b-40dabdf39814.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#666666;font-size:14px;\">时间：2016.8.6---2016.8.12（7天6晚）</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#666666;font-size:14px;\">地点：泰国 &nbsp;普吉岛</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;text-indent:0pt;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#FFFFFF;font-size:14px;background-color:#999999;\"><span style=\"color:#FFFFFF;font-size:16px;background-color:#999999;\"><strong>行程安排</strong></span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#666666;font-size:14px;\"><span style=\"color:#FF9900;\">8月6日（第一天</span><span style=\"color:#FF9900;\">）</span><span style=\"color:#FF9900;\">:晚上广州白云机场集合，乘坐飞机飞往普吉岛，预计凌晨到达。</span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span><span style=\"line-height:21px;font-size:14px;\">度假村介绍</span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<i><span class=\"15\" style=\"font-family:\'Helvetica Neue\';color:#EE33EE;font-size:14px;\"><strong>地理位置优越的超便捷酒店！</strong></span></i> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;text-indent:0pt;\">\r\n	<i><span class=\"15\" style=\"font-family:\'Helvetica Neue\';color:#EE33EE;font-size:14px;\"><strong><img src=\"/attached/image/2016/06/15/06496413-874b-47da-8e3c-4faa2ec0d907.jpg\" alt=\"\" /><br />\r\n</strong></span></i> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#EE33EE;font-size:14px;\"><em>干净漂亮的套房</em></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#EE33EE;font-size:14px;\"><em><img src=\"/attached/image/2016/06/15/1fba08ca-594d-47c9-824c-b058284ae940.jpg\" alt=\"\" /><br />\r\n</em></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#3E3E3E;font-size:14px;\"><img src=\"/attached/image/2016/06/15/d3f4b336-a8ec-48d9-8e3d-07a80914b468.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;text-indent:0pt;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#3E3E3E;font-size:14px;\"><img src=\"/attached/image/2016/06/15/16caf9c3-5515-4218-a744-aedd10f1882f.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#EE33EE;font-size:14px;\"><em>大型泳池</em></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;text-indent:0pt;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#EE33EE;font-size:14px;\"><em><img src=\"/attached/image/2016/06/15/76c4638a-ac2e-4b03-ac56-0afe2063b233.jpg\" alt=\"\" /><br />\r\n</em></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#EE33EE;font-size:14px;\"><em>健身房</em></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;text-indent:0pt;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#EE33EE;font-size:14px;\"><em><img src=\"/attached/image/2016/06/15/ec7ec98c-4d2d-4420-9833-492420647bf9.jpg\" alt=\"\" /><br />\r\n</em></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#666666;font-size:14px;\">酒店是一个距离海滨仅有200米的热带假日休闲地，位于普吉岛（Phuket）的巴东海滩（Patong&nbsp;</span><span style=\"color:#666666;font-family:\'Helvetica Neue\';font-size:14px;line-height:1.5;\">Beach）。酒店提供3个室外游泳池、2间餐厅和1个spa 中心。酒店设施齐全。每天提供自助</span><span style=\"color:#666666;font-family:\'Helvetica Neue\';font-size:14px;line-height:1.5;\">早餐。Banburee Restaurant 餐厅和Thomthong Restaurant 餐厅供应泰式、亚洲和国际菜肴。大堂酒</span><span style=\"color:#666666;font-family:\'Helvetica Neue\';font-size:14px;line-height:1.5;\">廊则提供饮品和现场娱乐表演。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;text-indent:0pt;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#666666;font-size:14px;\"><img src=\"/attached/image/2016/06/15/95e1b0af-07be-4a30-bb5c-2a9e4d40dfe8.jpg\" alt=\"\" />&nbsp;</span><span style=\"font-family:\'Helvetica Neue\';color:#3E3E3E;font-size:12pt;\"><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;text-indent:0pt;\">\r\n	<span style=\"line-height:1.5;\">▽</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;text-indent:0pt;\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#FF8124;font-size:14px;\">8月7日（第二天）:凌晨到达普吉并入住度假村。上午休息，下午半日游自由活动</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#5F9CEF;font-size:14px;\">推荐1：骑大象</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#666666;font-size:12px;\">不体验一次驰名海外的普吉岛大象骑行，你的普吉之旅注定不算完整。普吉岛的大象世界驰名。在森林骑大象可以感</span><span style=\"color:#666666;font-family:\'Helvetica Neue\';line-height:1.5;\">受绿树成荫的魅力，闻着绿树青草发出的自然的味道。骑在大象背上穿越茂密的热带雨林，你一定会爱上这里可爱非</span><span style=\"color:#666666;font-family:\'Helvetica Neue\';line-height:1.5;\">常的小象。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#666666;font-size:14px;\"><img src=\"/attached/image/2016/06/15/ad3ac10a-7834-4625-8ad4-79eadf19b39d.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#5F9CEF;font-size:14px;\">推荐2：丛林飞跃</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#666666;font-size:12px;\">丛林飞跃是十分受欢迎的旅游项目，起源于哥斯达黎加的茂密雨林。arzan-adventure丛林飞跃，是普吉岛新开发的丛</span><span style=\"color:#666666;font-family:\'Helvetica Neue\';line-height:1.5;\">林探险型类的项目，位于查隆区附近的一片雨林中，距离巴东、卡塔、卡伦三大海滩车程大约20-30分钟。全程包含了</span><span style=\"color:#666666;font-family:\'Helvetica Neue\';line-height:1.5;\">类目多样的挑战内容，不再单一地在树与树之间飞跃，还加入了攀爬、跨越桥梁、行走吊绳桥、翻越绳网等极限项</span><span style=\"color:#666666;font-family:\'Helvetica Neue\';line-height:1.5;\">目，经历体验这些丰富刺激的挑战后，让人大呼过瘾。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#666666;font-size:12px;\"><img src=\"/attached/image/2016/06/15/42b937a0-e2db-45c4-8efd-68662009c2d8.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#DFC5A4;font-size:10px;\"><img src=\"/attached/image/2016/06/15/6fea6a40-198d-4f35-aae4-f72996fa2ea0.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#3E3E3E;font-size:10px;\"><span style=\"color:#DFC5A4;font-family:\'Helvetica Neue\';line-height:15px;font-size:10px;\">备注：早餐（酒店自助），午餐&amp;晚餐（自行安排）</span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#3E3E3E;font-size:12pt;\"><span style=\"color:#DFC5A4;font-family:\'Helvetica Neue\';line-height:15px;font-size:12px;\"><br />\r\n</span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#3E3E3E;font-size:12pt;\"><span style=\"color:#DFC5A4;font-family:\'Helvetica Neue\';line-height:15px;font-size:12px;\"><span style=\"color:#666666;font-size:10px;\">▽</span><span style=\"color:#666666;font-size:10px;\"></span></span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#3E3E3E;font-size:12pt;\"><br />\r\n</span><span style=\"font-family:\'Helvetica Neue\';color:#FF8124;font-size:14px;\">8月8日（第三天）:大小皇帝岛（RACHA YAI &nbsp;3潜）</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#666666;font-size:12px;\">皇帝岛在普吉岛的南面，相距12公里，快艇需要30-40分钟左右，岛上不仅有天然热带岛屿风光，珊瑚礁的海岸景色同</span><span style=\"color:#666666;font-family:\'Helvetica Neue\';line-height:1.5;\">样让人难忘，鹿角珊瑚几乎遍地都是，时常会有大型海洋动物造访，此外还有一些有趣的动物，如石头鱼、石斑鱼、</span><span style=\"color:#666666;font-family:\'Helvetica Neue\';line-height:1.5;\">鲢鱼、海鳗、狮子鱼等。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#666666;font-size:12px;\"><img src=\"/attached/image/2016/06/15/4bb65716-dac7-4642-b365-c70c00918cce.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#DFC5A4;font-size:10px;\"><img src=\"/attached/image/2016/06/15/dfcedab8-9604-4f6f-8078-121f585e0d60.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#DFC5A4;font-size:10px;\">备注：早餐（酒店自助），午餐（船上便当），晚餐（自行安排）</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>\r\n<div style=\"text-align:center;\">\r\n	<span style=\"line-height:15px;font-family:\'Helvetica Neue\';\">▽</span><span style=\"line-height:15px;font-family:\'Helvetica Neue\';\"></span><span style=\"line-height:15px;font-family:\'Helvetica Neue\';\"></span> \r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#FF8124;font-size:14px;\">8月9日（第四天）：大小皮皮岛（3潜），潜水，日光浴，与宝宝一起享受海岛风光。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#FF8124;font-size:14px;\"><img src=\"/attached/image/2016/06/15/a1409c16-4401-4b95-a7f8-44e11f604ef0.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#666666;font-size:12px;\">皮皮岛位于泰国普吉岛东南约20公里处，是由两个主要岛屿（北部的大皮皮岛和南部的小皮皮岛）组成的姐妹岛。这</span><span style=\"color:#666666;font-family:\'Helvetica Neue\';line-height:1.5;\">是一个深受阳光眷宠的地方，柔软洁白的沙滩，宁静碧蓝的海水，鬼斧神工的天然洞穴，未受污染的自然风貌，使得</span><span style=\"color:#666666;font-family:\'Helvetica Neue\';line-height:1.5;\">她从普吉岛周围的30余个离岛中脱颖而出，一举成为近年来炙手可热的度假胜地之一。大pp岛上可以住宿，但海滩都</span><span style=\"color:#666666;font-family:\'Helvetica Neue\';line-height:1.5;\">不及小pp迷人。小皮皮岛上峭壁耸立，海洋生物也种类繁多，是绝佳的潜水点。PP岛上的蛋岛是亲子游的最佳选择。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#666666;font-size:12px;\"><img src=\"/attached/image/2016/06/15/945ab848-b6ca-4e8a-ab05-43214088160d.png\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#DFC5A4;font-size:10px;\"><img src=\"/attached/image/2016/06/15/3618fe1a-d52a-4851-98dd-0f2cf14e34a4.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#DFC5A4;font-size:10px;\"><span style=\"color:#DFC5A4;font-family:\'Helvetica Neue\';line-height:15px;\">备注：早餐（酒店自助），午餐（船上便当），晚餐（自行安排）</span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#DFC5A4;font-size:10px;\"><span style=\"color:#DFC5A4;font-family:\'Helvetica Neue\';line-height:15px;\"><br />\r\n</span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#DFC5A4;font-size:10px;\"><span style=\"color:#DFC5A4;font-family:\'Helvetica Neue\';line-height:15px;\"><span style=\"color:#666666;\">▽</span><br />\r\n</span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Helvetica Neue\';\"><span><span style=\"line-height:15px;\"><br />\r\n</span></span></span><span style=\"font-family:\'Helvetica Neue\';color:#FF8124;font-size:14px;\">8月10日（第五天）：皇家沉船（1潜，需AOW及以上等级的潜水员）、沉船周边潜水 （2潜）</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#3E3E3E;font-size:14px;\"><span style=\"color:#666666;font-size:12px;\">1997年5月4日，这是一个万里无云、风平浪静的日子，这艘长85米的渡轮是在这条已经行驶了几百次的航线上触礁沉</span></span><span style=\"line-height:1.5;font-family:\'Helvetica Neue\';color:#666666;\">没。在从触礁到沉没的2.5个小时里，超过600名乘客被全部救起，无人伤亡。现在，这艘船静静躺在32米的河床上，</span><span style=\"line-height:1.5;font-family:\'Helvetica Neue\';color:#666666;\">上面遍布着各种珊瑚。潜水员们可以在甲板、通道和引桥上任意探索（但是请尽可能不要进去船体内部）。在船的上</span><span style=\"line-height:1.5;font-family:\'Helvetica Neue\';color:#666666;\">半部，你可以看到大群的鲷鱼与梭鱼</span><span style=\"color:#3E3E3E;font-family:\'Helvetica Neue\';font-size:14px;line-height:1.5;\">。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#3E3E3E;font-size:14px;\"><img src=\"/attached/image/2016/06/15/573ec94e-1a88-48de-b8e6-86b086d4d4fc.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#DFC5A4;font-size:10px;\">备注：早餐（酒店自助），午餐（船上便当），晚餐（自行安排）</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<b><span class=\"16\" style=\"font-family:\'Helvetica Neue\';color:#CC33E5;font-size:12px;\">潜点分布图</span></b><b><span class=\"16\" style=\"font-family:\'Helvetica Neue\';color:#3E3E3E;font-size:12pt;\"><br />\r\n</span></b> \r\n</p>\r\n<div style=\"text-align:center;\">\r\n	<b><img src=\"/attached/image/2016/06/15/ab7da59c-bfc5-4c79-8319-00645dad628e.jpg\" alt=\"\" /> </b> \r\n</div>\r\n<span style=\"font-family:\'Helvetica Neue\';color:#3E3E3E;font-size:12pt;\"><br />\r\n</span> \r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#FF8124;font-size:14px;\"><span style=\"font-size:12px;color:#666666;\">▽</span><span style=\"font-size:12px;color:#666666;\"></span><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#FF8124;font-size:14px;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#FF8124;font-size:14px;\">8月11日（第六天）：自由一日行</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#5F9CEF;font-size:14px;\">推荐1：幻多奇乐园</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#666666;font-size:12px;\">幻多奇乐园，是普吉岛最大最受欢迎的主题乐园，融汇泰国本土文化与美国拉斯维加斯风格于一体，被誉为泰国迪斯</span><span style=\"color:#666666;font-family:\'Helvetica Neue\';line-height:1.5;\">尼。距离芭东海滩约10分钟的车程便可以到达，连续数年蝉联泰国最佳旅游景点奖项。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#666666;font-size:12px;\"><img src=\"/attached/image/2016/06/15/7bd6c9e6-d125-403e-8f0c-b9060a7e27a6.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#5F9CEF;font-size:14px;\">推荐2：查龙寺</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#666666;font-size:12px;\">查龙寺位于普吉岛的中部，普吉岛上最大的佛教寺庙，寺庙建筑华丽而又壮观，非常有泰国特色。&nbsp;据说里面的四面佛</span><span style=\"line-height:1.5;font-family:\'Helvetica Neue\';color:#666666;\">很灵，故引来大量游人烧香。寺庙内供奉了泰国的圣僧金身，当地信徒会以贴金箔的方式向佛祖献上虔诚的供养</span><span style=\"line-height:1.5;font-family:\'Helvetica Neue\';color:#666666;\">。</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#666666;font-size:12px;\"><img src=\"/attached/image/2016/06/15/29b77cae-4918-4b5a-b375-47ac37806595.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#DFC5A4;font-size:10px;\">备注：早餐（酒店自助），午餐&amp;晚餐（自行安排）</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';\"><span><span style=\"line-height:15px;\"><br />\r\n</span></span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';\"><span><span style=\"line-height:15px;\">▽</span></span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';\"><span><span style=\"line-height:15px;\"><br />\r\n</span></span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:left;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';\"> </span><span style=\"font-family:\'Helvetica Neue\';color:#FF8124;font-size:14px;\">8月12日（第七天）：上午自由行，逛街，晚上飞机回国</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:left;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#000000;font-size:14px;\">童童2016拍于芭东：</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:left;\">\r\n	<span style=\"color:#CC33E5;\">凸凸车</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;\">\r\n	<span><span style=\"font-size:10px;line-height:21px;\"><img src=\"/attached/image/2016/06/15/acddc22e-455c-4b96-a5d6-fa6d014313c1.jpg\" alt=\"\" /><br />\r\n</span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#CC33E5;font-size:14px;\"><span style=\"color:#CC33E5;font-size:10px;\">菜场</span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#3E3E3E;font-size:14px;\"><span style=\"color:#DFC5A4;font-size:10px;\"><img src=\"/attached/image/2016/06/15/8fd1c7f5-5bd3-42fb-b07b-596ef7e633fb.jpg\" alt=\"\" /><br />\r\n</span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#CC33E5;font-size:14px;\"><span style=\"color:#CC33E5;font-size:10px;\">街道&amp;夜市</span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#3E3E3E;font-size:14px;\"><span style=\"color:#DFC5A4;font-size:10px;\"><img src=\"/attached/image/2016/06/15/feeff3b6-f16b-4d37-8d34-6a37a778b135.jpg\" alt=\"\" /><br />\r\n</span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#3E3E3E;font-size:14px;\"><span style=\"color:#DFC5A4;font-size:10px;\"><img src=\"/attached/image/2016/06/15/4a864883-75a8-466d-a981-a1e79a59ed11.jpg\" alt=\"\" /><br />\r\n</span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#CC33E5;font-size:14px;\"><span style=\"color:#CC33E5;font-size:10px;\">芭东海滩</span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#3E3E3E;font-size:14px;\"><span style=\"color:#DFC5A4;font-size:10px;\"><img src=\"/attached/image/2016/06/15/5fcb9946-8483-4149-81a8-4d7d3b88afa9.jpg\" alt=\"\" /><br />\r\n</span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#3E3E3E;font-size:14px;\"><span style=\"color:#DFC5A4;font-size:10px;\"><img src=\"/attached/image/2016/06/15/023f8328-ce78-4053-9b4a-01eed0df7db2.jpg\" alt=\"\" /><br />\r\n</span></span> \r\n</p>\r\n<p clas