INSERT INTO `dive_activity` VALUES ('77c8ccf2-1a5f-4fbc-a52b-f6280e61a3b2', '2015-11-24 21:58:21', '随着潜水运动风靡全球，潜水技巧知识的普及，走进水中世界已不再是一个童话般的心愿，而是一份令您惊喜不已的浪漫。零基础体验潜水，便可以在模拟水域里感受到潜水的新奇，想象一下当您徐徐潜入清凉明澈的水中，阳光被水折射成无数个星星，在眼前不断地闪烁、耀动，成串的气泡，欢快地漂过耳际，您会惊奇地发现您正轻松地扇动脚蹼，自如地悬浮于水中，真心体味水中世界的奇妙！人鱼时代即将开启！零基础体验潜水课程', '上海', '2016-11-21', '零基础体验潜水课程“第一期”', '上海', '2015-11-21', null, 'AT02', '6a040c09-5758-41f8-8910-bf556692e824', null, null, null, null, '', '', null, '5', 'uploadfiles/activity/activity_wmQ51454503491638.jpg', '<p style=\"text-align:center;\">\r\n	<span style=\"font-size:18px;line-height:1.5;color:#003399;\"><strong><em>零基础体验潜水</em></strong></span><span style=\"font-size:16px;line-height:1.5;color:#E56600;\"></span><span style=\"font-size:16px;line-height:1.5;color:#E56600;\"><span style=\"color:#64451D;\"><strong><span style=\"color:#003399;font-size:18px;\"><em>课程</em></span><span style=\"color:#003399;font-size:18px;\"><em>〖</em></span><span style=\"color:#003399;\"></span></strong></span><span style=\"color:#64451D;\"><strong><span style=\"color:#003399;font-size:18px;\"><em>第一期</em></span><span style=\"color:#003399;font-size:18px;\"><em>〗</em></span><span style=\"color:#003399;\"></span></strong></span></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;color:#E56600;\"><span style=\"color:#64451D;\"><strong><span style=\"color:#003399;\"><em><span style=\"color:#B8D100;font-size:24px;\">☼</span><span style=\"color:#B8D100;\"></span><br />\r\n</em></span></strong></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-size:14px;color:#666666;\">&nbsp;随着潜水运动风靡全球，潜水技巧知识的普及，走进水中世界已不再是一个童话般的心愿，而是一份</span></span><span style=\"color:#666666;font-size:14px;line-height:1.5;\">令您惊喜不已的浪漫。零基础体验潜水，便可以在模拟水域里感受到潜水的新奇，想象一下当您徐徐</span><span style=\"color:#666666;font-size:14px;line-height:1.5;\">潜入清凉明澈的水中，阳光被水折射成无数个星星，在眼前不断地闪烁、耀动，成串的气泡，欢快地</span><span style=\"color:#666666;font-size:14px;line-height:1.5;\">漂过耳际，您会惊奇地发现您正轻松地扇动脚蹼，自如地悬浮于水中，真心体味水中世界的奇妙！人</span><span style=\"color:#666666;font-size:14px;line-height:1.5;\">鱼时代即将开启！</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#003399;font-size:14px;\">【体验时间】</span><span style=\"font-size:14px;color:#666666;\">2015年11月21日（星期六下午14：30-17：30 ）</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#003399;font-size:14px;\">【体验地点】</span><span style=\"font-size:14px;color:#666666;\">第二军医大学潜水馆（上海杨浦区翔殷路800号）</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#003399;font-size:14px;\">【本期人数】</span><span style=\"font-size:14px;color:#666666;\">10人（先报先体验）</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#003399;font-size:14px;\">【费用】</span><span style=\"font-size:14px;color:#666666;\">460∕人&nbsp;</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#003399;font-size:14px;\">费用包括：</span><span style=\"font-size:14px;color:#666666;\">潜水馆使用费、气瓶费、教练费（SSI专业潜水教练）、潜水BC装备费用</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"color:#003399;font-size:14px;\">【体验内容】</span><span style=\"font-size:14px;color:#666666;\">换装准备后就开始啦：30分钟岸上拉伸、准备；30分钟理论讲解、10分钟安全讲解、</span></span><span style=\"color:#666666;font-size:14px;line-height:1.5;\">40分钟水下体验 、之后可自由提问、请教、交流时间！</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;\"><span style=\"color:#003399;font-size:14px;\">【温馨提示】</span><span style=\"font-size:14px;color:#666666;\">自备游衣游裤、换洗衣服、毛巾、拖鞋、清洗护肤用品等私人物品;本次体验潜水所用装</span></span><span style=\"color:#666666;font-size:14px;line-height:1.5;\">备均由Mares提供!</span> \r\n</p>\r\n<span style=\"font-size:16px;\"> <br />\r\n<div style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2015/11/24/6f7ac9c9-e44b-4127-a910-01fdef1468bc.jpg\" alt=\"\" /> \r\n</div>\r\n</span> \r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:16px;\"><img src=\"/attached/image/2015/11/24/4d10fc02-bb86-4dc4-aec6-c63429642b0f.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:16px;\"><img src=\"/attached/image/2015/11/24/9cac2573-0a1b-4b77-b736-db9c0eed01a5.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:16px;\"><span style=\"font-size:14px;color:#E56600;\">水下动作要领的讲解、体验↓</span><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:16px;\"><img src=\"/attached/image/2015/11/24/2b1acafc-cec1-40e0-8ef2-7613ff14b789.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:16px;\"><img src=\"/attached/image/2015/11/24/0b7b904d-cac0-45a5-9956-ffcecd5b1078.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#337FE5;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;line-height:15px;\">☆ 感兴趣的小伙伴们欢迎来询/潜程小秘书“童童”（微信号：boyes127 或电话：18001661552)</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#337FE5;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;line-height:15px;\"><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\">|</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>');
INSERT INTO `dive_activity` VALUES ('931e58b5-e830-4f42-a85d-142706f267c4', '2016-03-07 11:06:05', '图巴嗒哈船宿', '菲律宾 图巴嗒哈', '2017-06-25', '推荐行程：图巴塔哈船宿4月启程', '全国各地', '2016-04-19', null, 'AT02', '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', null, null, null, null, '', '', null, '0', 'uploadfiles/activity/activity_GXe41457321017925.jpg', '<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/03/29/d7935bf5-f9c7-488b-8f15-c9ef497b82e5.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<em><br />\r\n</em> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<em><span style=\"color:#DFC5A4;font-size:18px;\">菲律宾图巴塔哈7天6晚(5天潜水19潜)船宿</span></em> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<em><span style=\"color:#DFC5A4;font-size:16px;\">船期:2016年4月19-25日(最后4个位)</span></em> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#666666;font-size:14px;\">港币12600</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#666666;font-size:14px;\">免费机票</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/03/29/95e827c3-4a89-4bb9-b659-32ce6c343fef.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">菲律宾最顶级的潜水点，也是全球排列前端的潜水员必潜之处,世界自然新七大奇跡提名.受到菲律宾</span><span style=\"font-size:14px;line-height:1.5;color:#666666;\">政府多年的保护,此处鱼类种类繁多,常有大只和大群的魚类出没,各种珊瑚和巨型海扇生长茂盛，能見</span><span style=\"font-size:14px;line-height:1.5;color:#666666;\">度</span><span style=\"font-size:14px;line-height:1.5;color:#666666;\">极佳(可达40米或以上).纯自然和原生态.这个自然保护区每年仅3-6月开放，绝对不要错过。</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/03/29/aa3e4e15-b6a0-47c1-85b0-44a3ad43c5b9.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<strong><span style=\"color:#337FE5;font-size:16px;\">所剩房型</span></strong><span style=\"color:#337FE5;font-size:16px;\">:</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">1间双人间</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">1个男士床位（双人间拼房）</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">1个女士床位（双人间拼房）</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<strong><span style=\"color:#337FE5;font-size:16px;\">船宿费用:</span></strong> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">双人房,每人港币HK$12600元(约1650美元)</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">现在报名会获赠送与本次行程有关的宿雾太平洋航空马尼拉至公主港來回机票和20kg行李费</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">免费机票数额有限, \r\n请尽快报名争取</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">此价是一个很优惠的价格,而且还有机票赠送,请勿错过</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">报名时请提供护照上的英文姓名用于留位</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<hr />\r\n<p>\r\n	<strong><span style=\"color:#337FE5;font-size:16px;\">费用包含</span></strong><span style=\"line-height:1.5;color:#337FE5;font-size:16px;\">:</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">◆公主港机场至船的來回交通</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\"><span style=\"color:#666666;font-size:14px;line-height:21px;\">◆</span>船上的食宿,茶啡,水,果汁</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\"><span style=\"color:#666666;font-size:14px;line-height:21px;\">◆</span>19潜</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\"><span style=\"color:#666666;font-size:14px;line-height:21px;\">◆</span>12L&nbsp;瓶和配重</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\"><span style=\"color:#666666;font-size:14px;line-height:21px;\">◆</span>潜导</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/03/29/dd1290bf-a3c4-4b86-b27b-62a2bc8ec294.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<strong><span style=\"color:#337FE5;\"><br />\r\n</span></strong> \r\n</p>\r\n<p>\r\n	<strong><span style=\"color:#337FE5;font-size:16px;\">费用不包含:</span></strong> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\"><span style=\"color:#666666;font-size:14px;line-height:21px;\">◆</span>图巴塔哈国家公园费4500菲律宾披索</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\"><span style=\"color:#666666;font-size:14px;line-height:21px;\">◆</span>潜水设备租用(每天以披索计价&nbsp;:全套1000 \r\n; BCD 500 ;&nbsp;调节器&nbsp;500 ;&nbsp;湿衣&nbsp;150 ;&nbsp;面镜&nbsp;100 ;&nbsp;脚蹼&nbsp;100 )需预订</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\"><span style=\"color:#666666;font-size:14px;line-height:21px;\">◆</span>15L气瓶( \r\n500披索)&nbsp;需预订,只有3个瓶</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\"><span style=\"color:#666666;font-size:14px;line-height:21px;\">◆</span>高氧( \r\n1500披索/10瓶)</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\"><span style=\"color:#666666;font-size:14px;line-height:21px;\">◆</span>酒精及有汽类饮料</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\"><span style=\"color:#666666;font-size:14px;line-height:21px;\">◆</span>潜水保险(这是强制要求,没有保险者会被拒绝上船并不会获得退款)</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\"><span style=\"color:#666666;font-size:14px;line-height:21px;\">◆</span>旅行保险(建议各位购买,我们有该类的旅行和潜水保险供客人选择,客人也可自行选择自己的保险公司购买.)</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\"><span style=\"color:#666666;font-size:14px;line-height:21px;\">◆</span>所有付款所产生的银行费用</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\"><span style=\"color:#666666;font-size:14px;line-height:21px;\">◆</span>机票,机场税</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\"><span style=\"color:#666666;font-size:14px;line-height:21px;\">◆</span>小费</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/03/29/645e4182-85f4-42c1-bedb-e249cbf761a6.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<strong><span style=\"color:#337FE5;font-size:16px;\">船型介绍:</span></strong> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">Mv \r\nNarayana,&nbsp;船长26m,载客量12人(不包括船员)</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">双人房,內带空调及卫生间</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/03/29/16335e8c-7fbe-4d5e-9444-dcff80abc115.gif\" alt=\"\" /> \r\n</p>\r\n<div style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"><img src=\"/attached/image/2016/03/29/2d7b1903-8703-480d-ac69-863e2be1bd71.jpg\" alt=\"\" /></span> \r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<strong><span style=\"color:#337FE5;font-size:16px;\">计划行程安排:</span></strong> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">Day 1 \r\n:&nbsp;下午2pm前抵达公主港,工作人员接上船安顿,连夜驶往图巴塔哈</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">Day \r\n2-5 :&nbsp;每天3+1夜潜</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">Day 6 : \r\n3潜后回航</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">Day 7 \r\n:&nbsp;早餐后送客人到公主港机场</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/03/29/4075954f-985c-47e9-8e95-b0e7806f0f9d.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<strong><span style=\"color:#337FE5;\"><br />\r\n</span></strong> \r\n</p>\r\n<p>\r\n	<strong><span style=\"color:#337FE5;font-size:16px;\">潜水员要求:</span></strong> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">AOW和50潜以上,</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">20-50瓶的OW或AOW的客人,必须聘用私人潜导(每人每日港币350元)</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#666666;\">本行程最多只能让两位此级别潜水员参加</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/03/29/8101e39b-3377-4ee4-916e-22b10d8c164a.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<strong><br />\r\n</strong> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<strong><span style=\"font-size:14px;\">如有任何查询请与Wai联系:</span></strong> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:14px;\">电邮: \r\neasywai6363@hotmail.com</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:14px;\">微信&nbsp;: easywai63</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:14px;\">电话&nbsp;: 13822107890</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"color:#64451D;font-size:14px;\">&nbsp;此行程由：【自由人潜水 \r\n】制定，【潜程】推荐！</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<hr />\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>');
INSERT INTO `dive_activity` VALUES ('a543e1e0-0a2d-49a7-8ef6-b4a32eefe9ae', '2016-05-24 09:58:57', '', '马尔代夫', '2016-10-02', '邂逅马尔代夫 | 豪华船宿之旅', '上海', '2016-09-25', null, 'AT01', '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', null, null, null, null, '', '', null, '0', 'uploadfiles/activity/activity_OxYJ1464055136846.jpg', '<h2 style=\"text-align:center;margin-left:0pt;\">\r\n	<b><span style=\"font-size:18px;color:#003399;\"><em>邂逅马尔代夫&nbsp; | 豪华船宿之旅</em></span></b> \r\n</h2>\r\n<p style=\"text-align:center;\">\r\n	<b><span style=\"font-size:18px;color:#337FE5;\"><span style=\"color:#FFE500;\"><em>＆</em></span><span style=\"color:#333333;\"></span></span></b> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<b><span style=\"font-size:10px;color:#CC33E5;\"><img src=\"/attached/image/2016/05/24/e1f55232-d447-41de-a51d-2cf5f48da4bd.jpg\" alt=\"\" /><br />\r\n</span></b> \r\n</p>\r\n<p class=\"p\" style=\"text-align:left;\">\r\n	<span style=\"font-size:14px;color:#333333;\">看<span style=\"color:#666666;\">过《麦兜故事》的人很难不和麦兜一起爱上这个岛国：“椰林树</span></span><span style=\"line-height:1.5;font-size:14px;color:#666666;\">影，水清沙幼。马尔代夫位于南亚，</span><span style=\"line-height:1.5;font-size:14px;color:#666666;\">是印度洋上的一个岛国，由</span><span style=\"line-height:1.5;font-size:14px;color:#666666;\">1200余个小珊瑚岛屿组成，其中202个岛屿有人居住，是亚洲最</span><span style=\"line-height:1.5;font-size:14px;color:#666666;\">小的国家。马尔代夫南部的赤道海峡和一度半海峡为海上交通要</span><span style=\"line-height:1.5;font-size:14px;color:#666666;\">道，一直享有“上帝抛洒人间的项链”，“印度洋上人间最后的乐</span><span style=\"line-height:1.5;font-size:14px;color:#666666;\">园”的美誉。</span> \r\n</p>\r\n<p class=\"p\" style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/05/24/d024d180-0909-4645-a487-4ced4cc62df9.jpg\" alt=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#666666;font-size:14px;\">随着全球的气候变暖， 海平面正以每年两厘米上升。这对于平均</span><span style=\"color:#666666;font-family:\'Helvetica Neue\';line-height:1.5;font-size:14px;\">海拔只有1.2米的马尔代夫来说是致</span><span style=\"color:#3E3E3E;font-family:\'Helvetica Neue\';font-size:14px;line-height:1.5;\"><span style=\"color:#666666;\">命的</span>。</span><span style=\"line-height:1.5;font-family:\'Helvetica Neue\';color:#D7ABA9;font-size:14px;\">那么，在这座伊甸园涅槃之前，你是否也要安排一次美丽的邂</span><span style=\"line-height:1.5;color:#D7ABA9;font-family:\'Helvetica Neue\';font-size:14px;\">逅，在马代，和恋人，把浪漫与快乐永</span><span style=\"line-height:1.5;color:#D7ABA9;font-family:\'Helvetica Neue\';font-size:14px;\">远铭刻在内心深处的某个</span><span style=\"color:#D7ABA9;font-family:\'Helvetica Neue\';line-height:1.5;font-size:14px;\">角落？</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#D7ABA9;font-size:12px;\"> </span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;text-indent:0pt;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#3E3E3E;font-size:16px;\"><span style=\"color:#FFFFFF;font-size:16px;background-color:#00D5FF;\"><strong><em>马尔代夫豪华潜水游轮</em></strong></span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;text-indent:0pt;background:#FFFFFF;\">\r\n	<span style=\"font-size:14px;background-color:#00D5FF;\"><img src=\"/attached/image/2016/05/24/af3de907-c8d4-430a-8b3f-a06a660ed9f8.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-align:center;text-indent:0pt;background:#FFFFFF;\">\r\n	<img src=\"/attached/image/2016/05/24/02de84a9-f3d5-4b10-9360-67684337fb1a.jpg\" alt=\"\" /> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#3E3E3E;font-size:12px;\"><span style=\"color:#00D5FF;font-size:14px;\">时间</span><span style=\"font-size:14px;color:#666666;\">：2016.9.25---2016.10.2</span></span><span style=\"font-family:\'Helvetica Neue\';color:#3E3E3E;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#666666;font-size:14px;\">&nbsp; &nbsp; &nbsp; &nbsp; 8天7夜（17次潜水）</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#FFFFFF;font-size:16px;background-color:#00D5FF;\"><strong>行程安排</strong></span><span style=\"font-family:\'Helvetica Neue\';color:#3E3E3E;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#F96E57;font-size:14px;\">Day 1</span><span style=\"font-family:\'Helvetica Neue\';color:#F96E57;font-size:14px;\">（9.25）&nbsp;</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#666666;font-size:14px;\">抵达马累，由工作人员接至多尼船直接接送至游艇休息。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#F96E57;font-size:14px;\">Day 2--Day 7</span><span style=\"font-family:\'Helvetica Neue\';color:#F96E57;font-size:14px;\">&nbsp;(9.26--10.1)</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#666666;font-size:14px;\">中文潜导潜水知识讲解完成每天3次潜水</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#666666;font-size:14px;\">（共17次潜水，第7天2潜，包括2次夜潜）</span><span style=\"font-family:\'Helvetica Neue\';color:#888888;font-size:10.5pt;\"></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#D7ABA9;font-size:14px;\">夜晚游艇星空派对及甲板夜钓或者无人岛BBQ</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#F96E57;font-size:14px;\">Day 8&nbsp;</span><span style=\"font-family:\'Helvetica Neue\';color:#F96E57;font-size:14px;\">(10.2)</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#666666;font-size:14px;\">起床（收拾行李）和早餐9:00游艇停靠马累附近，</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#666666;font-size:14px;\">可以选择体验一次水上飞机，逛马累的集市自由行</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:0pt;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#666666;font-size:14px;\">（有时间的朋友）</span><span style=\"font-family:\'Helvetica Neue\';color:#3E3E3E;font-size:12pt;\"></span> \r\n</p>\r\n<p class=\"p\" style=\"margin-left:0.0000pt;\">\r\n	<span style=\"font-family:\'Helvetica Neue\';color:#666666;font-size:14px;\">12点行程结束前往机场</span><span style=\"font-family:Calibri;font-size:12.0000pt;\"></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:16px;color:#FFFFFF;background-color:#00D5FF;\"><strong>行程价格</strong></span> \r\n</p>\r\n<p class=\"p\" style=\"margin-left:0.0000pt;\">\r\n	<b><span style=\"color:#E56600;font-size:14px;\">潜水员：13660元|位</span></b> \r\n</p>\r\n<p class=\"p\" style=\"margin-left:0.0000pt;\">\r\n	<b><span style=\"color:#E56600;font-size:14px;\">非潜水员（随军家属）：10660元|位</span></b> \r\n</p>\r\n<p class=\"p\" style=\"margin-left:0.0000pt;\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<i><span style=\"color:#337FE5;font-size:14px;\"><strong>费用包含：</strong></span></i> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"color:#00D5FF;font-size:14px;\">◆</span><span style=\"font-size:14px;color:#666666;\">马累机场至游艇往返接送</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"color:#00D5FF;font-size:14px;\">◆</span><span style=\"font-size:14px;color:#666666;\">7晚游艇住宿(俩人一间）</span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"color:#00D5FF;font-size:14px;\">◆</span><span style=\"font-size:14px;\">&nbsp;<span style=\"color:#666666;\">一日三餐（游艇早，中，晚餐）</span></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"color:#00D5FF;font-size:14px;\">◆</span><span style=\"font-size:14px;color:#666666;\">17次潜水（每天三潜，第