INSERT INTO `dive_address` VALUES ('500bd75b-6655-4349-8764-f33c8a7c3330', '2015-08-10 14:38:21', 'AR02', '<p style=\"font-size:14px;color:#666666;font-family:微软雅黑;\">\r\n	<img src=\"/dive/attached/image/2015/08/10/b7745fed-15b0-48dd-a113-746056941118.jpg\" alt=\"\" /> \r\n</p>\r\n<strong><span style=\"font-size:14px;color:#666666;\">潜点名称：海扇森林</span><br />\r\n<span style=\"font-size:14px;color:#666666;\"> 地址：东南亚中国海南省拉央拉央岛</span><br />\r\n<span style=\"font-size:14px;color:#666666;\"> 深度：25-40米</span><br />\r\n<span style=\"font-size:14px;color:#666666;\"> 可见度：30米以上</span></strong><br />\r\n<br />\r\n<br />\r\n<p style=\"font-size:14px;color:#666666;font-family:微软雅黑;\">\r\n	<span style=\"font-size:14px;line-height:1.5;\">&nbsp;&nbsp;&nbsp; 柳珊瑚丛林(Gorgonian Forest)紧接着狗牙穴的礁岩，所以在此处见到鲨群也不足为怪。但此处以海扇（Sea fans）更为闻名。20米深处的礁岩被五颜六色的 巨大海扇所覆盖——非常适合宽角度的摄影。导航者通道（Navigator\'s Lane）是挨着的礁岩。此处海扇让位于更令人惊艳的软珊瑚（soft corals）。我们在此处 潜水时遇到过锤头鲨，并且通常还有灰鲨和白斑鲨在礁岩外绕圈。金枪鱼也上下漫游寻找鱼群中松懈落伍的小鱼。</span>\r\n</p>\r\n<p style=\"font-size:14px;color:#666666;font-family:微软雅黑;\">\r\n	<img src=\"/dive/attached/image/2015/08/10/3a1a5ad3-43bd-4be2-914c-844b4e49a972.jpg\" alt=\"\" /> \r\n</p>\r\n<img src=\"/dive/attached/image/2015/08/10/15ea98e0-b3a0-4686-99a4-ca53490a5be9.jpg\" alt=\"\" /><br />', 'FT02', 'uploadfiles/address/address_4TCo1439188700603.jpg', '海扇森林', 'ST01', '柳珊瑚丛林(Gorgonian Forest)紧接着狗牙穴的礁岩，所以在此处见到鲨群也不足为怪。', '0');
INSERT INTO `dive_address` VALUES ('68095d30-f19f-4374-8169-678441678600', '2015-08-10 13:36:46', 'AR02', '<p>\r\n	<span style=\"font-family:微软雅黑;line-height:1.5;font-size:14px;color:#666666;\"><span style=\"font-size:14px;color:#666666;line-height:1.5;\"><span style=\"font-family:微软雅黑;line-height:1.5;font-size:14px;color:#666666;\">&nbsp; &nbsp;&nbsp;</span></span></span><span style=\"font-family:微软雅黑;line-height:1.5;font-size:14px;color:#666666;\"><span style=\"font-size:14px;color:#666666;line-height:1.5;\"><span style=\"font-family:微软雅黑;line-height:1.5;font-size:14px;color:#666666;\"><span style=\"font-size:14px;color:#666666;line-height:1.5;\">这里称为海底花园，如果你想观赏漂亮的珊瑚</span></span></span></span><span style=\"font-family:微软雅黑;line-height:normal;\"><span><span style=\"font-family:微软雅黑;line-height:1.5;font-size:14px;color:#666666;\">和热带鱼，这个潜点是最好不过了。鬼湾位于大</span><span style=\"font-family:微软雅黑;line-height:1.5;font-size:14px;color:#666666;\">小横洲之间水域，这里水深3至12米，由于周围环</span><span style=\"font-family:微软雅黑;line-height:1.5;font-size:14px;color:#666666;\">岛不受风浪影响，能见度可达十米，是初学者理</span><span style=\"font-family:微软雅黑;line-height:1.5;font-size:14px;color:#666666;\">想的下潜点。海床为沙石珊瑚礁底，常见此潜点</span><span style=\"font-family:微软雅黑;line-height:1.5;font-size:14px;color:#666666;\">有蘑菇珊瑚、鹿角珊瑚还有多种软体珊瑚，这</span><span style=\"font-family:微软雅黑;line-height:1.5;font-size:14px;color:#666666;\">里有几处礁石长滿了海葵，更住满了很多可爱的</span><span style=\"font-family:微软雅黑;line-height:1.5;font-size:14px;color:#666666;\">小丑鱼。</span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:微软雅黑;line-height:normal;font-size:16px;\"><span style=\"font-size:12px;\"><span style=\"font-family:微软雅黑;line-height:normal;\"><img src=\"/dive/attached/image/2015/08/10/44d6d285-5185-40a4-a8b6-95d40c67bcb0.jpg\" alt=\"\" /><br />\r\n</span></span></span> \r\n</p>\r\n<span style=\"color:#666666;font-family:微软雅黑;font-size:14px;line-height:1.5;\">&nbsp;</span><span style=\"color:#666666;font-family:微软雅黑;line-height:25.2000007629395px;font-size:16px;\"><span style=\"font-size:14px;line-height:1.5;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size:14px;line-height:1.5;\">这里除了可以潜水还是一个非常适合浮潜的地</span></span><span style=\"color:#666666;font-family:微软雅黑;line-height:1.5;font-size:14px;\">方，如果你还没学习过潜水课程，或想让小朋友</span><span style=\"color:#666666;font-family:微软雅黑;line-height:1.5;font-size:14px;\">一起亲近大自然，建议可在这里浮潜。</span><br />\r\n<p>\r\n	<br />\r\n</p>\r\n<span style=\"font-family:微软雅黑;line-height:normal;\"></span>', 'FT02', 'uploadfiles/address/address_cJ2k1439395834664.jpg', '鬼湾', 'ST01', '这里称为海底花园，如果你想观赏漂亮的珊瑚和热带鱼，这个潜点是最好不过了。', '0');
INSERT INTO `dive_address` VALUES ('9ad81a73-1345-408d-bfd5-e04e93f918cf', '2015-07-22 16:15:28', 'AR01', '<p style=\"font-size:14px;color:#666666;font-family:微软雅黑;\">\r\n	<img src=\"/dive/attached/image/2015/08/10/ddc2bfff-5218-40f6-80c9-f4ed2215102b.jpg\" alt=\"\" /> \r\n</p>\r\n<strong><span style=\"font-size:14px;color:#666666;\">潜点名称：绿岛&nbsp; &nbsp;</span><br />\r\n<span style=\"font-size:14px;color:#666666;\"> 地址：亚洲中国台湾省绿岛</span><br />\r\n<span style=\"font-size:14px;color:#666666;\"> 水深:&nbsp; 14-25 米</span><br />\r\n<span style=\"font-size:14px;color:#666666;\"> 可见度: 5-20米</span></strong><br />\r\n<p style=\"font-size:14px;color:#666666;font-family:微软雅黑;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-size:14px;color:#666666;font-family:微软雅黑;\">\r\n	<span style=\"line-height:1.5;\">&nbsp;&nbsp;&nbsp; 绿岛一年四季皆宜潜水活动，但因季节风的因素，所以10月至4月大多在岛的西南侧如：南寮、龟湾一带活动。 5月至9月大多在岛的东北侧如：楼门岩、公馆、中寮一带。视天气状况而决定潜水计画。</span><br />\r\n<img src=\"/dive/attached/image/2015/08/10/16fae123-8f2a-49df-a3d7-9c5bc06c7218.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"font-size:14px;color:#666666;font-family:微软雅黑;\">\r\n	<img src=\"/dive/attached/image/2015/08/10/c54193b2-a6c9-4861-97c9-5d941d11b3af.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"font-size:14px;color:#666666;font-family:微软雅黑;\">\r\n	<img src=\"/dive/attached/image/2015/08/10/c2717ed8-e63c-476b-a2ca-b04727d61c67.jpg\" alt=\"\" /> \r\n</p>', 'FT03', 'uploadfiles/address/address_Kszv1439189292114.jpg', '绿岛', 'ST01', '绿岛一年四季皆宜潜水活动，但因季节风的因素，所以10月至4月大多在岛的西南侧如：南寮、龟湾一带活动。', null);
INSERT INTO `dive_address` VALUES ('a531badb-e811-4f83-a5a7-f2f6094482f8', '2015-08-10 14:15:19', 'AR02', '<strong><span style=\"font-size:14px;color:#666666;\">潜点名称：杨梅坑</span><br />\r\n<span style=\"font-size:14px;color:#666666;\"> 位置：中国广东省深圳市龙岗区</span></strong><br />\r\n<br />\r\n<span style=\"font-family:微软雅黑;line-height:1.5;font-size:14px;color:#666666;\">&nbsp; &nbsp; 如果你想轻松方便的话前往杨梅坑的珊瑚区下潜是个不错的选择，珊瑚礁区离岸约200米处，水深4至6米，在些下潜可以观赏到众多细小的珊瑚鱼群在珊瑚礁追逐，因这里也是众多水上运动的活动区，在这里下潜时需留意摩托艇、快艇和其它船只经过。</span><br />\r\n<img src=\"/dive/attached/image/2015/08/10/aa694a7c-2627-4a2a-a946-4d3057d4c799.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:微软雅黑;line-height:1.5;font-size:14px;color:#666666;\">&nbsp; &nbsp; &nbsp; 潜完珊瑚礁可以再前往浪骑游艇会大坝底部由巨石彻成,防浪堤延伸到海底十多米，堤坝底部水深约十米，能见度可达五到八米，由于巨石构成错综复杂的空隙，制造了众多鱼类理想的匿藏和繁殖环境，这个潜点的特色主要是观看珊瑚鱼，还有一些微小的海洋生物。有时也会有大石斑出现，由于大坝离公路较近，每天有众多浮潜爱好者在此猎鱼，在此下潜需留意。</span><br />\r\n<img src=\"/dive/attached/image/2015/08/10/9caf3b9b-18e7-44ca-8c52-362049ceccf4.jpg\" alt=\"\" /><br />\r\n<p style=\"font-size:14px;color:#666666;font-family:微软雅黑;\">\r\n	<span style=\"line-height:1.5;\">&nbsp; &nbsp; &nbsp; &nbsp;也许很多潜友都知道在杨梅坑还有个人工珊瑚礁，人工珊瑚礁投放区域位于杨梅坑对开海域约1000米处，由浪骑游艇会大坝对开150米处至东3公里处都有投放，水深约13米，礁体为混凝土框架、汽车轮胎、废旧渔船。由于投放海床为淤泥，水流会将淤泥带起，能见度好的机会较少。</span> \r\n</p>\r\n<p style=\"font-size:14px;color:#666666;font-family:微软雅黑;\">\r\n	<img src=\"/dive/attached/image/2015/08/10/1f32b958-661c-49d0-baf9-924b45269419.jpg\" alt=\"\" /> \r\n</p>', 'FT02', 'uploadfiles/address/address_VMAP1439187357325.jpg', '杨梅坑', 'ST01', '如果你想轻松方便的话前往杨梅坑的珊瑚区下潜是个不错的选择', '0');
