INSERT INTO `dive_equip` VALUES ('06d1649c-aa2e-4a40-b56d-b137b873e5f1', '2015-12-18 09:34:21', 'EB01', '<p>\r\n	<span style=\"font-size:18px;color:#337FE5;line-height:1.5;font-family:\'Microsoft YaHei\';\"><strong>Mares Air Control</strong></span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">Code:416893</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">最佳演变</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">备用与BC充排气管的完美结合</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">符合人体工程学的外形特征</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">咬嘴与网格盖的独特设计使得使用更加方便</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">重量(g): 241 INT</span><br />\r\n<img src=\"/attached/image/2015/12/18/70840335-6e75-45a6-9df6-b83854d38ee3.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#999999;line-height:1.5;\">Air Control, Mares空气系统的全新替代，备用调节器与BC充排气管的完美结合，备用调节器中咬嘴与网格盖的独特设计使得使用更加方便，调节器盖子上具备专利的网格设计是由柔软的材料制作而成，所以即使你是戴着手套在按的操作中也非常的轻松方便。专用的符合EC标准的适配器使得其容易的适用于所有BC。符合人体工程学的形状允许我们单手方便的使用。</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;font-size:14px;color:#FF9900;\">价格：1500元</span>\r\n</p>', 'uploadfiles/equip/equip_GMMN1450402461094.jpg', 'Mares Air Control', '备用与BC充排气管的完美结合\r\n', 'ST01', 'ET07', null, '1500', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', '');
