INSERT INTO `dive_equip` VALUES ('0fa26292-cdc4-46ef-904f-e23c08a73121', '2016-01-25 15:23:49', 'EB09', '<p>\r\n	<span style=\"line-height:1.5;font-family:\'Microsoft YaHei\';font-size:24px;color:#337FE5;\"><strong>TUSA BCJ-4000 Soverin</strong></span> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/01/25/21a98558-b5c8-48ec-abf5-3d1f5655faa5.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\">BCJ-4000 Soverin是TUSA BC中集合主要功能和新設計風格的夾克。Soverin提供價值以及無與倫比的舒適性和耐久性。BCJ-4000具有500D的Cordura R尼龍以及新開發的高密度立體泡棉提高了在肩部、背墊和腰部的舒適性。BCJ-4000也提供全新設計的硬質提把方便搬運，重新設計的獨立式固定帶系統，極致穩定背架和腰部的支撐，在水中充份給予支撐和舒適性。此外，Soverin也提供刀扣眼和一個可調整的胸扣裝置。</span> \r\n</p>\r\n<img src=\"/attached/image/2016/01/25/b914de66-233c-448b-afb7-2264f0536abf.jpg\" alt=\"\" /><img src=\"/attached/image/2016/01/25/a1dde42c-7fae-49e4-8d44-ca499cc56479.jpg\" alt=\"\" /><img src=\"/attached/image/2016/01/25/3156bd2d-a56c-4912-977a-2b7558ebd0f5.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 特色：</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 全新設計的獨立式安全帶系統</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 腰部支撐系統</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 3Ｄ泡棉式背墊</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 軟墊肩帶提供出色的舒適性</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 先進的配重系統（A.W.L.S）可從後方調整配重口袋高度</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 500D CORDURA 尼龍布料具有良好的強度和耐久性</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 4個拉鍊口袋 - 2個大口袋、2個小口袋可放置較小較輕的配件</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 附有儀錶管線放置的袋口</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> FK-10刀扣眼（需使用TC-752刀座安裝）</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 可調胸帶安全哨扣</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 2個不銹鋼和4個標準D型環</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 3快洩閥門（防爆閥+快洩閥x2）</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 可選購A.P.A裝置</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> SIZE：XS、S、M 、L</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#FF9900;line-height:1.5;\">价格：4000元</span>\r\n</p>', 'uploadfiles/equip/equip_zuv81453706629180.jpg', 'TUSA BCJ-4000 Soverin', '獨立式安全帶系統\r\n（A.W.L.S）配重系统', 'ST01', 'ET08', null, '4000', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', 'SZ002,SZ003,SZ004,SZ005');
