INSERT INTO `dive_equip` VALUES ('10f16910-8754-48a0-a6ee-4b6f37a9906d', '2015-12-18 10:35:07', 'EB01', '<p>\r\n	<span style=\"font-size:18px;color:#337FE5;line-height:1;font-family:\'Microsoft YaHei\';\"><strong>Mares HYBRID MRS+&nbsp;</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;color:#337FE5;line-height:1;font-family:\'Microsoft YaHei\';\"><strong>SHE DIVES（女式）</strong></span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">Code:417346</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">可折叠的背板</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">超重量轻</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">柔软耐用的Alutex材料</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">前所未有的合身和贴服</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">低平的排气阀（比mares老款低30%） 减少触碰</span> \r\n</p>\r\n<p>\r\n	<span><span style=\"font-size:14px;line-height:21px;\"><span style=\"color:#999999;font-size:14px;line-height:21px;\">女式款具有独特的剪裁，特别符合女性潜水员的身材特征</span><br />\r\n</span></span> <img src=\"/attached/image/2015/12/18/6dd0982a-1510-4e20-9c9f-f90ac47e99e3.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#999999;line-height:1.5;\">Hybrid代表了Mares BCD的最新研发成果：重量轻、柔软且耐用的材料、气囊和甲胄完全分离、无缝的肩带、背架双侧的衬垫，这些都将带给您无可比拟的舒适度。其它特色如：可折叠的背板、超重量轻、柔软耐用的Alutex材料、配重压铅袋以及两侧的拉链式增大口袋、内置快卸扣、刀鞘固定扣、Suspension system腰部设计、低平的排气阀（比mares老款低30%）、MRS配重带与补偿配重袋。</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">款式：前调整式</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">材料：Cordure 420</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">背板：可折叠式</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">重量(size L):3.7kg</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">D-型环：8个铝制</span><br />\r\n<s