INSERT INTO `dive_equip` VALUES ('1baccef3-8028-43a9-a4a3-d66c95999634', '2015-12-19 14:43:30', 'EB06', '<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>Safe mew fin 十字潜水蛙鞋</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"></span><img src=\"/attached/image/2015/12/19/c9dbe412-3053-4d9e-a0d5-f651a844978c.jpg\" alt=\"\" /> \r\n</p>\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">· 橙色造型加上底部的十字标记，易于在水中及陆上辨认，救援潜水员的最佳选择</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> · 采用优质橡胶制造，硬度介乎一般Mew及加硬版之间，同样轻巧而灵敏度高</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> · 能快速地在水面起动</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> · 水底踢动控制自如</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> · 男女皆宜、设计全能的蛙鞋，适用于浮潜或水肺潜水</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> · 根据人体工学设计，舒适自然</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#FF9900;\">价格：820元</span>\r\n</p>', 'uploadfiles/equip/equip_HRqy1450507410442.jpg', 'GULL safe mew fin 十字潜水蛙鞋', '救援潜水员的最佳选择\r\n能快速地在水面起动', 'ST01', 'ET06', null, '820', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', 'CL005', 'SZ003,SZ004,SZ005');
INSERT INTO `dive_equip` VALUES ('1c672e54-26a1-4a31-aabb-e70fd352b52c', '2016-01-25 14:00:40', 'EB03', '<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>Aqua Lung</strong></span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>X Shot</strong></span><br />\r\n<img src=\"/attached/image/2016/01/25/197fb2c9-4e55-4e14-91eb-8a31c27b2330.jpg\" alt=\"\" /><img src=\"/attached/image/2016/01/25/d657dc13-73f2-429f-b3a4-f8546ad3d85b.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> X Shot 是结合了最新科技和最新材料的高效能蛙鞋，它采用了 SlingShot 和 HotShot 的设计理念并在其基础进行了发展。</span><br />\r\n<br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 蛙鞋的动力区按常规设计在鞋套的前部，但在结合了“V 型推进区”后使潜水员即使用更少的力也能有更大的动力。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 新型弹簧鞋带配合带扣让你感觉更加舒服</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 鞋套上配备了“动力传动区”，无需刻意伸展足部也能获得强劲的推动力</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 鞋面使用了新型材料以增加弹力并增进推动力</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 弹性鞋面加入了“汤勺效应”可使每一次向下踢动更具效率</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 鞋套设计更贴合潜水员足型，不会浪费任何踢动所产生的动力</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#FF9900;\">价格：1000元</span>\r\n</p>', 'uploadfiles/equip/equip_MWJF1453701640016.jpg', 'Aqua Lung X Shot', '新型材料\r\n增强弹力', 'ST01', 'ET06', null, '1000', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', 'CL005,CL009', 'SZ002,SZ003,SZ009,SZ006');
