INSERT INTO `dive_equip` VALUES ('1eaaa999-56a9-4223-a73f-d7433954d514', '2015-12-19 23:01:27', 'EB07', '<h2>\r\n	<strong><span style=\"color:#337FE5;\">Scubapro Delta Short Boots</span></strong> \r\n</h2>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"></span> \r\n</p>\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">多功能橡胶鞋底舒适的踝靴</span><br />\r\n<img src=\"/attached/image/2015/12/19/828f7bbe-92ca-4768-8a22-befdb8430b58.png\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> &nbsp; &nbsp; 5mm厚度的橡胶鞋底，增加舒适度。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> &nbsp; &nbsp; 非常灵活，很容易磨损可调鳍。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> &nbsp; &nbsp; 对于温暖的海水，倏地打开和关闭无拉链。</span><br />\r\n<br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 品牌：Scubapro</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 材料：橡胶及Neophrene</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 颜色：黑</span>\r\n</p>\r\n<p>\r\n	<span><span style=\"color:#FF9900;\">价格：280元</span></span>\r\n</p>', 'uploadfiles/equip/equip_4Qku1450537287098.png', 'SCUBAPRO Delta Short Boots 5mm', '5mm短筒软底潜水鞋', 'ST01', 'ET04', null, '280', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', 'SZ022,SZ023,SZ024,SZ025,SZ026,SZ027,SZ028,SZ029');
INSERT INTO `dive_equip` VALUES ('1eb678bc-a364-4000-96a7-68b8d85fe341', '2015-12-18 09:38:11', 'EB01', '<p>\r\n	<span style=\"font-size:18px;color:#337FE5;line-height:1.5;font-family:\'Microsoft YaHei\';\"><strong>Mares CARBON 52</strong></span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">Code:416162</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">金属性能，重量超轻</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">100%碳素纤维材料的二级调节器</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">双DFC（动态流量控制系统）</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">出众的热传导率</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\"></span><img src=\"/attached/image/2015/12/18/91e2a8ea-4ed2-436e-a279-e8614b356a13.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#999999;line-height:1.5;\">Carbon全碳素的二级调节器使其具有金属版的热传导性，全新的MR52一级调节器，MR52是为了现在技术潜水的需要，保持了MR22一级调节器的通气量，但尽可能的缩小了体积，将所有的中压接口都并排设计适合于技术潜水中的双瓶需要，在配置上，具备了双DFC系统，使得我们不仅在享用主调节器带来的顺畅呼吸外，备用也依旧可以享用；NCC自然对流系统大大提高了在冷水环境下的热交换能力。</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\"> 产品详情</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\"> Code: 416162</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\"> 一级调节器类型：平衡隔膜设计</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\"> 二级调节器类型：顺流式供气阀</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\"> 一级调节器重量： 687g</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\"> 二级调节器重量：197g</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\"> 总重量（g）： 1034</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\"> HP端口： 2个 HP 7/16\"</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\"> LP端口： 4个 LP 3/8\" &nbsp;其中有两个DFC接口</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\"> 中压管:80cm超柔软编织护套中压软管</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#FF9900;line-height:1.5;\">价格：5300元</span> \r\n</p>', 'uploadfiles/equip/equip_heUQ1450402691248.jpg', 'Mares Carbon 52', '100%碳素纤维\r\n出众的热传导率', 'ST01', 'ET07', null, '5300', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', '');
INSERT INTO `dive_equip` VALUES ('1f592aaa-ac89-4e79-9eea-97c3fb3ee875', '2015-12-18 10:54:25', 'EB01', '<p>\r\n	<span style=\"font-size:18px;color:#337FE5;line-height:1.5;font-family:\'Microsoft YaHei\';\"><strong>Mares AVANTI EXCEL</strong></span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">Code:410316</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#999999;line-height:1.5;\">在各种潜水条件下均具有最佳性能</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">采用鲸鱼尾设计 推力更大 体力更省</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">符合解剖学特点的脚套</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">采用ABS Plus搭扣</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">最佳性能的三合材料</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">OPB:该型脚蹼可使叶板呈最佳角度 可减少踢水时的疲劳感</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">导流口推力技术：水沿着脚蹼的叶板使其产生的推力增大更有效的推动水流</span> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/12/18/8ab49c8d-7f2a-4a1e-b814-1166aaab4b79.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/18/4ad3c2f4-f67c-4962-83d2-17fa85b68944.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/18/37e11321-17f7-4284-ae90-6144c445a7bb.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/18/8ecd3505-f93e-4e3f-84e0-b964bd00558c.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#999999;line-height:1.5;\">鞋肚类型：套脚式鞋肚</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">材质：热塑性橡胶 聚丙烯</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">叶板长度（size R）:34cm</span><br />\r\n<img src=\"/attached/image/2015/12/18/b8651d9b-7e41-490f-9cd7-89cb9cea7016.jpg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#FF9900;line-height:1.5;\">价格：580元</span> \r\n</p>', 'uploadfiles/equip/equip_uhaJ1450407264826.jpg', 'Mares AVANTI EXCEL', '采用鲸鱼尾设计 推力更大\r\n叶板呈最佳角度 减少疲劳', 'ST01', 'ET06', null, '580', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', 'CL009,CL006,CL003,CL001', 'SZ022,SZ023,SZ024,SZ025,SZ026,SZ027,SZ028,SZ029');
INSERT INTO `dive_equip` VALUES ('1f8662e5-6a6d-4c1e-a600-962442a09142', '2015-12-20 00:32:24', 'EB07', '<p>\r\n	<strong><strong><span style=\"font-family:\'Microsoft YaHei\';font-size:18px;color:#337FE5;\">Scubapro&nbsp;</span></strong><span style=\"font-family:\'Microsoft YaHei\';font-size:18px;color:#337FE5;\"></span><span style=\"font-family:\'Microsoft YaHei\';font-size:18px;color:#337FE5;\">MK21配G260&nbsp;</span></strong> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/12/20/d1a9861e-38f4-4885-b69b-edbd01d44e67.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">MK21：</span> \r\n</p>\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">&nbsp;防结冰, 合寒冷地区使用</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> &nbsp;中档活塞一级头，最合理的价格提供合适的性能和最佳平衡点</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> &nbsp;独特的热交换设计，外翅片，优化抗冻性</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> &nbsp;可选的可更换头4个LP路由配置（0°/30°）。理想技术潜水的最佳定位</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> &nbsp;标准头4个LP软管路由（+ / -20°）和2个HP端口，主体各侧都有一个</span><br />\r\n<br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> G260：</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 技术潜水员以及洞穴潜水爱好者的理想选择，同时也是一款适合在冷水域潜水的二级调节器。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 平衡式设计的二级调节器，全金属部件（通气管、底座、吸气控制阀以及喉管接口）即使在冷水中潜水也能发挥最佳性能。它的超高性能能够满足任何苛刻的潜水员的要求。同样，它也是 G250 粉丝们的首选。新增加的、可左右安装的喉管进一步使这款调节器成为技术潜水员的最爱。</span><br />\r\n<br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 空气平衡阀</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 潜水员可自行调节的呼吸阻抗</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 可由潜水员自行调节的防漏气开关（VIVA）</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 超高流量喉管</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 超高舒适度咬嘴</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 全金属阀门套件</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 重量：275 克 / 9.7 盎司</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 空气流量（200 bar 时）：1800 升/分钟 - 66 立方尺/分钟</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 最高低压压力：14 bar / 203 PSI</span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#FF9900;\">价格：3200元</span>\r\n</p>', 'uploadfiles/equip/equip_StKL1450542743515.jpg', 'Scubapro MK21配G260', '专业配套\r\n适合冷水', 'ST01', 'ET07', null, '3200', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', '');
INSERT INTO `dive_equip` VALUES ('218cd0c1-0091-4c98-ac7f-8f1ab3e977ac', '2016-01-25 14:41:15', 'EB03', '<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:24px;\"><strong>Aqua Lung</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:24px;\"><strong>Zuma</strong></span> \r\n</p>\r\n<img src=\"/attached/image/2016/01/25/30e8f89d-1c4c-4a95-9b51-1c99bd6e9a00.jpg\" alt=\"\" /><img src=\"/attached/image/2016/01/25/63e62921-d707-4f1e-94a7-91eba220e724.jpg\" alt=\"\" /><img src=\"/attached/image/2016/01/25/099e3dbb-8af9-402c-b130-5cdc45d6bc49.jpg\" alt=\"\" /><img src=\"/attached/image/2016/01/25/fbfb1d62-7454-4a5f-8723-09865e3041f6.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">极度轻便</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> ML/LG 尺寸的重量小于 2.2 公斤（4.75 磅）</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 无论平整或卷起，都非常容易打包</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 配备 SureLock II（专利技术）内置整合机械式配重系统。配重袋能自行滑入凹槽，因此只需将配重袋插入扣住即可。简单的拉出式卸除方式能够在紧急情况中快速卸除。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 革命性的气瓶支撑系统终结了需要硬式背架的时代</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 下方的气瓶固定带与腰带的高度相同</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 气瓶头的固定带保持气瓶垂直并将重量转移至臀部以减轻肩膀的负担</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 只需 4 个尺寸即可涵盖从 XXS 到 XXL 的所有尺寸</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 可调整胸部固定带</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 平行设计的两条带快卸扣的肩带可自由调整肩带的上下位置</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 专利设计的扁平排气阀让 BCD 的打包体积更小（专利申请中）</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 口袋盖向下关闭使物品存取更方便</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 配备 6 个 D 型环可挂置潜水用具</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 预留潜水刀固定孔</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 右侧肩带配有快速排气阀拉绳</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 右侧底部同样配有快速排气阀</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 网状填充肩带</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 填充式脊椎及腰椎软背架</span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#FF9900;\">价格：3000元</span>\r\n</p>', 'uploadfiles/equip/equip_8PQH1453704074973.jpg', 'Aqua Lung Zuma', '极度轻便\r\n革命性的气瓶支撑系统', 'ST01', 'ET08', null, '3000', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', 'CL003,CL007,CL004,CL008', 'SZ002,SZ003,SZ004,SZ005,SZ006');
INSERT INTO `dive_equip` VALUES ('222208b8-ac61-4e69-8d9c-70b97174396e', '2015-12-19 23:38:17', 'EB07', '<p>\r\n	<strong><span style=\"font-family:\'Microsoft YaHei\';font-size:18px;color:#337FE5;\">Scubapro GO BCD&nbsp;</span></strong> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">重量轻 易于包装</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">大尺寸，重量只有2.4公斤/4.2磅。</span> \r\n</p>\r\n<img src=\"/attached/image/2015/12/19/52c09db6-578d-446e-921c-ed7fd2c740d1.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/19/80dae7f3-9fd7-45d0-8f71-3e29a9c20e45.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> &nbsp; &nbsp;&nbsp;</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> &nbsp; &nbsp; 背包重量轻、软，没有硬性元素</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> &nbsp; &nbsp; 整个 BC可以很容易地折叠</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> &nbsp; &nbsp;&nbsp;长期磨损和快干的网格</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> &nbsp; &nbsp; 大尺寸铝合金 D型环</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> &nbsp; &nbsp; 附有挂刀扣</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#FF9900;\">&nbsp; &nbsp; &nbsp;价格：4600元</span>', 'uploadfiles/equip/equip_Z7FW1450595940980.jpg', 'Scubapro GO BCD ', '重量轻 易于包装', 'ST01', 'ET08', null, '4600', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', 'SZ002,SZ003,SZ004,SZ005,SZ006');
INSERT INTO `dive_equip` VALUES ('258a4699-dfcf-428e-881b-4f28daf41def', '2016-01-25 14:29:50', 'EB03', '<span style=\"font-family:\'Microsoft YaHei\';font-size:24px;color:#337FE5;\"><strong>Aqua Lung</strong></span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:24px;color:#337FE5;\"><strong>Pearl</strong></span><br />\r\n<img src=\"/attached/image/2016/01/25/7f45ff2b-2e37-4436-8031-901cc03d4e70.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;\"> 这款最畅销的 BCD 现已增添了新的暮色以突显女性潜水员的线条，同时胸垫也改进为运动型发泡橡胶材质以增加舒适度及耐用性。</span><br />\r\n<br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;\"> 混合式气囊设计让 BCD 兼具夹克式和背囊式BCD最好的特性</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;\"> 专利设计的整合式运动胸罩提供高舒适性、安全性和稳定性</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;\"> 扁平式E-Valves 单向防水快速排气阀减少水的阻力并保持BCD的流线型</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;\"> SureLock II（专利技术）内置整合机械式配重系统能自行滑入凹槽，因此只需将配重袋插入扣住即可。简单的拉出式卸除方式能够在紧急情况中快速卸除。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;\"> 两侧设计有大型带拉链的口袋</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;\"> 配备 4 个 D 型环可挂置潜水用具</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;\"> 预留 Aqua Lung 潜水刀固定孔</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;\"> 两侧的备用调节器固定口（专利申请中）可安全的固定你的备用调节器</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;\"> 可调整式腰带可满足大多数的潜水员的需要</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#FF9900;\">价格：4200元</span>\r\n</p>', 'uploadfiles/equip/equip_WfMb1453703390205.jpg', 'Aqua Lung Pearl', '突显女性潜水员的线条\r\n两侧均有备用调节器固定扣', 'ST01', 'ET08', null, '4200', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', 'CL013', 'SZ002,SZ003,SZ004,SZ005,SZ006');
INSERT INTO `dive_equip` VALUES ('2680cba3-ca9f-47c1-8add-8709a656fc6e', '2015-12-18 14:18:31', 'EB01', '<p>\r\n	<span style=\"font-size:18px;color:#337FE5;line-height:1.5;font-family:\'Microsoft YaHei\';\"><strong>Mares I3<span style=\"color:#337FE5;\">&nbsp;</span></strong></span><strong><span style=\"font-family:\'Microsoft YaHei\';font-size:18px;color:#337FE5;\">Sunrise</span></strong> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#999999;line-height:1.5;\">编号: 411042&nbsp;</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">一个无与伦比的视野视野广阔</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">独特的侧视窗设计</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">Sunrise设计 适合亚洲人</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">不可配光学镜片</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#999999;font-size:14px;line-height:1.5;\">I3结合了三重舒适技术和大视野，中间大视窗以及双侧小视窗确保令你吃惊的宽大视野，双按钮面镜扣，使调整面镜快捷和方便，特别是带着厚潜水手套的时候。</span> \r\n</p>\r\n<p>\r\n	<span><span style=\"font-size:14px;line-height:21px;\"> <span style=\"font-size:14px;color:#999999;line-height:1.5;\">配合Sunrise技术的应用，使得其更加符合亚洲人人中偏长的设计。</span><br />\r\n</span></span><img src=\"/attached/image/2016/06/14/fbc1d38f-e5bd-4456-9585-92149bc4bd65.jpg\" alt=\"\" /><img src=\"/attached/image/2016/06/14/6e0591ad-b1d4-472e-9eb0-56d058f764cd.jpg\" alt=\"\" /><img src=\"/attached/image/2016/06/14/a1a12f11-dfc0-49fa-9bbf-bf55ec9f9a80.jpg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"color:#FF9900;font-size:14px;line-height:1.5;\">价格：320元</span>\r\n</p>', 'uploadfiles/equip/equip_q8DS1450419511368.jpg', 'Mares I3 Sunrise', '具备I3的特点\r\nSunrise设计适合亚洲人', 'ST01', 'ET01', null, '320', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', 'CL001,CL009,CL012', 'SZ040');
