INSERT INTO `dive_equip` VALUES ('2772df59-3a4b-4093-9180-3d8014cbaee1', '2015-12-18 12:05:08', 'EB01', '<p>\r\n	<span style=\"font-size:18px;color:#337FE5;font-family:\'Microsoft YaHei\';\"><strong>Mares VOLO POWER</strong></span><br />\r\n<span style=\"font-size:14px;color:#999999;\">Code: 410008</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#999999;\">重量轻、最省力、高性能</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">导流口推进技术</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">符合解剖学特点的鞋肚内有突起的条纹</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">OPB:该型脚蹼可使叶板呈最佳角度，可减少踢水时的疲劳感，女性的理想用品</span> \r\n</p>\r\n<p>\r\n	<span><span style=\"font-size:14px;line-height:21px;\"><span style=\"color:#999999;font-size:14px;line-height:21px;\">ABS Plus：其设计独特的ABS搭扣，可方便的打开和闭合，双按钮锁定可防止搭扣的意外打开</span><br />\r\n</span></span> <img src=\"/attached/image/2016/01/14/907c3321-a1dd-44aa-b39c-c5fc0c4f8577.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/12/18/c95591c8-3bd4-4c09-9734-266cc033bd4f.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/18/280845da-39ad-4551-ba75-b024691cb5d8.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/18/a3aac12d-def6-4ab4-9e1d-1a7b5e40790b.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#999999;\">鞋肚类型：无跟式开式鞋肚</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">材质：热塑性橡胶 聚丙烯</span><br />\r\n<img src=\"/attached/image/2015/12/18/9cad1d61-ebf4-49c9-baa4-71f247705bcb.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#FF9900;\">价格：1100元</span> \r\n</p>', 'uploadfiles/equip/equip_GijD1450411507567.jpg', 'Mares VOLO POWER', '高效且潮流的设计\r\nVOLO 开口式', 'ST01', 'ET06', null, '1100', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', 'CL001,CL014,CL017,CL020', 'SZ002,SZ003,SZ009,SZ006');
