INSERT INTO `dive_equip` VALUES ('28ad48ae-2f19-4023-9b0c-6b07c5e5097a', '2015-12-19 15:49:30', 'EB06', '<h2>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;\">GULL SUPER MEW FIN</span> \r\n</h2>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">Super Mew是GULL蛙鞋中的皇牌产品</span> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/12/19/fb6c2fb9-5097-4340-b426-d8fb792a9953.png\" alt=\"\" /><img src=\"/attached/image/2015/12/19/10427021-45c1-41bd-b322-67d839b513a9.png\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">立体脚套可方便赤足穿着有助减低脚部磨损，亦可配合特制的Mew Boot，舒适贴服。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">脚踭位置采用防滑设计，於进行跨步式下水时可避免滑倒。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">另特别加高蛙鞋两侧的骨干及加长蹼面长度，大大提升踢动效能。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">蹼面末端呈曲线，可借助水流的力量提升动能。</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;font-family:\'Microsoft YaHei\';color:#999999;\">Super Mew以速度见称，并已在多项双蹼比赛中取得多个奖项。</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\"><img src=\"/attached/image/2015/12/19/63e6a679-7d33-4840-b563-144251c3397b.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">S: L580×W200×H83mm</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> M: L600×W210×H87mm</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> L: L615×W210×H87mm</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> XL: L650×W210×H93mm</span><br />\r\n<div>\r\n	<span style=\"color:#FF9900;\">价格：1200元</span> \r\n</div>', 'uploadfiles/equip/equip_agdH1450511370097.jpg', 'GULL SUPER MEW FIN', '脚踭位置采用防滑设计\r\nSuper Mew以速度见称', 'ST01', 'ET06', null, '1200', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', 'CL008,CL007,CL004,CL001,CL009,CL006,C