INSERT INTO `dive_equip` VALUES ('36024cdb-39ee-4ce5-9b8e-5792a1c026a1', '2015-12-18 11:03:23', 'EB01', '<p>\r\n	<span style=\"font-size:18px;color:#337FE5;font-family:\'Microsoft YaHei\';\"><strong>Mares AVANTI QUATTRO POWER</strong></span><br />\r\n<span style=\"font-size:14px;color:#999999;\">Code:410301</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#999999;\">4个导流口 4倍推力</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">卓越的性能 更大的舒适度</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">符合解剖学特点的脚套</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">鞋肚可向叶板传送更大的动力</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">蛙鞋的高稳定性减少扭伤脚踝的可能</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">Channel THrust:导流口推力技术可以通过导流槽引导水流，使其产生的推力增大 Upper&amp;Lower stabilizers：脚蹼叶板上的垂直加强肋在打水时具有稳定叶板的作用，避免左右摆动，使脚蹼具有较强的稳定性。</span><br />\r\n<img src=\"/attached/image/2015/12/18/a6462286-712a-426f-aead-26e8174d43f2.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/18/890f1e4a-d2e2-48e4-baaa-3684fbc6cd6b.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/18/59baf4e1-1258-4c1b-8aca-7ab9029a6dae.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;font-size:14px;color:#999999;\">鞋肚类型：套脚式鞋肚</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#999999;\">材质：热塑性橡胶 聚丙烯</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">叶板长度（size R）:45m</span><br />\r\n<img src=\"/attached/image/2015/12/18/40a07927-9188-44b9-9312-8acea7fdb689.jpg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#FF9900;\">价格：800元</span> \r\n</p>', 'uploadfiles/equip/equip_hlAY1450407802826.jpg', 'Mares AVANTI QUATTRO POWER', '4个导流口 极强推力\r\n套脚式  四线金刚', 'ST01', 'ET06', null, '800', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', 'CL009,CL006,CL001', 'SZ022,SZ023,SZ024,SZ025,SZ026,SZ027,SZ028,SZ029');
INSERT INTO `dive_equip` VALUES ('36b010ca-2822-4167-9718-456b1795e00c', '2015-12-18 03:44:50', 'EB01', '<p>\r\n	<span style=\"font-size:18px;color:#337FE5;font-family:\'Microsoft YaHei\';\"><strong>Mares PUCK PRO</strong></span><br />\r\n<span style=\"font-size:14px;color:#999999;\">Code:414122</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#999999;\">舒适轻便的设计</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">直观的用户界面</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">大屏显示 &nbsp;优越的可读性</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">可升级软件和多种气体设计</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">按键“一点红” &nbsp;操作方便</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">十种表身颜色选择</span><br />\r\n<span style=\"font-size:14px;color:#999999;\"></span> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/12/18/5109e48f-bea8-4008-9b37-09eb00792758.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;\">按键“一点红” &nbsp;操作方便</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\"></span><img src=\"/attached/image/2015/12/18/2e31a06b-f3a4-4830-b05f-33981dfc4881.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/18/9fd7ec08-f238-413a-9d6e-d53635a71649.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/18/068c04d2-6833-40a5-b9be-58597c3ab044.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/18/bcc7ddaa-f153-4b6c-bb65-176ac6c76d44.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;\"><br />\r\n<span style=\"font-size:14px;color:#999999;\">当2008年Mares在市场上介绍Puck潜水电脑表的时候，Mares在整个行业中引起了轰动，一个超级直观，易于使用，全功能的RGBM电脑表以相当具有竞争价格的姿态出现了。现在又再次做起，尽管在原始概念上加以改进并不简单，但是我们还是通过更小的机身达到更大的显示，然后增加可升级的固件和混合气体功能。Puck PRO 是一共可以陪伴你冒险多年的潜水电脑表。</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#999999;\">高氧范围：21%-99%</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">USB