INSERT INTO `dive_equip` VALUES ('37669c0e-470c-42fc-adc9-4a875f3c993e', '2015-12-19 13:20:01', 'EB06', '<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>GULL COCO 潜水硅胶面镜</strong></span><br />\r\n<img src=\"/attached/image/2015/12/19/ec4aa2ce-4a27-4b83-83c7-a20f5453e3d5.jpg\" alt=\"\" /><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">单眼面镜，却有广大的视野。</span><span style=\"line-height:1.5;font-family:\'Microsoft YaHei\';color:#999999;\">小容积，清洗容易。 特殊裙边设计.符合东方人脸型. 面镜扣与硅胶一体成型.角度更大. 护髮带特殊材质处裡.穿脱更容易.绝不打结。</span> \r\n</p>\r\n<img src=\"/attached/image/2015/12/19/0d1d99a3-49a1-4a8f-9736-e6dc731e7385.jpg\" alt=\"\" /><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> Coco是一众女潜水员的心水选择，每年均推出别致的新颜色。采用单视窗设计，特别适合面型较小的亚洲女生。於日本生产的面镜以优质矽胶制造。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#FF9900;\">价格：500元</span>\r\n</p>', 'uploadfiles/equip/equip_lWZM1450502400776.jpg', 'GULL COCO MASK', '符合东方人脸型\r\n独特色搭 女性首选', 'ST01', 'ET01', null, '500', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', 'CL008,CL010,CL006,CL002,CL028,CL029,CL021,CL013,CL001', 'SZ040');
