INSERT INTO `dive_equip` VALUES ('3982beb9-2761-492a-bf64-42bab6922f17', '2015-10-25 19:44:31', 'EB01', '<p>\r\n	<span style=\"font-size:16px;\"><span style=\"font-family:\'Microsoft YaHei\';font-size:18px;color:#337FE5;line-height:1.5;\"><strong>Mares Octopus FUSION</strong></span><span style=\"font-family:Microsoft YaHei;\"></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#999999;line-height:1.5;\"><span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"><span style=\"font-family:\'Microsoft YaHei\';font-size:14px;line-height:1.5;color:#999999;\">Code:416544</span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#999999;font-family:\'Microsoft YaHei\';font-size:14px;line-height:1.5;\"><br />\r\n</span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#999999;font-family:\'Microsoft YaHei\';font-size:14px;line-height:1.5;\">FUSION二级头</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#999999;line-height:1.5;\"> 全金属技术使二级头能适应各种极端寒冷的潜水环境</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#999999;line-height:1.5;\"> MR52X一级头</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#999999;line-height:1.5;\"> \"X\"系统的坚固性增加到6倍</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#999999;line-height:1.5;\"> 水平管路设计：主调节器和备用调节器并排水平安装</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#999999;line-height:1.5;\"> NCC自然对流系统：大大提高在冷水环境中的热交换</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#999999;line-height:1.5;\"> 双DFC动态流量控制系统：确保在任何深度都呼吸顺畅</span> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/10/25/01a4dd9d-005d-4736-83af-fde8b3d49e66.jpg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;\"></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#FF9900;line-height:1.5;\">价格：2800元</span> \r\n</p>', 'uploadfiles/equip/equip_ilvA1445773471395.jpg', 'Mares Octopus FUSION', 'NCC自然对流系统\r\n双DFC动态流量控制系统', 'ST01', 'ET07', null, '2800', null, '6a040c09-5758-41f8-8910-bf556692e824', '', '');
INSERT INTO `dive_equip` VALUES ('39e85ab8-e27a-4db1-a6a5-b6bc6d16e7a5', '2015-12-18 10:38:05', 'EB01', '<p>\r\n	<span style=\"font-size:18px;color:#337FE5;line-height:1.5;font-family:\'Microsoft YaHei\';\"><strong>Mares HYBRID&nbsp;</strong></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;color:#337FE5;line-height:1.5;font-family:\'Microsoft YaHei\';\"><strong>PRO TEC MRS+</strong></span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">Code:417348</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">背浮式BCD的革命性跨越</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">高强度Alutex材料 结实耐用</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">模块式配重整合系统 可拆卸技术</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">10个金属D型环 其中3个可以随意调节</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">无缝的肩带 气囊与甲胄完全分离</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">Susepesion system的腰部设计</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">背板两边设有配重袋</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">低平的排气阀 比原Mares的低了30%</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\"></span><img src=\"/attached/image/2015/12/18/c5de70e8-0ee4-4de4-bf42-ec69f3700947.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/18/46e97194-87be-4378-ba06-2b865a352c4b.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/18/d623f507-d99d-4123-8140-011136ca7db2.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#999999;line-height:1.5;\">Hybrid代表了Mares BCD的最新研发成果：重量轻、柔软且耐用的材料、气囊和甲胄完全分离、无缝的肩带、背架双侧的衬垫，这些都将带给您无可比拟的舒适度。而Pro Tec款采用模块式设计，可以使用或拆卸整合式配重系统，配10个金属D型环，其中3个可以根据需要在肩带上上下移动。气囊采用坚固的Alutex材料，轻便又结实耐用。</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">款式：可折叠 后背囊式</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">材料：外部Cordure 100</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;内部Cordure 420 Alute</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">重量(size L):4.4kg</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">D-型环：10个不锈钢质(其中3个可移动)</span> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/12/18/20d1f74c-5975-4ff0-aa2d-f0ed975bc148.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#FF9900;line-height:1.5;\">价格：4200元</span> \r\n</p>', 'uploadfiles/equip/equip_RcIw1450406284644.jpg', 'Mares HYBRID PRO TEC', '高强度Alutex材料 结实耐用\r\n模块式配重整合系统 可拆卸技术', 'ST01', 'ET08', null, '4200', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', 'SZ010,SZ011,SZ012,SZ013,SZ014,SZ015,SZ016,SZ017,SZ018,SZ019,SZ020,SZ021');
INSERT INTO `dive_equip` VALUES ('3a642877-47b6-4153-ac56-22188c6f9ab1', '2015-12-18 04:06:21', 'EB01', '<p>\r\n	<span style=\"font-size:18px;color:#337FE5;line-height:1.5;font-family:\'Microsoft YaHei\';\"><strong>Mares ABYSS 52</strong></span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">Code:416163</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">一级和二级调节器的最佳组合</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">双DFC（动态流量控制系统）</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">NCC自然对流设计</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">金属二级调节器及旁通管</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\"></span><br />\r\n<img src=\"/attached/image/2015/12/18/aee461d8-3b57-4f60-bb9f-d50cc5a56c89.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<br />\r\n<span style=\"font-family:Microsoft YaHei;\"><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">全新的MR52一级调节器，MR52是为了现在技术潜水的需要，保持了MR22一级调节器的通气量，但尽可能的缩小了体积，将所有的中压接口都并排设计适合于技术潜水中的双瓶需要，在配置上，具备了双DFC系统，使得我们不仅在享用主调节器带来的顺畅呼吸外，备用也依旧可以享用；NCC自然对流系统大大提高了在冷水环境下的热交换能力。 在二级调节器的配备上，选择了Mares的功勋调节器Abyss，金属的外壳设计，在满足强度需要的同时了，发挥了金属的优良热传导性能，全金属的旁通管设计使得气体直接流入口中，避免了调节器避免结冰的发生。</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">一级调节器类型：平衡隔膜式</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">二级调节器类型：顺流式供气阀</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">一级调节器重量：687g</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">二级调节器重量：269g</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">总重量（含管子g）： 1106g</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">HP端口： 2 个7/16\"</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">LP端口： 4 个3/8\" &nbsp;两个DFC接口</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">中压管: 80cm超柔软编织护套中压软管</span></span><span style=\"font-family:Microsoft YaHei;\"></span><br />\r\n<span style=\"font-family:Microsoft YaHei;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:Microsoft YaHei;\"><span><span style=\"font-size:14px;color:#999999;line-height:1.5;\"></span><br />\r\n</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:Microsoft YaHei;\"><span style=\"font-size:14px;color:#FF9900;line-height:1.5;\">价格：3600元</span><br />\r\n</span> \r\n</p>', 'uploadfiles/equip/equip_XoSe1450382841144.jpg', 'Mares ABYSS 52', '一级和二级调节器的最佳组合', 'ST01', 'ET07', null, '3600', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', '');
INSERT INTO `dive_equip` VALUES ('3c2bc16b-4140-4374-b815-78e1170db568', '2015-12-19 03:16:35', 'EB04', '<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>ATOMIC AQUATICS SV1呼吸管 &nbsp;</strong></span> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/12/19/3c693e1d-9791-40b4-89f6-1d3172e575fa.jpg\" alt=\"\" /> \r\n</p>\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">Atomic Aquatics的SV系列呼吸管，卖点就是SV这两个字，它是Scupper Valve，设计原理来自船用的单向排水阀。在呼吸管里海水只能单向流动，只会排出，不会在同一位置被吸入。</span><br />\r\n<img src=\"/attached/image/2015/12/19/abf876cd-5468-4e40-861d-77c1b0e8f36f.jpg\" alt=\"\" /><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">所有的Atomic Aquatics的SV系列呼吸管，在真正投产前，都会采集潜水员的反馈意见，根据大家的要求，把呼吸管做得尽量体积小，重量轻而且富有弹性。所以SV系列的呼吸管，都是体积小而且非常符合流体力学的，在水中的阻力是尽可能的小。</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;font-family:\'Microsoft YaHei\';color:#999999;\">SV系列呼吸管采用旗下的二级头的咬嘴，非常舒服。</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;font-family:\'Microsoft YaHei\';color:#FF9900;\">价格：480元</span>\r\n</p>', 'uploadfiles/equip/equip_Ah4h1450466238917.jpg', 'ATOMIC AQUATICS SV1', '全称Scupper Valve\r\n经典单向阀呼吸管', 'ST01', 'ET02', null, '480', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', 'CL013,CL015,CL019,CL014,CL017', '');
