INSERT INTO `dive_equip` VALUES ('40f19bbf-46e4-4a7a-8f44-bc160a608e2f', '2015-12-20 12:44:32', 'EB10', '<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>Water Pro</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>UPF50 快干防晒衣</strong></span> \r\n</p>\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 产造纤维弹性布并加入钛金属材质制成, 提供绝佳抗UV完善保护性及防水性</span><br />\r\n<img src=\"/attached/image/2015/12/20/e5dd50cc-e509-4a3d-bebb-876b17286d8a.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/20/6174d5e4-aa8a-40e9-a9e4-45577977082f.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 抗UV的绝佳好帮手,可保护水上运动者减少曝晒于紫外线之伤害。使用10oz 的Nylon及人</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> FLEX 等级, 提供 更佳延展度, 更高效能防晒.</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 专业防晒等级高达 50 UPF UV rate Lycra material.</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 完美的照顾您的健康, 减少晒伤机会与皮肤癌产生.</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 您只需要擦防晒油在未保护到的部份即可.</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 莱卡材质的舒适度, 给您更贴合, 更容易穿脱, 更无束缚延伸P.</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 快干的效果. 不会让您在岸上因失温而受凉, 衣服快干, 不容易黏在身上.</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 让你更愉快享受每一次潜水或是水上活动.</span><br />\r\n<div>\r\n	<span style=\"color:#FF9900;\">价格：320元</span> \r\n</div>', 'uploadfiles/equip/equip_DGKW1450586672023.jpg', 'Water Pro UPF50+ 快干防晒衣', '纤维弹性布加入钛金属\r\n绝佳抗UV完善保护性及防水性', 'ST01', 'ET03', null, '320', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', 'CL017', 'SZ002,SZ003,SZ004,SZ005,SZ006,SZ007');
