INSERT INTO `dive_equip` VALUES ('4439b961-45d7-4207-ab68-a411b9bf6832', '2016-01-25 14:06:07', 'EB03', '<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>Aqua Lung</strong></span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>Calypso Titan Octopus</strong></span><br />\r\n<img src=\"/attached/image/2016/01/25/2a06f010-ffd9-4ae2-8de2-77480928f631.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> Aqua Lung 提供了几种不同的备用调节器以满足潜水员的不同需要。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 中等尺寸的高性能备用调节器可与 Titan 和 Calypso 配合使用</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 高可视度 1 米黄色喉管</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 方便调节的防漏气阀门有效阻止非使用期间的漏气问题</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 可方便的将喉管安装在左边或右边，而无需任何额外配件</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 全新的排水按钮设计</span>\r\n</p>\r\n<p>\r\n	<span><span style=\"color:#FF9900;\">价格：1100元</span></span>\r\n</p>', 'uploadfiles/equip/equip_GHWC1453701966851.jpg', 'Aqua Lung Calypso Titan Octopus', '可轻松将喉管安装在任意一边\r\n可与Titan和Calypso配合使用', 'ST01', 'ET07', null, '1100', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', '');
