INSERT INTO `dive_equip` VALUES ('48b1f26a-c341-4392-bf69-378d6e1591f9', '2015-12-18 12:10:12', 'EB01', '<p>\r\n	<span style=\"font-size:18px;color:#337FE5;font-family:\'Microsoft YaHei\';\"><strong>Mares X-STREAM</strong></span><br />\r\n<span style=\"font-size:14px;color:#999999;\">Code: 410019</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">德国红点设计大奖</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">筛状的鞋套消除了降落伞阻力</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">材料及导流口 使其推进效率更高</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">蛙鞋叶面设计可减小涡流产生</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">新一代的OPB技术 叶板角度最优化</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#999999;\">德国红点设计大奖，独特的设计，新颖舒适。筛状的鞋套消除了降落伞阻力效应；材料及导流口，使其推进效率更高；蛙鞋页面设计，减小涡流产生；新一代的OPB技术，叶板角度最优化；X-Stream终于成为结合了四项Mares的专利技术的潮品。</span><br />\r\n<br />\r\n<img src=\"/attached/image/2015/12/18/aaafa410-4844-4fd8-b61a-fe5d8ea1731e.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/18/6164b0d5-abaa-4901-ba80-b939b1166c68.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/18/f038a16e-8b71-40ae-9435-fcd389bbcbbd.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/18/71f8ea84-43fc-48fe-966c-6d72b7563ad7.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/18/92dbbdf9-3ebc-4b26-b854-94ab21c5f7bd.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#999999;\">鞋肚类型：无跟式开式鞋肚</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">材质：热塑性橡胶 聚丙烯</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">叶板长度（size R）:37cm</span><br />\r\n<br />\r\n<img src=\"/attached/image/2015/12/18/050cb7c4-c4dc-42dd-98ad-6ec0e5698836.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#FF9900;\">价格：1200元</span> \r\n</p>', 'uploadfiles/equip/equip_FFCf1450411811536.jpg', 'Mares X-STREAM', '筛状的鞋套消除了降落伞阻力\r\n蛙鞋叶面设计可减小涡流产生', 'ST01', 'ET06', null, '1200', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', 'CL001,CL004,CL006,CL01