INSERT INTO `dive_equip` VALUES ('4a319b4e-d6c2-4792-8845-d04d4b52f9e5', '2015-12-20 01:07:50', 'EB07', '<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>ScubaPro</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>Seawing Nova</strong></span> \r\n</p>\r\n<img src=\"/attached/image/2015/12/20/44be93fe-0852-4294-b99b-21198e0e3d1a.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/20/46b43a97-477d-431d-997c-87571df67d93.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 优点：此款脚蹼采用最新设计，使脚蹼极易弯曲产生更多的向后推力，而不是上下浮力；在逆流状态下产生的推进力远胜其他脚蹼。特种合成橡胶使脚蹼非常耐用，而巧妙的设计减轻了脚蹼的重量，让你的脚踝避免容易抽筋的问题。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> S号---适合39码以下潜水鞋</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> M号---适合39--43码潜水鞋</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> L号---适合43码以上潜水鞋</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#FF9900;\">价格：2200元</span>\r\n</p>', 'uploadfiles/equip/equip_z5Ww1450544870252.jpg', 'ScubaPro Seawing Nova', '逆流状态下产生的推进力\r\n远胜其他脚蹼', 'ST01', 'ET06', null, '2200', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', 'CL001,CL002', 'SZ003,SZ004,SZ005');
INSERT INTO `dive_equip` VALUES ('4a43dfb7-ca4c-4f0a-88dd-ee5af26ec4ef', '2016-01-25 15:04:25', 'EB03', '<span style=\"font-family:\'Microsoft YaHei\';font-size:24px;line-height:1.5;color:#337FE5;\"><strong>Aqua Lung&nbsp;</strong></span><span style=\"font-family:\'Microsoft YaHei\';font-size:24px;line-height:1.5;color:#337FE5;\"><strong>Infinity</strong></span><br />\r\n<br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;line-height:1.5;color:#999999;\">贴近眼睛的镜框设计使得面镜内容积减小并且提供广阔的上下视野</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;line-height:1.5;color:#999999;\">侧窗使用高质量的光学聚合物制成，提供更亮的视觉以及提高横向视野</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;line-height:1.5;color:#999999;\">万向带扣使面镜带更加易于调节</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;line-height:1.5;color:#999999;\">调节按钮采用上下按压按钮，即使穿戴手套时也能轻松调节</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;line-height:1.5;color:#999999;\">宽大的硅胶面镜使佩戴更舒适、稳定</span><br />\r\n<img src=\"/attached/image/2016/01/25/05e7b08f-267a-4242-99e5-38a02bdfb4c4.jpg\" alt=\"\" /><img src=\"/attached/image/2016/01/25/a19fabeb-a720-4954-b09f-ea3fcf4acf54.jpg\" alt=\"\" /><br />\r\n<span><span style=\"font-size:14px;line-height:21px;color:#FF9900;\">价格：1800元</span></span>', 'uploadfiles/equip/equip_GMng1453705465273.jpg', 'Aqua Lung Infinity', '较好视野\r\n配万象带扣', 'ST01', 'ET01', null, '1800', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', 'CL001,CL003,CL004,CL009', '');
INSERT INTO `dive_equip` VALUES ('4acc4e83-270b-4ff8-8727-b85023c5f93c', '2015-12-19 13:09:26', 'EB06', '<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>Barracuda 加长套脚式蛙鞋</strong></span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 世界上最长的橡胶蛙鞋</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> ．专为徒手潜水员及水肺潜员而设的蛙鞋</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> ．特长的蹼面，用力少，推进力特强，让你轻松在水下前进</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> ．柔软舒适的脚袋能完全包裹你的脚掌，令你踢水时更顺畅</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> &nbsp;</span><img src=\"/attached/image/2015/12/19/631d4d11-f436-4535-af7b-f52c2ff8103e.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/19/45884e9a-a8f6-40a6-9d4f-01e703b99990.jpg\" alt=\"\" /><br />\r\n<br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> ．尺碼:</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> ．M: Mew 潛水靴: 24cm; 裸足: 25-26cm</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> ．L: Mew 潛水靴: 26cm; 裸足: 27-28cm</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#FF9900;\">价格：2400元</span>\r\n</p>', 'uploadfiles/equip/equip_wd6e1450501765640.jpg', 'GULL Barracuda 加长套脚式蛙鞋', '世界上最长的橡胶蛙鞋', 'ST01', 'ET06', null, '2400', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', 'CL001,CL002', 'SZ004,SZ005');
INSERT INTO `dive_equip` VALUES ('4ad00cfe-f85d-422e-8a33-d7cf0ada085c', '2015-12-19 15:14:29', 'EB06', '<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\">GULL Super Bullet Snorkel 呼吸管</span><br />\r\n<img src=\"/attached/image/2015/12/19/b604e2a3-0c61-4210-9b02-6488233d57c7.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/19/9cf2f70e-cbce-4ef9-b223-666ee0ae58c4.png\" alt=\"\" /><img src=\"/attached/image/2015/12/19/a4c8cc52-239b-430e-b73e-b88c0d966b14.png\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> ．独特流线型圆滑柔软管子，水阻减至最少，让你无论浮潜或徙手潜水亦呼吸畅顺；</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> ．特配备两个排水阀口，排水更快又易，同时设有止水阀片的设计，即使进水也轻易于两个排水阀排出而不会进水；</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> ．特大直径21mm的呼吸管子让你轻松于水面呼吸；</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> ．附有安全快速卸除管夹，让你轻易装卸呼吸管；</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> ．配备柔软及可调较之硅胶咬嘴，适合长时间使用</span><br />\r\n	<p>\r\n		<img src=\"/attached/image/2015/12/19/862a7a38-c813-48a2-9a32-bff9e3fbf84d.jpg\" alt=\"\" />\r\n	</p>\r\n	<p>\r\n		<span style=\"color:#FF9900;\">价格：360元</span>\r\n	</p>\r\n</p>', 'uploadfiles/equip/equip_rnYj1450509269258.jpg', 'GULL Super Bullet Snorkel 呼吸管', '', 'ST01', 'ET02', null, '360', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', 'CL004,CL007,CL006,CL008,CL009,CL010,CL001,CL002', 'SZ040');
INSERT INTO `dive_equip` VALUES ('4b016f76-aec4-4ee8-bb65-340179164eb8', '2015-12-20 00:58:11', 'EB07', '<p>\r\n	<strong><span style=\"font-family:\'Microsoft YaHei\';font-size:18px;color:#337FE5;\">Scubapro Profile 3mm</span></strong> \r\n</p>\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> Profile系列潜水衣混合了多元化的功能和特别的裁剪，使之保暖性能好，经久耐用和舒贴身来突显其与众不同。</span><br />\r\n<img src=\"/attached/image/2015/12/20/a7e53537-322f-4fcc-95b8-7fa729ca8d70.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 温暖水域或温带水域最适合的潜水服</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 绒毛针织面料衬里（只在5mm厚度型号），保暖性能更好，更舒适，如果天气稍凉，可以配上连帽的背心</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 溶胶 Tatex 护膝，无需胶水或缝线，将工业级橡胶护膝与潜水服结合，保护力强，防止脱落，更加耐用</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 背后拉链穿着容易，并配有模压拉链和脊柱保护垫</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 手腕及脚踝拉链</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">胶水粘贴缝骨再加双盲缝线使线骨节面不渗水</span> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/12/20/380e2370-72e0-419a-97f0-ae5ca266fb30.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/20/a358fe96-4c17-47ec-992c-638fb92fb6ad.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"color:#FF9900;\">价格：1500元</span>\r\n</p>', 'uploadfiles/equip/equip_aZhZ1450544291448.jpg', 'Scubapro Profile 3mm ', '绒毛针织面料衬里\r\n只在5mm厚度型号', 'ST01', 'ET03', null, '1500', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', 'SZ002,SZ003,SZ004,SZ005,SZ006,SZ007');
INSERT INTO `dive_equip` VALUES ('4cc89d9b-0d82-418c-a68a-c584a5bbedf9', '2015-12-18 12:00:47', 'EB01', '<p>\r\n	<span style=\"font-size:18px;color:#337FE5;font-family:\'Microsoft YaHei\';\"><strong>Mares RAZOR PRO</strong></span><br />\r\n<span style=\"font-size:14px;color:#999999;\">Code:420401</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#999999;\">市场上表现卓越的技潜脚蹼</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">弹性最好的塑料长蛙鞋之一</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">可拆卸叶板 方便使用</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">鞋套的设计提供了完美的动力传到</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">锥形高质量弹性叶板可提高敏捷性</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">颜色：黑色</span><br />\r\n<span style=\"font-size:14px;color:#999999;\"></span> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/12/18/1d2ebf74-1412-49d6-a9c1-ae022ef73c7b.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/05/04/cff9bc8c-ff8e-4628-9b46-42daf96211b9.jpg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"color:#FF9900;font-size:14px;line-height:1.5;\">价格：1100元</span> \r\n</p>', 'uploadfiles/equip/equip_z35k1450411247286.jpg', 'Mares RAZOR PRO', '弹性最好的塑料长蛙鞋之一', 'ST01', 'ET06', null, '1100', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', 'SZ022,SZ023,SZ024,SZ025,SZ026,SZ027,SZ028,SZ029');
