INSERT INTO `dive_equip` VALUES ('4d55ace3-ea7d-4f98-a2fa-b98ff306dc54', '2015-12-17 15:14:23', 'EB02', '<p>\r\n	<span style=\"font-family:Microsoft YaHei;\"><span style=\"color:#337FE5;font-family:\'Microsoft YaHei\';font-size:18px;\"><strong>Poseidon One Shoe</strong></span><span style=\"color:#337FE5;font-family:\'Microsoft YaHei\';font-size:18px;\"><strong></strong></span><br />\r\n</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">适合在陆地及水底穿着</span> \r\n</p>\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">配合Poseidon胶衣而设计</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">鞋面材质为潜水布</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">底部材质为橡胶</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">拉链为YKK名牌防水拉链。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">颜色：黑色 黑白色</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">尺码：36-47</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:Microsoft YaHei;\"><img src=\"/attached/image/2015/12/17/b76c0f0d-7d62-4ae9-9d5f-9dd30cc4fe02.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/17/d1df02a9-6d40-4cc2-814c-9cae3d92941e.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">价格：780元</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:Microsoft YaHei;\"><br />\r\n<span style=\"color:#999999;font-family:\'Microsoft YaHei\';\"> Poseidon(海神，又名波塞冬) 是一家来自瑞典，具有58年历史的潜水器材生产商,其产品素以品质闻名于世，</span><span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">深受专业潜水人士的喜爱</span><span style=\"color:#999999;font-family:\'Microsoft YaHei\';\">。各国的军事单位无不采用她的产品，甚至美国太空总署也是合作伙伴。</span><span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">是高品质，高可靠性的代名词。</span><br />\r\n</span> \r\n</p>', 'uploadfiles/equip/equip_4utk1450336462617.jpg', 'Poseidon One Shoe', '适合在陆地及水底穿着\r\n潜水布鞋面 橡胶鞋底 防水拉链', 'ST01', 'ET04', null, '780', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', null, null);
INSERT INTO `dive_equip` VALUES ('4dd1dafa-afdb-40a0-9983-a2c1ea83b2fa', '2015-12-20 01:25:14', 'EB07', '<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>SCUBAPRO</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>Thermal TEC 3mm</strong></span>\r\n</p>\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 黑色专业设计，配上各种特别构造的物料增加潜水服功能。</span><br />\r\n<img src=\"/attached/image/2015/12/20/a09f0aee-a763-4271-9f2d-4195285eafc3.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 双面尼龙面料，更加耐用</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 身体外部使用无孔FAIRSKIN材料减缓热量流失同时加快风干速度</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 衬里使用HELIOSPAN绒毛针织材料，穿着舒适，戏水率强，良好的保温性能</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 身体二侧采用特制EVERFLEX弹性材料，增加拉深度，容易穿着，贴身舒适</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> TATEX护膝是利用特殊配方的热溶胶，将护膝直接和潜水服结合在一起，无需缝纫或胶水，保护力强，不会脱落</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 背后式拉链设计，设有模压拉链盖及脊柱保护垫</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 尼龙拉链拉环穿着容易，无需他人协助，并且可以避免拉链接合部过度拉扯</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 金属拉链扣，更加坚固耐用</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 手腕及脚踝拉链&nbsp;</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#FF9900;\">价格：1500元</span> \r\n</p>', 'uploadfiles/equip/equip_mz3a1450545913598.jpg', 'SCUBAPRO Thermal TEC 3mm', '双面尼龙  更加耐用\r\n无孔FAIRSKIN材料保暖 快干', 'ST01', 'ET03', null, '1500', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', 'CL001', 'SZ001,SZ002,SZ003,SZ004,SZ005,SZ006,SZ007');
INSERT INTO `dive_equip` VALUES ('4de1fb67-44a4-415c-a87b-d65addabca41', '2016-01-25 21:51:09', 'EB07', '<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;line-height:1;font-size:18px;\"><strong>Scubapro&nbsp;</strong></span><span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;line-height:1;font-size:18px;\"><strong>Marker Buoy</strong></span><br />\r\n<span style=\"line-height:1;\"></span> \r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1;\"><span style=\"font-family:Microsoft YaHei;\">经济 PVC 制造的标示浮标</span><span style=\"font-family:\'Microsoft YaHei\';\"></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1;\">可用二级头调节器进行充气</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1;\">9 米长的绳子协助潜水员</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1;\">进行减压和安全停留</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#999999;font-family:\'Microsoft YaHei\';font-size:14px;line-height:1;\">方便连在浮力背心上</span> \r\n</p>\r\n<br />\r\n<img src=\"/attached/image/2016/01/25/5e3b7da9-ebfc-4adf-99ef-5e32a068e0b9.jpg\" alt=\"\" /><br />\r\n<p>\r\n	<br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1;\">只需简单地通过备用调节器将空气从底部充入浮标，1.3 米长的浮标就会浮至水面，轻松帮助定位。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#FF9900;line-height:1;\">价格：500元</span>\r\n</p>', 'uploadfiles/equip/equip_sidQ1453729869471.jpg', 'Scubapro Marker Buoy', '绳长9米\r\n二级头充气', 'ST01', 'ET15', null, '500', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', '');
INSERT INTO `dive_equip` VALUES ('4f119f24-f24a-47e0-b0d8-18f599c1c4dd', '2015-12-19 14:12:38', 'EB06', '<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>GULL NAIDA MASK</strong></span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">重量：195g</span> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/12/19/672c993c-7d8c-4bfd-bf5a-7dfa9b182078.png\" alt=\"\" /><img src=\"/attached/image/2015/12/19/4ed25f40-0459-4215-8112-cd9dd33f3b02.jpg\" alt=\"\" /> \r\n</p>\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">特殊裙边设计.符合东方人脸型.</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 面镜扣与硅胶一体成型.角度更大.</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 护发带特殊材质处里.穿脱更容易.绝不打结.</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 鼻部雾化处理</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">GULL的用心创造出的高质量.您用了才知道.</span> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/12/19/3110821f-2eb8-414a-baee-092a1ebc4059.png\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"color:#FF9900;\">价格：620元</span>\r\n</p>', 'uploadfiles/equip/equip_rnUs1450505557957.png', 'GULL NAIDA MASK', '符合东方人脸型\r\n鼻部雾化处理', 'ST01', 'ET01', null, '620', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', 'CL010,CL005,CL006,CL009,CL001,CL002', 'SZ040');
INSERT INTO `dive_equip` VALUES ('50ba39ff-eecd-443a-b879-f7d834c1bdda', '2015-12-19 00:05:53', 'EB01', '<p>\r\n	<span style=\"color:#337FE5;font-family:\'Microsoft YaHei\';font-size:18px;\"><strong>Mares Buoy</strong></span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#337FE5;font-family:\'Microsoft YaHei\';font-size:18px;\"><strong>STANDARD INFLATABLE</strong></span><br />\r\n<span style=\"font-size:14px;color:#999999;\">Code: 415707</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">鲜艳的橙色，警示更明显</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">耐用的PVC材质</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">20米的绳索和连接挂钩，可以悬挂任何装备</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">鲜艳的橙色，可以从很远的地方就可以被发现。使用耐用的PVC材料制造，并按照国家和国际惯例印上英文字样\"DIVER BELOW\".两舱的设计更安全</span><br />\r\n<span style=\"font-size:14px;color:#999999;\"></span> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/12/19/d4523864-3d3e-4b17-b2b2-bac4b72d819a.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;font-size:14px;color:#FF9900;\">价格：200元</span> \r\n</p>', 'uploadfiles/equip/equip_knzE1450454752993.jpg', 'Mares Buoy STANDARD INFLATABLE', '潜水浮标\r\n配有绳索和挂钩', 'ST01', 'ET15', null, '200', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', '');
INSERT INTO `dive_equip` VALUES ('522a2b04-a1df-464f-ac22-fedecc1d0a5e', '2015-12-20 01:06:09', 'EB07', '<p>\r\n	<strong><span style=\"font-family:\'Microsoft YaHei\';font-size:18px;color:#337FE5;\">Scubapro SEAMLESS 1.5mm</span></strong> \r\n</p>\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 采用加热融合胶粘合取代缝线缝合，带来绝对轻量化和舒适的柔软手套。氯丁橡胶，手掌带磨纹。</span><br />\r\n<img src=\"/attached/image/2015/12/20/8f351f9b-0480-4214-a75a-9c153d26a6a5.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 参考尺码：</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> S-中指长7-7.5cm，</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> M-中指长7.5-8cm，</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> L-中指长8-8.5cm，</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> XL-中指长8.5-9cm</span><br />\r\n<span style=\"color:#FF9900;\">价格：280元</span>', 'uploadfiles/equip/equip_cFem1450544769069.jpg', 'Scubapro SEAMLESS 1.5mm ', '绝对轻量化和舒适的柔软手套', 'ST01', 'ET05', null, '280', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', 'SZ003,SZ004,SZ005,SZ006');
