INSERT INTO `dive_equip` VALUES ('5435f31c-bfc0-48b8-9cea-3222becfe8b5', '2015-12-19 23:09:39', 'EB07', '<p>\r\n	<strong><span style=\"font-family:\'Microsoft YaHei\';font-size:18px;color:#337FE5;\">Scubapro EQUATOR BCD</span></strong> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\"><img src=\"/attached/image/2015/12/19/dcafbf8f-fbec-4630-b6cc-ab274763187d.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;font-family:\'Microsoft YaHei\';color:#999999;\">1、适合轻便旅行的易于折叠小背板。</span> \r\n</p>\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">2、超舒适的Airnet网状快干式背垫。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 3、Scubapro BPI充气阀，波纹软管，弯头以及轻重量排气阀均采用最新的科学技术，以确保绝对的安全性与最佳的舒适度。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 4、新型的剪道通道。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 5、采用最新设计的两根气瓶绑带。轻质的塑料搭扣和易于插入的网状织带。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 6、尼龙420D面料，厚实耐磨耐用。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 7、配有轻质搭扣的新型一体化配重袋。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 8、优质材质的防紫外线标识位于两侧以及双肩，两侧备有备用二级头口袋。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">9、6个不锈钢D型环，刀具保护环以及2个拉链口袋。</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">10、颈部以柔软材质镶边，穿着更舒适。</span>\r\n</p>\r\n<p>\r\n	<span><span style=\"color:#FF9900;\">价格：4000元</span></span>\r\n</p>', 'uploadfiles/equip/equip_BXvb1450595876639.jpg', 'Scubapro EQUATOR BCD', '适合轻便旅行的易于折叠小背板', 'ST01', 'ET08', null, '4000', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', '');
