INSERT INTO `dive_equip` VALUES ('55f40cc0-8fa5-4d05-9f2d-ce0ba736320d', '2015-12-20 00:44:09', 'EB07', '<p>\r\n	<strong><span style=\"font-family:\'Microsoft YaHei\';font-size:18px;color:#337FE5;\">Scubapro Phoenix2 全干式</span></strong> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/12/20/0c7d0c9c-83e4-478a-bf56-d7d33a741364.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/20/9d99972d-c736-4cc0-a325-9c3b66d3ae39.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">干式呼吸管设计</span> \r\n</p>\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">专利的阀门设计</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 舒适、高规格的硅胶咬嘴</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 活动硅胶延长管</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 呼吸管通用活动挂钩</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 较低的按压排水阀</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 颜色分类: 蓝色 黄色 黑色</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#FF9900;\">价格：190元</span>\r\n</p>', 'uploadfiles/equip/equip_wHnT1450543449262.jpg', 'Scubapro Phoenix2 全干', '全干式呼吸管', 'ST01', 'ET02', null, '190', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', 'CL001,CL006,CL009', '');
INSERT INTO `dive_equip` VALUES ('56175540-74d0-4ea2-9629-82a49865f482', '2015-12-18 10:40:25', 'EB01', '<p>\r\n	<span style=\"font-size:18px;color:#337FE5;line-height:1.5;font-family:\'Microsoft YaHei\';\"><strong>Mares PRESTIGE MRS+</strong></span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">Code:417222</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">最新设计 崭新外形 性价比高</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">气囊超大容量 超厚面料</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">最新设计的ERGO控制阀</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">背部人性化设计 更大活动空间</span><br />\r\n<br />\r\n<img src=\"/attached/image/2015/12/18/4e0df4da-70d3-4c0c-b5ff-b74243b503b8.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#999999;line-height:1.5;\">在背囊外部材料上选用了耐磨耐用的尼龙材料，提高安全和舒适的同时，也提供了难以置信的浮力。肩部人性化的设计，更大的活动空间使得舒适是毋庸置疑的。全新的装饰和吸引人的风格使其在外观上有了新鲜的感觉.在排气阀的设计上也采用了全新的充排气阀以及自动泄压阀，泄压阀比原来的低了30%，这样可以减少因礁石等碰触。</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">款式：前调整式浮力背心</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">材料：Cordura1000 / Cordura420</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">重量(size L):4.2kg</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">D-型环：4个不锈钢（2个高强度聚合物材质）</span><br />\r\n<span style=\"color:#999999;font-size:14px;line-height:1.5;\">浮力控制在140N(XS-S); 170N (M); 190N(L); 220N(XL);</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#999999;line-height:1.5;\">颜色：黑色</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#999999;font-size:14px;line-height:21px;\">尺寸：XXS &nbsp;XS &nbsp;S &nbsp;M &nbsp;L &nbsp;XL （不分男女）</span>\r\n</p>\r\n<p>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#FF9900;line-height:1.5;\">价格：4000元</span> \r\n</p>', 'uploadfiles/equip/equip_rajT1450406425148.jpg', 'Mares PRESTIGE MRS', '崭新外形 性价比高\r\n设ERGO控制阀', 'ST01', 'ET08', null, '4000', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', 'SZ001,SZ002,SZ003,SZ004,SZ005,SZ006');
INSERT INTO `dive_equip` VALUES ('569ae9a6-467c-4434-a8ab-94dd0026d613', '2015-12-20 14:25:36', 'EB02', '<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>Poseidon</strong></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>BLACK LINE MISSION SUIT 3MM</strong></span>\r\n</p>\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 军用级性能</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 颜色：黑色</span><br />\r\n<img src=\"/attached/image/2015/12/20/2572cedf-0341-454a-afc7-dfc2c451c053.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/20/0e589d5e-69b6-4c7d-b9b4-84959d5fc0c2.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 弹性氯丁胶 舒适 超弹</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 袖口、裤脚有拉链</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 手肘、膝盖和臀部采用了防磨布和压纹材料，不易变形，活动方便。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 腰部采用拉伸材料，弹力和保暖性好</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 内衬里有金属纤维，传热保暖</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">正面拉链，方便特殊潜水需要，容易穿脱。</span>\r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/12/20/621f378b-ce59-4b0a-b96f-824e7516938c.jpg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;font-family:\'Microsoft YaHei\';color:#999999;\">Poseidon(海神，又名波塞冬) 是一家来自瑞典，具有58年历史的潜水器材生产商，其产品素以品质闻名于世，深受专业潜水人士的喜爱。各国的军事单位无不采用她的产品，甚至美国太空总署也是合作伙伴。是高品质、高可靠性的代名词。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\"><br />\r\n</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">\r\n	<p>\r\n		<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">价格：1800元</span><span style=\"line-height:1.5;font-family:\'Microsoft YaHei\';color:#999999;\"></span>\r\n	</p>\r\n</span>\r\n</p>', 'uploadfiles/equip/equip_J36l1450592735856.jpg', 'Poseidon BLACK LINE MISSION SUIT 3MM', '军用级品质\r\n超弹氯丁胶', 'ST01', 'ET03', null, '1800', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', null, null);
INSERT INTO `dive_equip` VALUES ('57524f63-7973-4afc-ad75-2b7175656d27', '2015-12-18 15:01:43', 'EB01', '<p>\r\n	<span style=\"font-size:18px;color:#337FE5;line-height:1.5;font-family:\'Microsoft YaHei\';\"><strong>Mares X-VU&nbsp;</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;color:#337FE5;line-height:1.5;font-family:\'Microsoft YaHei\';\"><strong>LiquidSkin Sunrise</strong></span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">Code：411041</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">专为亚洲人设计的时尚面镜</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">独特的Sunrise设计</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">时尚、舒适、美丽</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">双硅胶材质</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">可配光学镜片</span> \r\n</p>\r\n<br />\r\n<p>\r\n	<img src=\"/attached/image/2016/06/14/86603868-f28b-4dd5-ba68-84fa1e0dda99.jpg\" alt=\"\" /><img src=\"/attached/image/2016/06/14/8447370b-25e9-45b3-a912-f564f9068dd0.jpg\" alt=\"\" /><img src=\"/attached/image/2016/06/14/f76c58ea-6902-4c10-bf60-dd4d853383c2.jpg\" alt=\"\" /><img src=\"/attached/image/2016/06/14/f939a0cf-9e0a-406b-8be0-0e15d9e43376.jpg\" alt=\"\" /><img src=\"/attached/image/2016/06/14/664d6ff7-6412-4b85-b8fb-a299f9a32b9f.jpg\" alt=\"\" /><img src=\"/attached/image/2016/06/14/f5878094-5a7b-4716-acf6-39124e00371c.jpg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"color:#999999;font-size:14px;line-height:1.5;\">X-VU liquidskin系列是为亚洲特别设计，适合亚洲人的面型，大大降低面镜对面部轮廓的改变，照片更美丽。在液态双硅胶裙边之外，更柔软的硅胶注入到鼻部，起到类似防撞的作用，双按钮面镜扣也是全新的技术，调整方便容易。在裙边的前部平排了一系列的“缓冲器”的设计，减小面镜在面部的移动，也降低看在额头和面部的挤压感觉，长时间潜水更受其惠。 与X-VU不同的地方是Sunrise这种设计，更加符合亚洲人人中偏长的设计。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#999999;line-height:1.5;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#999999;line-height:1.5;\">颜色:&n