INSERT INTO `dive_equip` VALUES ('5d519ba0-fc1a-4007-af10-4b1438a70b2d', '2015-12-18 10:30:16', 'EB01', '<p>\r\n	<span style=\"font-size:18px;color:#337FE5;line-height:1.5;font-family:\'Microsoft YaHei\';\"><strong>Mares F-LIGHT</strong></span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">Code:417261</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">理想的旅游伙伴</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">超轻量 仅2.2kg&nbsp;</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">小体积 可折叠 容易打包</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">致力于旅行潜水员</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">ERGO浮力控制系统</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">带有两个拉链口袋</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">两个后置Trim配重</span><br />\r\n<img src=\"/attached/image/2015/12/18/9fce25c3-d6e2-4183-921d-305f40468792.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/18/262269a2-1786-4322-9d47-7a6fbb31f7e9.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/18/009c2603-0697-414e-bedf-50edf99b5561.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#999999;line-height:1.5;\">款式：前调整式浮力背心</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">材料：Cordure 420</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">重量(size L):2.2kg</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">D-型环：5个铝制环扣</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">尺寸及对应浮力：XS—XL</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">浮力控制在140--235N</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">颜色：黑-红</span><br />\r\n<span></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/