INSERT INTO `dive_equip` VALUES ('5ddd55a3-107f-47cd-bea2-1f82f7164925', '2015-12-18 09:40:36', 'EB01', '<p>\r\n	<span style=\"font-size:18px;color:#337FE5;line-height:1.5;font-family:\'Microsoft YaHei\';\"><strong>Mares INSTINCT 12S</strong></span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">Code:416164</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">重量轻</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">流体动力学外形设计的二级调节器</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">横向设置的排水导向口，避免排气气泡影响视野</span><br />\r\n<img src=\"/attached/image/2015/12/18/ec56870c-5aaa-4d73-9e71-8ff730b64398.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#999999;line-height:1.5;\">创新的设计引导一系列的优点，横向设计的排气阀使排气更轻松，相信你也不喜欢自己呼出的气泡从眼前飘过，这样的完美设计就是为了避免因为气泡而影响到的视野以及自己拍照时的效果，VAD系统设计，确保在不同体位的呼吸都及其的顺畅，独特的管路设计使我们在潜水中不会因为管子的拉扯而造成咬肌疲劳。12S的一级调节器，配置了DFC系统，确保了稳定顺畅的供气，三合一的材料阀确保耐用性，并配备四个中压供气口。</span><span style=\"font-size:14px;color:#999999;line-height:1.5;\"></span><img src=\"/attached/image/2015/12/18/b2c10c90-38f8-4577-a387-57cce2871380.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#999999;line-height:1.5;\">一级调节器类型：平衡隔膜设计&nbsp;</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">二级调节器类型：顺流式供气阀</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">一级调节器重量：674g&nbsp;</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">二级调节器重量：150g&nbsp;</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">总重量（含管子g）： 962g &nbsp;</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">2个HP 7/16\"&nbsp;</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">4个LP 3/8\" 其中有1个DFC接口</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">中压管及长度：超柔软编织护套中压软管，长度为62cm</span><span style=\"font-size:14px;color:#999999;line-height:1.5;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#999999;line-height:1.5;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#FF9900;line-height:1.5;\">价格：2700元</span> \r\n</p>', 'uploadfiles/equip/equip_HyER1450402835706.jpg', 'Mares INSTINCT 12S', '横向设置的排水导向口\r\n避免排气气泡影响视野', 'ST01', 'ET07', null, '2700', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', '');
