INSERT INTO `dive_equip` VALUES ('63c42986-c5bd-4eb4-a531-6853f65773c0', '2015-12-18 19:57:18', 'EB01', '<p>\r\n	<span style=\"font-size:18px;color:#337FE5;font-family:\'Microsoft YaHei\';\"><strong>2nd SKIN</strong></span><br />\r\n<span style=\"font-size:14px;color:#999999;\">Code:&nbsp;</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">412131(man)</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">412136(she dives)</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#999999;\">适用于冷水水域的6mm潜服</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">舒适的背部保护系统 背部拉链为防水拉链</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">超弹氯丁胶面料更容易穿脱</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">袖口脚踝领口均有短拉链&nbsp;</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">头套: Yes</span> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/12/18/0326712d-b74f-417a-b5fd-b17760df1baa.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/18/c4b44486-749d-4d19-9136-e8e79d4a4c6f.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#999999;\">被誉为“第二层皮肤”设计的潜服厚度为6mm，在所有的水会进入的地方（领口、袖口和脚踝）都重新设计，旨在减少内部水与外界水的交换，减少热量散失，脚踝袖口和领口又多增加了短款拉链，减少因过厚而出现的穿脱困难，超弹的氯丁胶外层更贴服舒适。Supratex护膝和背部保护系统提供了安全可靠。并配有头套保护。</span> \r\n</p>\r\n<p>\r\n	<span><span style=\"font-size:14px;line-height:21px;\">\r\n	<p>\r\n		<span style=\"color:#999999;\">男款尺寸：男2#~男7#</span>\r\n	</p>\r\n	<p>\r\n		<span style=\"color:#999999;\">女款尺寸：女1#~女5#</span>\r\n	</p>\r\n</span></span><img src=\"/attached/image/2015/12/18/a2d4f6ef-22d5-4826-87eb-61f96d51a415.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"color:#FF9900;font-size:14px;line-height:1.5;\">价格：2800元</span>\r\n</p>', 'uploadfiles/equip/equip_vd3g1450444383409.jpg', 'Mares 2nd Skin', '适用于冷水水域的6mm潜服\r\n袖口脚踝领口均有短拉链 ', 'ST01', 'ET03', null, '2800', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', 'SZ010,SZ011,SZ012,SZ013,SZ014,SZ015,SZ017,SZ018,SZ019,SZ020,SZ021');
INSERT INTO `dive_equip` VALUES ('6473338b-5d2a-4244-9d89-a4c9d69692c4', '2015-12-20 01:33:48', 'EB07', '<p>\r\n	<strong>Scubapro</strong>&nbsp;<strong>U-Line SPG 400Bar残压表</strong>\r\n</p>\r\n<img src=\"/attached/image/2015/12/20/bb648cfe-1993-4c5d-9500-aba04aec86e6.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 本体外层包覆强抗磨损塑料材料,强度更强,耐久性更高。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 0-400 BAR适合所有高压管径,为增加潜水者的安全, 0-50 BAR为红色警告颜色涂装。</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 每10BAR为一刻度使潜水者更容易判读，荧光刻度。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#FF9900;\">价格：890元</span>\r\n</p>', 'uploadfiles/equip/equip_B6QY1450596216874.jpg', 'Scubapro U-Line SPG', '经典荧光残压表', 'ST01', 'ET09', null, '890', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', '');
