INSERT INTO `dive_equip` VALUES ('7405c1fb-dc98-406c-a9c7-22e0b2d3eca3', '2016-01-25 21:43:44', 'EB07', '<span style=\"font-family:\'Microsoft YaHei\';font-size:24px;color:#337FE5;line-height:1.5;\"><strong>Scubapro&nbsp;</strong></span><span style=\"font-family:\'Microsoft YaHei\';font-size:24px;color:#337FE5;line-height:1.5;\"><strong>K6</strong></span><br />\r\n<img src=\"/attached/image/2016/01/25/3944bb93-a515-4494-b1c6-536956d6d2b2.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 适合所有潜水所需的、功能最多的潜水刀</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 14cm 不锈钢刀身，具有切、割等功能，并带有锯齿装刀刃以及专为切割鱼线所设计的刀刃</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 人体工学刀柄设计，无论是否穿戴手套都能舒适紧握</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 插入式刀鞘以及两个快速可调整搭扣</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 能快速调整的固定带，使潜水者能快速调整刀的位置</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#FF9900;line-height:1.5;\">价格：900元</span>\r\n</p>', 'uploadfiles/equip/equip_YWNi1453729423861.jpg', 'Scubapro K6', '插入式刀鞘\r\n14cm 不锈钢刀身', 'ST01', 'ET13', null, '900', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', '');
INSERT INTO `dive_equip` VALUES ('741e0f4e-2eb1-49b0-a974-ecfb12075ae1', '2015-12-20 12:54:43', 'EB10', '<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>Water Pro&nbsp;</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';line-height:1.5;color:#337FE5;font-size:18px;\"><strong>防水手提拉杆箱</strong></span> \r\n</p>\r\n<img src=\"/attached/image/2015/12/20/5673a5e4-2dae-4d95-b694-87a220eab7dd.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> *全新超大容量，可容纳两套潜水重装备，如BCD，呼吸调节器，潜水衣等。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> *采用防水布料制作，海边、船上、下雨天均可用。</span><br />\r\n<img src=\"/attached/image/2016/06/13/df33635b-6043-4100-9e8a-9294caf0f3a5.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> *双滚轮，双拉杆，配有肩带，旅行最佳选择，方便实用。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> *采用粉色及蓝色的选色，适合各类人群，在各种场合下都展现独特设计。</span><br />\r\n<img src=\"/attached/image/2015/12/20/65cf4fb7-047f-47f5-a012-6c28e9adc44f.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> *拉杆包角落采用独有排气口设计，可以排水放气，潜水后装备可直接在拉杆包内逐渐滴干排水。</span><span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"></span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 拉杆箱包尺寸：长80cm 宽36cm 高74cm（自重4.5KG）</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#FF9900;\">价格：1800元</span> \r\n</p>', 'uploadfiles/equip/equip_FouE1450587282741.jpg', 'Water Pro 防水手提拉杆箱', '超大容量 时尚设计\r\n采用防水布料制作', 'ST01', 'ET14', null, '1800', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', 'CL008,CL011', 'SZ040');
INSERT INTO `dive_equip` VALUES ('74709e87-3849-4342-9b1a-6c2cdfe500e4', '2015-12-18 11:49:30', 'EB01', '<p>\r\n	<span style=\"font-size:18px;line-height:1.5;color:#337FE5;font-family:\'Microsoft YaHei\';\"><strong>Mares Bungee&nbsp;</strong></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;line-height:1.5;color:#337FE5;font-family:\'Microsoft YaHei\';\"><strong>Fin Strap</strong></span><span style=\"font-size:18px;line-height:1.5;color:#337FE5;font-family:\'Microsoft YaHei\';\"><strong>(pair)</strong></span><br />\r\n<span style=\"font-size:14px;line-height:1.5;color:#999999;\">Code:415353</span><br />\r\n<br />\r\n<span style=\"font-size:14px;line-height:1.5;color:#999999;\">简单而创新的设计</span><br />\r\n<span style=\"font-size:14px;line-height:1.5;color:#999999;\">4中可调节的尺寸，可供所有蛙鞋带要求</span><br />\r\n<span style=\"font-size:14px;line-height:1.5;color:#999999;\">人体工程学设计</span><br />\r\n<span style=\"font-size:14px;line-height:1.5;color:#999999;\">100%兼容mares所有蛙鞋</span><br />\r\n<img src=\"/attached/image/2015/12/18/a9c0c5d7-f35e-4746-ae30-2dd80a954dc2.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span><span style=\"font-size:14px;line-height:1.5;color:#999999;\">全新的橡胶弹性蛙鞋带，虽然就两种规格尺寸，确适合了Mares所有蛙鞋的需要，比起原来弹簧的蛙鞋带，这种在材料上就省去了更多费用，但弹性却和以前一样，一个麻利的操作即可完成蛙鞋带的固定，两个可调式接头满足你需要的尺寸，除此之外，这种材质更不会担心因为海水等造成的辐射和生锈，人体工程学的设计，提供更便捷的舒适。</span><br />\r\n</span>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;line-height:1.5;color:#FF9900;\">价格：340元</span> \r\n</p>', 'uploadfiles/equip/equip_8eGR1450410569587.jpg', 'Mares Bungee Fin Strap', '橡胶弹性蛙鞋带\r\n兼容所有mares所有蛙鞋', 'ST01', 'ET06', null, '340', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', 'SZ002,SZ003,SZ004,SZ006');
