INSERT INTO `dive_equip` VALUES ('796ea845-f4bd-4738-a497-a56ba0173bba', '2015-12-20 01:10:57', 'EB07', '<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>SCUBAPRO&nbsp;</strong></span><span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>全干式呼吸管</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>Spectra Dry Snorkal</strong></span> \r\n</p>\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 可选色彩：黑银，黄，白，红，粉，蓝</span><br />\r\n<img src=\"/attached/image/2015/12/20/96850289-b1b1-4019-abee-e6e7505b4ccf.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 材料：硅胶(咬嘴及螺纹管用了纯净的硅胶，无异味性质稳定)，聚氨酯(无毒无味的聚氨酯用于管身和管头等部件，并非国产的PVC材料，不会跟海水成份反生反应产生有毒物质，而稍倾内侧的咬嘴设计使你咬得更舒服)</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 扣件：浮潜时只需轻轻扣上绝不会在潜水过程脱落，不使用时只需轻轻一按就可将呼吸管和潜水面镜分开，方便清洗、放置和挂扣。</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 适用：浮潜及水肺潜水</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#FF9900;\">价格：200元</span>\r\n</p>', 'uploadfiles/equip/equip_P3ON1450545057313.jpg', 'SCUBAPRO Spectra Dry Snorkal', '全干式呼吸管\r\n绚丽的颜色', 'ST01', 'ET02', null, '200', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', 'CL001,CL006,CL002,CL004,CL011,CL008', '');
INSERT INTO `dive_equip` VALUES ('798fe348-afba-48af-8467-d02f9945dc86', '2016-01-25 21:34:07', 'EB07', '<span style=\"font-family:\'Microsoft YaHei\';font-size:24px;color:#337FE5;line-height:1.5;\"><strong>Scubapro&nbsp;</strong></span><span style=\"font-family:\'Microsoft YaHei\';font-size:24px;color:#337FE5;line-height:1.5;\"><strong>Meridian</strong></span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\">由潜水员为潜水员设计的 SCUBAPRO Meridian 无论在水面或是在水下都是一款设计时尚、功能强大的潜水电脑。</span><br />\r\n<img src=\"/attached/image/2016/01/25/c64791fc-2d73-4bf3-98c8-b7b56bff5eaf.jpg\" alt=\"\" /><img src=\"/attached/image/2016/01/25/b54c4058-09a5-4c57-af19-20d5be0c634e.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 无论你把 Meridian 作为一块多功能的腕表或是在水肺潜水、自由潜水或是仅仅使用它的仪表模式，它都为你提供了最好的功能。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> Meridian 的数据显示一目了然，通过简单的 +/- 按钮可在各种信息间进行切换。表壳采用最高海事级不锈钢 316L 制成，并采用两道镀膜工序，经久耐用。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 减压算法：ZH-l8 ADT MB PMG</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> PDIS：动态潜水中间深度停留</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 心率检测功能（需配合心率带使用）</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 6 个等级（0-5）微气泡抑制技术</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 2 种气体切换：任意两种混合气体减压计算，可在潜水的最后阶段切换至另一种含氧量更高的气体进行减压</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 可适用于 21 - 100% 的高氧</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 潜水过程中可添加书签记录</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 仪表模式时的最大深度 120 米/400 英尺</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 个性化声音提醒功能</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 潜水计划功能</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 安全停留计时器（可设置 1-5 分钟的时长）</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 可调整背光灯时间（2-12 秒）</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 100 小时潜水记录储存，采样率 4 秒/次</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 可储存 99 次潜水记录</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 红外线传输技术</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#FF9900;line-height:1.5;\">价格：5000元</span>\r\n</p>', 'uploadfiles/equip/equip_8lfh1453728847411.jpg', 'Scubapro Meridian', '2 种气体切换\r\n心率检测功能\r\n', 'ST01', 'ET10', null, '5000', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', '');
