INSERT INTO `dive_equip` VALUES ('7b847654-5d5a-4e40-8f0d-2ccebe87f2e7', '2015-12-18 09:43:38', 'EB01', '<p>\r\n	<span style=\"font-size:18px;color:#337FE5;line-height:1.5;font-family:\'Microsoft YaHei\';\"><strong>Mares Octopus ABYSS</strong></span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">Code:416504 &nbsp;</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">材质：金属</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">二级重量(g)：427</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">可以用于高氧潜水</span><br />\r\n<img src=\"/attached/image/2015/12/18/9bcb98c6-31e0-4597-9cef-f60431e3791a.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#999999;line-height:1.5;\">金属二级调节器及旁通管；金属插入和编织网，结合新软管保护更好的热交换，授予最大的可靠性，满足对装备高要求的潜水员。Abyss 金属的外壳，发挥了金属的优良热传导性能，旁通管的设计使呼入的气体直接通过“VIP”通道进入口中，避免因进入二级调节器腔体内的气体而出现结冰想象的发生。MR22的一级调节器，专属独家的1/2\"中压接头,使传输的气体更足呼气更顺畅。</span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#FF9900;font-size:14px;line-height:1.5;\">价格：1600元</span>\r\n</p>', 'uploadfiles/equip/equip_CtoE1450403017490.jpg', 'Mares Octopus ABYSS ', '更好的热交换\r\n最大的可靠性', 'ST01', 'ET07', null, '1600', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', '');
INSERT INTO `dive_equip` VALUES ('7c7683d3-1b46-443a-b5b3-eaccec5eba50', '2015-12-18 03:38:34', 'EB01', '<p>\r\n	<span style=\"font-size:18px;color:#337FE5;line-height:1.5;font-family:\'Microsoft YaHei\';\"><strong>Mares NEMO WIDE</strong></span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">Code:414114</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">人与海洋的最佳匹配</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">三种气体模式的切换</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">宽显示屏 数据读取特佳</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">人性化设计 界面直观</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">软件可升级</span> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/12/18/54f2f61f-7509-421d-9f39-a34ecb301285.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;font-size:14px;color:#999999;\">Nemo宽屏的设计安全、直观、可靠，易于阅读、易于使用。在这个新版本中,我们在保持了原有的这些优秀性能的同时，我们还通过重组提高了可读性显示的布局和组织信息的显示，使显示更完整。同样适合技术潜水员。</span> \r\n</p>\r\n<p>\r\n	<br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">最大显示深度：150m</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">储存时长：38hr</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">采样率：5s</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">电池可更换</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#FF9900;line-height:1.5;\">价格：2800元</span> \r\n</p>', 'uploadfiles/equip/equip_acaP1450381113979.jpg', 'Mares NEMO WIDE', '三种气体模式的切换\r\n宽显示屏 数据读取特佳', 'ST01', 'ET10', null, '2800', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', '');
