INSERT INTO `dive_equip` VALUES ('811f9ea8-c8d5-4ba1-96ed-885c4ffc6e44', '2015-12-18 03:57:01', 'EB01', '<p>\r\n	<span style=\"font-size:18px;color:#337FE5;line-height:1.5;font-family:\'Microsoft YaHei\';\"><strong>Mares ABYSS 22 NAVY</strong></span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">Code:416158</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">极端环境下的海军调节器</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">配有CWD防冻包 &nbsp;所有内部机械部件完全和水隔绝</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">多聚体树脂涂层的二级调节器 &nbsp;使水不会积留</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\"></span><img src=\"/attached/image/2015/12/18/14ba1296-bcec-4398-9348-376c0597621d.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';line-height:1.5;\"><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">防冻：配有标准CWD冰潜防冻装置，所有机械部件完全与水隔绝；防水涂层：多聚体树脂涂层，避免了因水积留在调节器上造成的结冰；水平管路设计：主调节器和备用调节器并排水平安装；NCC自然对流系统：大大提高在冷水环境中的热交换；双DFC动态流量控制系统：确保在任何深度都呼吸顺畅。</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">一级调节器类型：平衡膜设计</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">二级调节器类型：顺流式供气阀</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">一级调节器重量(g):850&nbsp;</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">二级调节器重量(g):269&nbsp;</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">总重量(含管子g):1337&nbsp;</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">HP端口： 2 HP 7/16\"&nbsp;</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">LP端口： 3 LP 3/8\" 其中1个1/2\"DFC接口连接</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">80cm橡胶管</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#FF9900;font-size:14px;line-height:1.5;\">价格：4200元</span><span style=\"font-family:Microsoft YaHei;\"></span>\r\n</p>', 'uploadfiles/equip/equip_j35W1450382220577.jpg', 'Mares ABYSS 22 NAVY', '所有内部机械部件完全和水隔绝\r\n多聚体树脂涂层使水不会积留', 'ST01', 'ET07', null, '4200', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', '');
