INSERT INTO `dive_equip` VALUES ('8fc04fcb-35ca-45e7-b0e2-982b06c3b43d', '2015-10-25 20:04:54', 'EB02', '<p>\r\n	<img src=\"/attached/image/2015/12/17/8ed8d9f1-4f29-4558-b2ab-cfbc9c112057.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:medium;line-height:24px;\">价格：6400元</span>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	Poseidon(海神，又名波塞冬) 是一家来自瑞典，具有58年历史的潜水器材生产商，其产品素以品质闻名于世，深受专业潜水人士的喜爱。各国的军事单位无不采用她的产品，甚至美国太空总署也是合作伙伴。是高品质、高可靠性的代名词。\r\n</p>', 'uploadfiles/equip/equip_3e3s1445774693856.jpg', 'Poseidon Cyklon Metal', '顺流阀式二级配活塞式一级\r\n可适用于冷水潜水', 'ST01', 'ET07', null, '6400', null, '6a040c09-5758-41f8-8910-bf556692e824', null, null);
INSERT INTO `dive_equip` VALUES ('90edef74-10ae-42d8-95d9-91750351f08f', '2015-12-20 00:42:17', 'EB07', '<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>男式半干潜水衣</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>Nova Scotia 6.5mm&nbsp;</strong></span> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/12/20/367e8118-76c6-470f-8c04-72f8de4868a3.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">【产品特点】基本为6.5mm尼龙氯丁胶，活动部位用Everflex超弹材料增加舒适度，绒毛针织衬里，德国Ti-Zip干式拉练，Glideskin 密封口，溶胶 Tatex 护膝</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">【主要功用】</span> \r\n</p>\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">在水中，热的传导比空气要快25倍。防寒衣的公用就是要将这种热传导速度减缓，让我们在潜水的时候感觉舒适温暖。防寒衣的厚度跟实际的水温有100%的关系(请参阅下表)。较薄的潜水衣(如1.6mm、水母衣)虽然它的保暖效果有限，但也提供了保护身体不被珊瑚割伤的公用。水母衣 收藏的时候体积很小，非常适合到热带国家潜水旅游时携带。在珠三角地区夏天时3mm即可,到了冬天5mm加头套比较适合。</span><br />\r\n<img src=\"/attached/image/2015/12/20/33bd9336-57d3-4f7a-b9a4-06668ce3e3a6.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 【水温 防寒衣厚度】(材质)</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 24-30℃ 1.6mm(尼龙、莱卡)</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 21-30℃ 3mm(尼龙)</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 18-24℃ 5mm(尼龙)</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 10-21℃ 6.5mm(尼龙)</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 1.5-18℃ 9.5mm(尼龙)、干式防寒衣</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#FF9900;\">价格：2600元</span>\r\n</p>', 'uploadfiles/equip/equip_D2PM1450543337226.jpg', 'SCUBAPRO Nova Scotia 6.5mm', '男式半干潜水衣', 'ST01', 'ET03', null, '2600', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', 'SZ002,SZ003,SZ004,SZ005,SZ006,SZ007');
