INSERT INTO `dive_equip` VALUES ('93b97f33-7afb-4488-8b34-a3fdd3ca26dd', '2016-01-25 14:07:56', 'EB03', '<span style=\"color:#337FE5;font-family:\'Microsoft YaHei\';font-size:18px;\"><strong>Aqua Lung</strong></span><br />\r\n<span style=\"color:#337FE5;font-family:\'Microsoft YaHei\';font-size:18px;\"><strong>Legend Octopus</strong></span><br />\r\n<img src=\"/attached/image/2016/01/25/11534de6-daff-4e65-bfde-22a9a7787d86.jpg\" alt=\"\" /><span style=\"color:#999999;font-family:\'Microsoft YaHei\';\"> Lung Legend 系列的 Legend 备用调节器，具有高可视度的黄色版本是与 Legend 呼吸调节器的完美组合。</span><br />\r\n<br />\r\n<span style=\"color:#999999;font-family:\'Microsoft YaHei\';\"> 高可视度黄色排水按钮及组件</span><br />\r\n<span style=\"color:#999999;font-family:\'Microsoft YaHei\';\"> 1 米长的高可视度黄色编织喉管</span><br />\r\n<span style=\"color:#999999;font-family:\'Microsoft YaHei\';\"> 气动平衡设计是 Legend 超平衡一级调节器完美组合</span><br />\r\n<span style=\"color:#999999;font-family:\'Microsoft YaHei\';\"> 防漏气设计</span><br />\r\n<p>\r\n	<span style=\"color:#999999;font-family:\'Microsoft YaHei\';\"> 冷水环境中的卓越性能源自于阀门周围的高效热能交换技术</span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#FF9900;font-family:\'Microsoft YaHei\';\">价格：1900元</span>\r\n</p>', 'uploadfiles/equip/equip_i4vs1453702075866.jpg', 'Aqua Lung Legend Octopus', '气动平衡设计\r\n高效热能交换技术', 'ST01', 'ET07', null, '1900', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', '');
INSERT INTO `dive_equip` VALUES ('93bdf600-5bf7-4e8d-9c3f-e4c276914c00', '2015-12-18 22:17:41', 'EB01', '<p>\r\n	<span style=\"color:#337FE5;line-height:1.5;font-family:\'Microsoft YaHei\';font-size:18px;\"><strong>Mares Trilastic long-sleeve</strong></span> \r\n</p>\r\n<p>\r\n	<span><span style=\"line-height:36px;\"><b><span style=\"font-family:\'Microsoft YaHei\';line-height:1.5;color:#337FE5;font-size:18px;\">长袖防晒服</span><br />\r\n</b></span></span> <span style=\"font-size:12px;color:#999999;line-height:1.5;font-family:\'Microsoft YaHei\';\">Code: 412972 412984(BGR/PK)</span><br />\r\n<span style=\"font-size:12px;color:#999999;line-height:1.5;font-family:\'Microsoft YaHei\';\">适合所有水上运动</span><br />\r\n<span style=\"font-size:12px;color:#999999;line-height:1.5;font-family:\'Microsoft YaHei\';\">紫外防护</span><br />\r\n<span style=\"font-size:12px;color:#999999;line-height:1.5;font-family:\'Microsoft YaHei\';\">体感柔滑</span><br />\r\n<span style=\"font-size:12px;color:#999999;line-height:1.5;font-family:\'Microsoft YaHei\';\">使用特殊材料</span> \r\n</p>\r\n<div>\r\n	<br />\r\n</div>\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\"></span>\r\n<p>\r\n	<img src=\"/attached/image/2015/12/18/020358f9-c32e-43d0-92df-e2fd74fe5cb9.jpg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"color:#999999;\">男式款 &nbsp;</span><span style=\"color:#999999;\">颜色：黑白色 &nbsp; &nbsp; &nbsp;大小：XS到XL</span>\r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/12/18/a95b30cf-b09c-47c2-ac28-1935c5ee50a6.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/18/271b8a14-3de7-40a7-99ab-40a3d45263be.jpg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"color:#999999;\">男式款 &nbsp;</span><span style=\"color:#999999;\">颜色：灰 &nbsp; &nbsp; &nbsp;大小：2XS到L</span><span style=\"color:#999999;\"></span>\r\n</p>\r\n<p>\r\n	<br />\r\n<span style=\"font-size:12px;color:#999999;line-height:1.5;font-family:\'Microsoft YaHei\';\">一款时尚的防晒服，适合所有在水上的运动；防紫外功能更有效地保护您；使用超级弹性的氯丁胶制作而成，使穿着后体感柔滑。</span><span style=\"font-size