INSERT INTO `dive_equip` VALUES ('9446cdeb-6b9c-4576-a131-542e1fa10332', '2015-12-20 01:40:20', 'EB07', '<p>\r\n	<strong>Scubapro</strong>&nbsp;<strong>X-Force BCD</strong>\r\n</p>\r\n<img src=\"/attached/image/2015/12/20/edf85aba-bc90-4bf9-b982-5684039c3728.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 融合了创新和传统功能,划时代设计,适合专业和体育休闲潜水员的浮力调整器.</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 1.平衡式强力充排气阀(BPI)</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> &nbsp;设计轻巧,只需轻轻按下,就能稳定地排气及充气</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 2.3个排气卸压阀</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> &nbsp;为了控制浮力,保持体位,SCUBAPRO浮力背心配有多个排气阀</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> &nbsp;⑴右肩排气卸压阀,配有胸前拉绳</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> &nbsp;⑵左肩排气卸压阀,与浮力调节器软管连接充排气阀,并配有安全快速排气杆(SQAP)系统,可以推动排气杆,直接从肩部放气,方便教练控制潜水员的浮力</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> &nbsp;⑶腰部排气卸压阀,方便头下脚上时使用</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 3.整体采用500丹尼尔(Denier)国都拿,经久耐用</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 4.流体动力学设计的裁剪,配合在腰封和肩部内侧的泡沫塑料软垫更加合身,舒适</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 5.SCUBAPRO专利配重整合系统</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> &nbsp;独有设计,浮力衣内置可拆配重袋,每个可装高达5公斤的铅块,方便可靠,减少了传统的配重带给腰部的压力</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 6.背部更设有可扩张配重袋,分布气瓶两侧的平衡配重袋,使身体的配重分布均匀也可以使用后备配重袋装载安全浮标,潜水灯等</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 7.可调整式腰封,操作简单,适应性广,并设有拉链口袋,方便装取配件</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 8.T-Comfort舒适背架</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> &nbsp;牙花加厚衬垫,适合所有潜水员的体形,并给予无可比拟的舒适</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 9.备有两个可扩大的口袋,采用大型齿型拉链更坚固耐用</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 10.胸膛前可调较的单边弹力扣带,令潜水员更感舒适</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 11.6个不锈钢D型环,用途广泛,可携不同配件</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 12.SCUBAPRO专利气瓶固定装置(Supercinch Stainless Steel Tank Band System)专有技术,</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 不锈钢快卸气瓶扣,可单手操作,容易调较,也可令气瓶稳固地锁上</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">13.原厂意大利制造型号:X FORCE</span> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/12/20/644c60ef-29b9-492b-8152-2fecf2a5097d.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"color:#FF9900;\">价格：4800元</span>\r\n</p>', 'uploadfiles/equip/equip_sgby1450595826301.jpg', 'Scubapro X-Force BCD', '平衡式强力充排气阀\r\n流体动力学设计的裁剪', 'ST01', 'ET08', null, '4800', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', 'SZ003,SZ004,SZ005');
INSERT INTO `dive_equip` VALUES ('95af7b4d-ed12-43d7-b3f1-8123abea02ef', '2015-12-20 01:29:00', 'EB07', '<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>Scubapro Trinidad</strong></span><img src=\"/attached/image/2015/12/20/bd9f4971-9d59-4a6b-b64e-2868f783868c.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 无需外框将面镜和裙边组合一起</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 内容积特别小 清洗容易方便</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 单一镜片设计 无碍视野</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 钢化玻璃</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 防滑面镜带</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 转动面镜带扣方便</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 单手快速调整</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 黑色硅胶裙边</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 附送塑料面镜盒</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#FF9900;\">价格：280元</span>\r\n</p>', 'uploadfiles/equip/equip_1mft1450546139645.jpg', 'Scubapro Trinidad Mask', '经典潜水面镜', 'ST01', 'ET01', null, '280', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', '');
INSERT INTO `dive_equip` VALUES ('9618ecaf-3776-4c4a-8e61-f6dbaefb7943', '2015-12-18 12:07:12', 'EB01', '<p>\r\n	<span style=\"font-size:18px;color:#337FE5;font-family:\'Microsoft YaHei\';\"><strong>Mares VOLO RACE</strong></span><br />\r\n<span style=\"font-size:14px;color:#999999;\">Code: 410313</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#999999;\">轻便舒适、最省力的套脚式蛙鞋</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">符合解剖学特点的鞋肚</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">女性的理想用品</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">打水最省力的脚蹼</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">OPB:该型脚蹼可使叶板呈最佳角度，可减少踢水时的疲劳感</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">Channel THrust:导流口推力技术可以通过导流槽引导水流，使其产生的推力增大</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">Superchannel：导流口的推进技术最新进展是超级导流口，叶板设计成一个大而灵活的中央超级导流口，使水沿着脚蹼的叶板（而不是从四周溢流），更有效的推动水流</span><br />\r\n<br />\r\n<img src=\"/attached/image/2015/12/18/25d73ab9-b275-4019-af8f-c99ae627391c.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/18/059d9d4b-1f57-4dc9-b5e8-d7bce9ba766a.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/18/47326d01-461b-499f-bfae-19fbd3428edb.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#999999;\">鞋肚类型：套脚式鞋肚</span><br />\r\n<span style=\"font-size:14px;color:#999999;\"> 材质：热塑性橡胶 聚丙烯</span><br />\r\n<img src=\"/attached/image/2015/12/18/ded646f0-9240-42bc-bb10-fc28ed569fec.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#FF9900;\">价格：560元</span> \r\n</p>', 'uploadfiles/equip/equip_eDyV1450411632257.jpg', 'Mares VOLO RACE', '高效且潮流的设计\r\nVOLO 套脚式', 'ST01', 'ET06', null, '560', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', 'CL013,CL017,CL019', 'SZ022,SZ023,SZ024,SZ025,SZ026,SZ027,SZ028,SZ029');
