INSERT INTO `dive_equip` VALUES ('97be1992-ca92-43dc-8972-7bddf4bee783', '2015-12-19 22:49:48', 'EB07', '<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>Scubapro CONSOLE 3 Inline</strong></span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 一款最新紧凑型设计、结合 UWATEC 指北针和 SCUBAPRO 深度计与气压表的全功能三联表，是潜水初学者、潜水中心以及潜水专家作为备用仪表的理想选择。</span><br />\r\n<img src=\"/attached/image/2015/12/19/46ca473d-cf1a-4899-a88d-8a799effa10f.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> &nbsp;气瓶压力表</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 接管采用高强度黄铜制造，经久耐用</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 0 - 400 BAR 可适用于任何高压气瓶</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 0 – 50 BAR 区域采用红色显示，进一步潜水员确保安全</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 表盘显示简单易懂</span><br />\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">深度计</span> \r\n</p>\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">接管采用高强度黄铜制造，经久耐用</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 表盘设计简单易读，最大深度可达 70 米</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 最大深度指示，使用简便</span><br />\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">指北针</span> \r\n</p>\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">侧边观察窗设计，导航简便、精确</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 特制夜光表面，即使在昏暗的环境中也能方便使用</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"><br />\r\n</span>\r\n</p>\r\n<p>\r\n	<span><span style=\"color:#FF9900;\">价格：1900元</span></span>\r\n</p>', 'uploadfiles/equip/equip_H1qL1450536587665.jpg', 'SCUBAPRO CONSOLE 3 Inline', '残压表400Bar\r