INSERT INTO `dive_equip` VALUES ('98de7815-1008-48e6-8499-a2581c6f0002', '2016-01-25 21:36:24', 'EB07', '<span style=\"font-family:\'Microsoft YaHei\';font-size:18px;color:#337FE5;line-height:1.5;\"><strong>Scubapro&nbsp;</strong></span><span style=\"font-family:\'Microsoft YaHei\';font-size:18px;color:#337FE5;line-height:1.5;\"><strong>Nova 230</strong></span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\">中等尺寸的高亮度 LED 潜水灯</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\">电池使用寿命可长达 14-18 小时</span> \r\n</p>\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\">休闲潜水的最佳选择，也可作为技术潜水时的备用潜水灯</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\">结实的铝制灯身，经久耐用</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\">使用 3 节 2 号碱性电池</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\">光照亮度：230 流明</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\">灯泡寿命长达 100,000 小时</span><br />\r\n<p>\r\n	<img src=\"/attached/image/2016/01/25/80a03d48-5fcc-40a8-a8de-d99c7048f877.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\">参数</span> \r\n</p>\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\">功率（瓦）：Hi-LED</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\">电池寿命：14-18 小时</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\">电池数：3</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\">电池型号：2 号（C batteries）</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\">能否充电：否</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\">灯泡类型：LED</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\">尺寸：193 mm 长 / 43 mm 直径</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\">重量（不含电池）：109g</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\">最大潜水深度：120 米 / 393 英尺</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\">材质：铝</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#FF9900;line-height:1.5;\">价格：1000元</span>\r\n</p>', 'uploadfiles/equip/equip_N1xV1453728984349.jpg', 'Scubapro Nova 230', '结实的铝制灯身\r\n灯泡寿命长达十万小时', 'ST01', 'ET12', null, '1000', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', '');
INSERT INTO `dive_equip` VALUES ('98edba9c-cfca-4080-b173-8f40bc137a74', '2016-01-25 01:22:38', 'EB03', '<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>Aqua Lung</strong></span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>ABS Octopus</strong></span><br />\r\n<br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> Aqua Lung 提供几种不同的备用调节器以满足潜水员不同的需要。</span><br />\r\n<img src=\"/attached/image/2016/01/25/711d382f-1c94-41f5-bdde-2ad44ba96c4a.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 新设计的 ABS 备用调节器是高性能和小巧轻便的完美组合</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 超越调节器呼吸测试的 CE 标准</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 万向设计允许潜水员以任意方式使用</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 喉管与咬嘴之间独特的 120° 弯角是 Aqua Lung 为方便潜伴使用备用调节器的独有创新，没有任何方向限制，不会引起任何喉管弯曲问题</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 可快速拆卸的挂钩设计方便固定 ABS 备用调节器</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 高可视度的 1 米长的黄色喉管可让潜伴在需要时及时定位备用调节器</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#FF9900;\">价格：1300元</span>\r\n</p>', 'uploadfiles/equip/equip_Adw41453656157725.jpg', 'Aqua Lung ABS Octopus', '超越调节器呼吸测试的 CE 标准', 'ST01', 'ET07', null, '1300', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', '');
INSERT INTO `dive_equip` VALUES ('98ff55b4-496c-498a-83b2-e686e635e1da', '2016-01-25 13:53:39', 'EB03', '<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>Aqua Lung</strong></span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>Legend LX ACD, Twilight</strong></span><br />\r\n<img src=\"/attached/image/2016/01/25/68c62802-0d6a-49ae-92ba-c7cac581b475.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 新的 Legend 系列调节器拥有创新的科技工艺以及美观设计。Legend 系列调节器小巧轻便，为潜水者带来舒适的享受。无论你在全球任何潜点，Legend 都让你看起来像一位专业的潜水明星。</span><br />\r\n<br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 一级调节器</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">自动关闭装置（ACD）在调节器从气瓶头卸下时关闭调节器进气口，免除一级头进水困扰，同时可防止内部润滑剂被水洗去，在使用高氧时更安全</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 独有的超平衡隔膜式设计不论在任何深度都能体现优秀的性能</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 2 个高压接口和 4 个中压接口</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 全密闭设计让主弹簧舱保持干燥并防止结冰和灰尘、污染物的侵入</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 完全适合冰潜环境</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 有 YOKE 和 DIN 两种接口可供选择</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 适用于 40% 的高氧</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"><br />\r\n</span>\r\n</p>\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 二级调节器</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 气动平衡设计二级头为潜水员提供顺畅的呼吸</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 舒适的咬嘴大大缓解咬合的疲劳度</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 专利设计的阀门热能交换技术，缓解在温暖水域因气体膨胀导致的降温</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 人体工学排气通道确保潜水员不被气泡干扰，这样的设计同样减低了呼气阻抗</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 附赠的 29 寸网带编制中压管柔软轻巧，彻底消除拖曳感</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 嘴唇保护套有助于嘴唇保温</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"><br />\r\n</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#FF9900;\">价格：4900元</span>\r\n</p>', 'uploadfiles/equip/equip_w0k11453701218655.jpg', 'Aqua Lung Legend LX ACD调节器', '小巧轻便\r\n创新的科技工艺以及美观设计', 'ST01', 'ET07', null, '4900', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', '');
INSERT INTO `dive_equip` VALUES ('9a2db688-d1aa-4e31-9478-704fd1147725', '2016-01-25 21:47:07', 'EB07', '<span style=\"font-family:\'Microsoft YaHei\';font-size:24px;color:#337FE5;line-height:1.5;\"><strong>Scubapro&nbsp;</strong></span><span style=\"font-family:\'Microsoft YaHei\';font-size:24px;color:#337FE5;line-height:1.5;\"><strong>Reel</strong></span><br />\r\n<br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 可用于固定水面标示浮标、沉船潜水、洞穴潜水或是绑定减压绳，是各种类型潜水理想的辅助器材。</span><br />\r\n<img src=\"/attached/image/2016/01/25/818d8024-f72d-48a1-bdf7-93d653b9f9de.jpg\" alt=\"\" /><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 多功能配件在搜索和导航潜水时使用，也是洞穴潜水等活动中保障潜水员能回到出发点的必需品。</span><span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\">耐磨电镀合金制造的支架，配上尼龙玻璃纤维的绞轮和把手，让这绞轮的重量特别轻和耐用长寿。跟支架折叠一起的旋钮开关设计与滑轮轴心平衡的把手，使之容易控制，减少线绳纠缠的机会。滑轮转动的速度可以凭不同的摩擦力来调教，从而避免不受控制的滚动。把手上独特的滚动开关可让滚轮自由滚动或根据调节后的速度滚动。80米手工纤维线和支架上的挂钩以便于携带。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#FF9900;line-height:1.5;\">价格：760元</span>\r\n</p>', 'uploadfiles/equip/equip_bKfw1453729627285.jpg', 'Scubapro Reel', '耐磨电镀合金支架\r\n滑轮设计防纠缠和失控\r\n', 'ST01', 'ET15', null, '760', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', '');
INSERT INTO `dive_equip` VALUES ('9b68f6d4-93b0-464a-b7e2-f3fdb97e6b6e', '2015-12-20 01:38:38', 'EB07', '<p>\r\n	<strong>Scubapro</strong>&nbsp;<strong>X-BLACK BCD</strong>\r\n</p>\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 使用了 AirFlex 空气无障碍流通系统以保证最简单和稳定的浮力控制。它不仅拥有最多的功能，同时也是所有 Scubapro BCD 中以人体工学原理设计的、最舒适的BCD。</span><br />\r\n<img src=\"/attached/image/2015/12/20/5479586c-5545-4f56-a8c7-e64d09efef06.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 超级气瓶金属固定扣</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 外部采用高耐磨的Cordura 1000D 面料配合内部丹尼尔 420 尼龙材质，经久耐用</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 内置整合式配重系统</span><br />\r\n<img src=\"/attached/image/2015/12/20/fcd5070d-32b2-4de6-beaa-500fe15a3850.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 额外的背部配重放置袋使配重更加平衡</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 7 个不锈钢 D 型环可满足任何需要</span><br />\r\n<img src=\"/attached/image/2015/12/20/d974ce8b-ab7a-44cc-8178-63845db98d6e.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 网状背垫增加无与伦比的舒适度</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 预置潜水刀固定孔</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 可旋转肩部快卸扣</span><br />\r\n<img src=\"/attached/image/2015/12/20/f1b01c11-b43f-422d-ac56-86e14fd6590c.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 两侧带拉链的大口袋</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 可调整长短的快粘带</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 提升浮力（尺码/公斤）：XS/11.2、S/14.3、M/18.3、L/20.3、XL/21.4</span><br />\r\n<div>\r\n	<span style=\"color:#FF9900;\">价格：5200元</span> \r\n</div>', 'uploadfiles/equip/equip_3wv21450546717681.jpg', 'Scubapro X-BLACK BCD', '超级气瓶金属固定扣\r\n额外的背部配重放置袋使配重更加平衡', 'ST01', 'ET08', null, '5200', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', 'SZ002,SZ003,SZ004,SZ005,SZ006');
