INSERT INTO `dive_equip` VALUES ('9c11b45b-2554-45b6-885e-087f76f89f0a', '2016-01-25 01:34:59', 'EB03', '<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>Aqua Lung</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>3mm shorty mahe 短款潜水服</strong></span><span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">此短款连身衣集许多功能于一身，它采用了超弹性面料和时髦的设计并且价格十分具有吸引力。</span> \r\n</p>\r\n<img src=\"/attached/image/2016/01/25/fa61024d-f587-4fb7-b5a2-149e1d243f70.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> - 通体使用3毫米超弹性面料</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> - 颈部使用可调节搭扣</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> - 使用更耐用的10#拉链</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> - 平式锁缝针迹使穿着更舒适</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> - 后背密封条</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#FF9900;\">价格：620元</span>\r\n</p>', 'uploadfiles/equip/equip_N62p1453656898633.jpg', 'Aqualung 3mm shorty mahe 短款潜水服', '平式锁缝针迹使穿着更舒适', 'ST01', 'ET03', null, '620', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', 'SZ003,SZ004,SZ005,SZ006');
INSERT INTO `dive_equip` VALUES ('9ccbc543-b7ec-45cb-862a-291d7a9fc79c', '2015-12-18 09:48:46', 'EB01', '<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><span style=\"font-family:\'Microsoft YaHei\';line-height:1.5;color:#337FE5;font-size:18px;\"><strong>Mares Octopus CARBON</strong></span></span><span style=\"font-family:\'Microsoft YaHei\';font-size:24px;\"><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;line-height:1.5;color:#999999;\">Code:416535</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;line-height:1.5;color:#999999;\"><br />\r\n</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:24px;\"><span style=\"font-family:\'Microsoft YaHei\';font-size:14px;line-height:1.5;color:#999999;\">用最轻演绎最佳性能</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;line-height:1.5;color:#999999;\">100%碳素纤维材料的二级调节器</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;line-height:1.5;color:#999999;\">明显的黄色中压管长度为1m</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;line-height:1.5;color:#999999;\">材料：碳素</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;line-height:1.5;color:#999999;\">重量 (g): 354</span><br />\r\n</span><img src=\"/attached/image/2015/12/18/fa28098a-1548-49e4-a26b-1ac659fc434f.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"color:#999999;font-family:\'Microsoft YaHei\';font-size:14px;line-height:1.5;\">Carbon的备用调节器在Mesh-Grid system 和VAD涡流系统的应用使得其在呼吸上变得极其简单和顺畅，碳素材料的使用使其发挥了金属的优良传导性能的同时在质量上确极其的轻，比传统的二级要轻了65%。优良的碳素导热系统使呼吸中的水分得以保留，减少口感的感觉。MR52一级头的水平管路设计使得主调节器和备用可以并排水平安装。NCC自然对流系统，大大提高在冷水环境中的热交换。</span><span style=\"font-family:\'Microsoft YaHei\';font-size:14px;line-height:1.5;color:#FF9900;\">双DFC动态流量控制系统，可确保在任何深度呼吸顺畅。</span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#999999;font-family:\'Microsoft YaHei\';font-size:14px;line-height:1.5;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;line-height:1.5;color:#FF9900;\">价格：2200元</span> \r\n</p>', 'uploadfiles/equip/equip_M5751450403325813.jpg', 'Mares Octopus Carbon', '最轻质量演绎最佳性能\r\n100%碳素纤维材料', 'ST01', 'ET07', null, '2200', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', '');
