INSERT INTO `dive_equip` VALUES ('a0572267-8079-46a8-b5b3-8a0ae033303f', '2016-01-25 14:03:40', 'EB04', '<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>ATOMIC</strong></span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>T3 Titanium Regulator with Swivel</strong></span><br />\r\n<img src=\"/attached/image/2016/01/25/2fcdbfc0-4817-4e53-8ca5-2e9399088716.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> Atomic T3 调节器为不考虑预算的潜水员打造一个全新标准的钛合金调节器。T3 是 Atomic 旗舰调节器，为你提供了空前的性能和可信赖度。极其轻巧的重量和可靠的制造材料使这款调节器非常方便携带，它让你在即使没有调节器维护服务、充满异国情调的潜水度假胜地也能非常自信、安心的潜水。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 一级调节器的部件完全由整块钛合金材料经精密加工制造，重量轻并且无任何锈蚀的可能</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 高性能喷射座垫活塞</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 冰冻保护原厂密封设计使一级调节器在泥沙等污染环境也能高效工作</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> T3 可用在最高 40% 氧气混合气的潜水中</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 一级调节器接口：2 个固定高压口和 5 个可旋转低压口</span><br />\r\n<br />\r\n<ul>\r\n	<li>\r\n		<span style=\"line-height:1.5;font-family:\'Microsoft YaHei\';color:#999999;\">Atomic 专利“Seat Saving”通气孔（钛合金）</span> \r\n	</li>\r\n	<li>\r\n		<span style=\"line-height:1.5;font-family:\'Microsoft YaHei\';color:#999999;\">Atomic 专利自动流量控制（AFC）</span> \r\n	</li>\r\n	<li>\r\n		<span style=\"line-height:1.5;font-family:\'Microsoft YaHei\';color:#999999;\">用于特殊场合由软材料预制成型的空气调节杆</span> \r\n	</li>\r\n	<li>\r\n		<span style=\"line-height:1.5;font-family:\'Microsoft YaHei\';color:#999999;\">钛合金二级头弯角转接头</span> \r\n	</li>\r\n	<li>\r\n		<span style=\"line-height:1.5;font-family:\'Microsoft YaHei\';color:#999999;\">高流量二级调节器外壳并配以球状杠杆低阻抗设计以适用于任何浅水区域至超越极限深度的潜水需要</span> \r\n	</li>\r\n	<li>\r\n		<span style=\"line-height:1.5;font-family:\'Microsoft YaHei\';color:#999999;\">复合材料制作的调节器排水按钮可让潜水员按压任何位置进行排水</span> \r\n	</li>\r\n	<li>\r\n		<span style=\"line-height:1.5;font-family:\'Microsoft YaHei\';color:#999999;\">两种硅胶材质制成的放撕裂咬嘴</span> \r\n	</li>\r\n	<li>\r\n		<span style=\"line-height:1.5;font-family:\'Microsoft YaHei\';color:#999999;\">金属前盖环</span> \r\n	</li>\r\n	<li>\r\n		<span style=\"line-height:1.5;font-family:\'Microsoft YaHei\';color:#999999;\">一级调节器和二级调节器总重量为 0.77 公斤 / 1.7 磅（YOKE）</span>\r\n	</li>\r\n	<li>\r\n		<span style=\"line-height:1.5;color:#FF9900;\">价格：15000元</span>\r\n	</li>\r\n</ul>', 'uploadfiles/equip/equip_stLp1453701819558.jpg', 'ATOMIC T3 Titanium Regulator with Swivel', 'Atomic 旗舰调节器\r\n钛合金打造', 'ST01', 'ET07', null, '15000', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', '');
