INSERT INTO `dive_equip` VALUES ('b25d8278-6c73-4ea7-8d77-b15e7fa54836', '2015-12-19 16:07:58', 'EB06', '<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>VADER MASK潜水面镜</strong></span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 防UV面镜&nbsp;</span><br />\r\n<img src=\"/attached/image/2015/12/19/5ea551ac-0e6f-4095-8985-f5f4cf3178b9.png\" alt=\"\" /><img src=\"/attached/image/2015/12/19/ffd03153-43f0-470c-9416-98f10f278755.png\" alt=\"\" /><img src=\"/attached/image/2015/12/19/a75f82c5-eb58-4082-9246-4f8dbd8a54c1.png\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 三宽设计，确保了宽敞明亮的视野</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 1）无框中心</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 这里没有中心框架，左眼视力，扩大了左眼视力右，视力也降低了压力，确保明亮和宽敞。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 高能见度性能是需要严重跳水现场。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 2）10 °倾斜角</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 10 °倾斜角度，与协同作用和镜片形状，甚至更好的能见度下降。为了确保适当的可见性和胸部是最潜水作业。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 3）113 °方</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 4）对紫外线A的保护镜头规格</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 波长紫外线A高于平均水平的80％削减。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 琥珀与阴影的颜色被卷入水中更容易。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 5）Rovoryumu</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 内容：140cc&nbsp;</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 最后嘴唇卷曲，积极围绕软密封面。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 这条线索也具有难以跟踪。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 7）Doreningusukato</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 改善排水明确面具是为了防止积水。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 8）Sofutodanpa</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 面具十分普遍下调为导向的观点，减轻压力，使框架颧骨由于挤压力。</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> GM-1253为防UV镜片，可选GM-1254透明镜片。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#FF9900;\">价格：900元</span>\r\n</p>', 'uploadfiles/equip/equip_ibaV1450512477963.png', 'GULL VADER MASK潜水面镜', '', 'ST01', 'ET01', null, '900', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', 'CL001,CL002,CL004,CL005,CL006,CL007,CL008,CL009,CL010', 'SZ040');
INSERT INTO `dive_equip` VALUES ('b3490159-03a3-40dc-8e08-058ab17d4bdd', '2015-12-18 09:51:10', 'EB01', '<p>\r\n	<span style=\"font-size:18px;color:#337FE5;line-height:1.5;font-family:\'Microsoft YaHei\';\"><strong>Mares Octopus INSTINCT</strong></span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">Code:416542</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">流体力学设计的二级头</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">横向排水设置的排水导向口，避免排气起泡影响视野</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">材质: Technopolymer</span> \r\n</p>\r\n<div>\r\n	<img src=\"/attached/image/2015/12/18/8f983821-ff39-4fda-bfd5-cc6f0916cc3a.jpg\" alt=\"\" /> \r\n</div>\r\n<p>\r\n	<span style=\"color:#999999;font-size:14px;line-height:1.5;\">创新的设计引导一系列的优点，横向设计的排气阀使排气更轻松，相信你也不喜欢自己呼出的气泡从眼前飘过，这样的完美设计就是为了避免因为气泡而影响到的视野以及自己拍照时的效果。成角度的吸气阀膜片及VAD（涡流辅助供气）设计，确保在不同体位的呼吸都及其的顺畅，并在强水流的情况下，最大限度的避免了自动排气。独特的管路设计使我们在潜水中不会因为管子的拉扯而造成咬肌疲劳。</span>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#FF9900;line-height:1.5;\">价格：990元</span> \r\n</p>', 'uploadfiles/equip/equip_QtVY1450403469561.jpg', 'Mares Octopus INSTINCT', '横向排水设置排水导向口\r\n避免排气起泡影响视野', 'ST01', 'ET07', null, '990', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', '');
