INSERT INTO `dive_equip` VALUES ('ba63ea6d-e8cc-41dc-b858-fe49aad2bd0a', '2015-12-20 00:35:55', 'EB07', '<p>\r\n	<strong><strong><span style=\"font-family:\'Microsoft YaHei\';font-size:18px;color:#337FE5;\">Scubapro&nbsp;</span></strong><span style=\"font-family:\'Microsoft YaHei\';font-size:18px;color:#337FE5;\"></span><span style=\"font-family:\'Microsoft YaHei\';font-size:18px;color:#337FE5;\">MK25 EVO(YOKE)</span></strong> \r\n</p>\r\n<img src=\"/attached/image/2015/12/20/b7efbc3a-147a-4a99-9f35-d88a675ecd67.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 一级头分YOKE和DIN&nbsp;</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 新型XTIS（扩展隔温系统）结构，使调节器与环境温度隔绝开，提高了冷水中的抗冻性，避免低温环境影响</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 活塞尺寸使得调节器具有更高的吸气灵敏度，在轻轻吸气时，超快响应使用者呼吸</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 在主体附加散热片，提供额外的热交换功能</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 现代外观设计</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 关键的隔温系统组件以蓝色展示</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#FF9900;\">价格：2100元</span>\r\n</p>', 'uploadfiles/equip/equip_Xd3U1450542955492.jpg', 'SCUBAPRO MK25 EVO(YOKE)', '新型扩展隔温系统\r\n提高抗冻性', 'ST01', 'ET07', null, '2100', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', '');
INSERT INTO `dive_equip` VALUES ('c1b86221-f00a-4d1e-b239-272a50839164', '2015-12-19 14:48:12', 'EB06', '<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>SP GLOVES 2MM 女式潜水手套</strong></span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 2mm潜水物料手套</span><br />\r\n<p>\r\n	<img src=\"/attached/image/2015/12/19/d0004c20-ed5a-4263-93c7-b435db2ee761.png\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">．较短的手腕位置能让你轻松穿戴你的潜水计算机表；</span> \r\n</p>\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">．设有手套扣子，防止手套意外遗失；</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> ．于掌心及指尖位加有Amara人造皮革，大大提高保护力；</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> ．特设魔术贴于手腕位置，容易调较松紧度，大大提升穿戴时的舒适感；</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> ．手套背位印有GULL字样及图案，时尚美观；</span><br />\r\n<img src=\"/attached/image/2015/12/19/cf663092-8645-4821-9c40-0875aed4c679.png\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 尺碼： S(17cm)；M(19cm)；L(21cm)；</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 掌長：由食指底部拉一條對角線至小指至手腕處的2/3位置；</span><br />\r\n<div>\r\n	价格：320元\r\n</div>', 'uploadfiles/equip/equip_c6zr1450507691752.jpg', 'GULL SP GLOVES 2MM 女式潜水手套', '轻松穿戴潜水表\r\n掌心及指尖位加有人造皮革', 'ST01', 'ET05', null, '320', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', 'CL001,CL008,CL011', 'SZ003,SZ004,SZ005');
INSERT INTO `dive_equip` VALUES ('c1f7000e-d01a-46d1-a525-a5dc4b75a679', '2015-12-18 10:12:24', 'EB01', '<p>\r\n	<span style=\"font-size:18px;color:#337FE5;font-family:\'Microsoft YaHei\';\"><strong>Mares PRESTIGE 12S</strong></span><br />\r\n<span style=\"font-size:14px;color:#999999;\">Code:416156</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#999999;\">新的简约设计和轻盈的二级调节器</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">在一级调节器的选择上，使用了最为小巧的隔膜式调节器MR12S。</span><br />\r\n<img src=\"/attached/image/2015/12/18/e447f0a8-48ba-424a-a894-0d64508417b9.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/18/a7d15784-35ea-407a-b37f-73b7e3691486.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<br />\r\n<span style=\"font-size:14px;color:#999999;\">一级调节器类型：平衡膜设计</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">二级调节器类型：顺流式供气阀</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">一级调节器重量(g):674</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">二级调节器重量(g):205</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">总重量(含管子g):1058</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">2个HP 7/16\"&nbsp;</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">4个LP 3/8\" 其中有1个DFC接口</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#FF9900;\">价格：2200元</span> \r\n</p>', 'uploadfiles/equip/equip_hDEW1450404743850.jpg', 'Mares PRESTIGE 12S', '设计简约\r\n结构轻盈', 'ST01', 'ET07', null, '2200', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', '');
INSERT INTO `dive_equip` VALUES ('c22f4402-71e6-4523-b7e2-1530743f90ff', '2015-12-20 14:28:50', 'EB02', '<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>Poseidon</strong></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>BLACK LINE MISSION SUIT 1.5MM</strong></span>\r\n</p>\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 军用级性能</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 颜色：黑色</span><br />\r\n<img src=\"/attached/image/2015/12/20/97e75ebe-de23-4948-81dc-3151e572a71d.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/20/a8fd1677-3c79-463d-b74b-c4b42625586a.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 弹性氯丁胶 舒适 超弹</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 1.5mm更轻薄 灵活 适合暖水</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 手肘、膝盖和臀部采用了防磨布和压纹材料，不易变形，活动方便。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 腰部采用拉伸材料，弹力和保暖性好</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 内衬里有金属纤维，传热保暖</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">正面拉链，方便特殊潜水需要，容易穿脱。</span>\r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/12/20/f4027646-a3ef-47e4-9ef9-4d72d21f70e4.jpg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"color:#999999;font-family:\'Microsoft YaHei\';line-height:1.5;\">Poseidon(海神，又名波塞冬) 是一家来自瑞典，具有58年历史的潜水器材生产商，其产品素以品质闻名于世，深受专业潜水人士的喜爱。各国的军事单位无不采用她的产品，甚至美国太空总署也是合作伙伴。是高品质、高可靠性的代名词。</span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#999999;font-family:\'Microsoft YaHei\';line-height:1.5;\"><br />\r\n</span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#999999;font-family:\'Microsoft YaHei\';line-height:1.5;\">\r\n	<p>\r\n		<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">价格：1500元</span>\r\n	</p>\r\n</span>\r\n</p>', 'uploadfiles/equip/equip_uDi31450592929819.jpg', 'Poseidon BLACK LINE MISSION SUIT 1.5MM', '军用级品质\r\n超弹氯丁胶', 'ST01', 'ET03', null, '1500', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', null, null);
INSERT INTO `dive_equip` VALUES ('c2778b9d-18d6-4a93-889c-9d53be5255a8', '2015-12-19 00:14:46', 'EB01', '<p>\r\n	<span style=\"color:#337FE5;font-size:18px;line-height:1.5;font-family:\'Microsoft YaHei\';\"><strong>Mares Writing Slate&nbsp;</strong></span><br />\r\n<span style=\"color:#999999;font-size:14px;line-height:1.5;\">Code: 415719</span><br />\r\n<span style=\"color:#999999;font-size:14px;line-height:1.5;\">水下手写板，记录水下所见</span><br />\r\n<span style=\"color:#999999;font-size:14px;line-height:1.5;\">与潜伴跟顺畅沟通</span><br />\r\n<span style=\"color:#999999;font-size:14px;line-height:1.5;\">塑料挂扣可挂在BC上</span><br />\r\n<span style=\"color:#999999;font-size:14px;line-height:1.5;\"></span><img src=\"/attached/image/2015/12/19/0ba08d81-8dc9-4b3e-9461-05338a155d8c.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"color:#FF9900;font-size:14px;line-height:1.5;\">价格：100元</span> \r\n</p>', 'uploadfiles/equip/equip_5LsD1450455286096.jpg', 'Mares Writing Slate', '水下手写板\r\n可挂在BC上', 'ST01', 'ET15', null, '100', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', '');
