INSERT INTO `dive_equip` VALUES ('c3681f04-3fa2-4313-b4be-ef4534073594', '2015-12-20 01:46:26', 'EB07', '<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>Scubapro S</strong></span><span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>olo</strong></span> \r\n</p>\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 尺寸：均码</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 材料：特种钢化玻璃和硅胶</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 颜色：黑（现亚洲区只有黑）</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 适用：浮潜及水肺潜水</span><br />\r\n<img src=\"/attached/image/2016/06/17/bf43f6c7-53c0-4b2c-82f5-d3eaef4607fc.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 特点：</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 1、透澈的水晶硅胶裙边,舒适完美的紧贴脸庞.</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 2、低水容积样式设计,轻易的排除内部积水.</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 3、回火锻造强化玻璃镜片(符合美国潜水面镜镜片质量标准).</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#FF9900;\">价格：600元</span> \r\n</p>', 'uploadfiles/equip/equip_uJgr1450547186060.jpg', 'Scubapro solo', '特种钢化玻璃和硅胶', 'ST01', 'ET01', null, '600', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', 'CL001', 'SZ040');
INSERT INTO `dive_equip` VALUES ('c37913ee-38e1-4b52-afad-71a59c31bbdf', '2015-12-20 12:58:59', 'EB10', '<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>Water Pro</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>抓绒防晒衣</strong></span> \r\n</p>\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">产造纤维弹性布并加入钛金属材质制成, 提供绝佳抗UV完善保护性及防水性品：</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 抗UV的绝佳好帮手,可保护水上运动者减少曝晒于紫外线之伤害。使用10oz 的Nylon及人</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> FLEX 等级, 提供 更佳延展度, 更高效能防晒.</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 专业防晒等级高达 50 UPF UV rate Lycra material.</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 完美的照顾您的健康, 减少晒伤机会与皮肤癌产生.</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 您只需要擦防晒油在未保护到的部份即可.</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 莱卡材质的舒适度, 给您更贴合, 更容易穿脱, 更无束缚延伸P.</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 快干的效果. 不会让您在岸上因失温而受凉, 衣服快干, 不容易黏在身上.</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 让你更愉快享受每一次潜水或是水上活动.</span><br />\r\n<p>\r\n	<img src=\"/attached/image/2016/06/13/4917fe0e-ec48-483a-9764-39b11bb2d304.jpg\" alt=\"\" /><img src=\"/attached/image/2016/06/13/db0a9dd3-1a51-4df3-b12d-be5ce5314c72.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"color:#FF9900;\">价格：460元</span> \r\n</p>', 'uploadfiles/equip/equip_4mmW1450587538893.jpg', 'Water Pro 抓绒防晒衣', '抓绒提供更好\r\n保暖效果和舒适度\r\n', 'ST01', 'ET03', null, '460', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', 'CL001,CL008', 'SZ003,SZ004,SZ005,SZ006,SZ007');
INSERT INTO `dive_equip` VALUES ('c54ecc38-4a6b-4608-9a73-175c2718b5dc', '2016-01-25 14:44:06', 'EB03', '<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:24px;color:#337FE5;line-height:1.5;\"><strong>Aqua Lung&nbsp;</strong></span><span style=\"font-family:\'Microsoft YaHei\';font-size:24px;color:#337FE5;line-height:1.5;\"><strong>Malibu</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\">Aqualung Malibu 是一款流线型单镜片设计的面镜。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\">面镜装配了安全的高强度钢化玻璃，虽然它的内容积很小，但却为潜水员提供了宽阔的上下视野，柔软的裙边提供了极好的舒适性。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\">无镜框、低容积、单镜片设计</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\">柔软的硅胶裙边提供了极好的舒适性</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\">更大的上下视野镜片</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\">安全的钢化玻璃镜片</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\">面镜头戴调整简单舒适</span><span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"></span> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/01/25/0a95158b-0fde-403b-9671-2848d0c40072.jpg\" alt=\"\" /><img src=\"/attached/image/2016/01/25/b08b52d6-81a8-430d-a384-a5bde6cd12b3.jpg\" alt=\"\" /> \r\n</p>\r\n<span style=\"color:#FF9900;\">价格：1800元</span>', 'uploadfiles/equip/equip_q3Z01453704246470.jpg', 'Aqua Lung Malibu', '无镜框 低容积 \r\n单镜片设计', 'ST01', 'ET01', null, '1800', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', 'CL001,CL002', '');
