INSERT INTO `dive_equip` VALUES ('cae62681-7890-47a7-8d86-56812ad9c8cc', '2015-10-25 19:40:46', 'EB01', '<p>\r\n	<span style=\"font-size:18px;color:#337FE5;font-family:\'Microsoft YaHei\';\"><strong>Mares FUSION 52X</strong></span><br />\r\n<span style=\"font-size:14px;color:#999999;\">Code:416233</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#999999;\">FUSION 二级头</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">全金属技术使二级头能适应各种极端寒冷的潜水环境。</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">MR52X一级头</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">\"X\"系统的坚固性增加到6倍。</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">水平管路设计：主调节器和备用调节器并排水平安装。</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">NCC自然对流系统：大大提高在冷水环境中的热交换。</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">双DFC动态流量控制系统：确保在任何深度都呼吸顺畅。</span> \r\n</p>\r\n<div>\r\n	<br />\r\n</div>\r\n<span style=\"font-size:14px;color:#999999;\"></span> \r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/10/25/2f537f10-4ba0-4b7d-81d3-1057798c005a.jpg\" alt=\"\" /><span style=\"font-size:16px;\"></span> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/10/25/8d32f3eb-4830-40f2-8abc-3127db191c77.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"color:#FF9900;font-size:14px;line-height:1.5;\">价格：3900元</span>\r\n</p>', 'uploadfiles/equip/equip_rL7Y1445773245666.jpg', 'Mares FUSION 52X', 'NCC自然对流系统\r\n双DFC动态流量控制系统', 'ST01', 'ET07', null, '3900', null, '6a040c09-5758-41f8-8910-bf556692e824', '', '');
INSERT INTO `dive_equip` VALUES ('cbca0bba-0006-462a-bd59-1cca28435292', '2016-01-25 15:30:21', 'EB09', '<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:24px;color:#337FE5;line-height:1.5;\"><strong>TUSA WING BCJ-8000</strong></span> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/01/25/d07dd8f4-303f-44ac-8afc-19ebdc6a5b87.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\">特色：</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 獨立式超穩定背架系統(Independent Harness System)，背架與氣瓶接觸面加裝兩條防滑軌道，可防止氣瓶滾動移位。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 背架附上下兩條氣瓶固定帶，下方固定帶增加方型氣瓶固定座，使氣瓶固定更加穩固。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 3D立體式軟質背墊，增加潛水員穿戴舒適性。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 配備新設計的胸扣與安全哨一體成形，胸帶附硬質滑軌可上下調整高度，增加舒適度及安全性。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 配備整合式配重系統，附安全保險插扣，全新設計的安全保險插扣只需轉90°即可輕鬆將配重袋卸除，有別以往拉扯式的設計，增加潛水安全性。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 位於後腰部兩側之配重口袋為可拆卸式，配重口袋可移至氣瓶固定帶上，增加實用性及舒適度並提供最佳之配重平衡感。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 500D Cordura?尼龍材質的馬蹄形後背式氣囊，氣囊材質內襯PU背膠，提供高強度及耐用性。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 氣囊主體附具彈性的鬆緊帶，可限制氣囊體積，增加在狹窄空間潛水的安全性。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 附可拆卸的網型採集口袋，共4個位置可供固定，並且可摺疊收納以應付潛水的不同需求。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 左右兩側整合式配重系統上方須配備刀具固定座孔，刀具可直接固定於BC上，方便使用且提升安全性。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 配備2個不鏽鋼和2個塑鋼材質的D型環，方便固定各種潛水配件。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 後背肩部兩側及左右腰部內側共4個防爆閥，符合人體工學，任何姿勢角度都可迅速使BC洩氣。</span><br />\r\n<img src=\"/attached/image/2016/01/25/296aac48-3842-4e5f-b5e0-7947e2fe4a00.jpg\" alt=\"\" /><img src=\"/attached/image/2016/01/25/776f57c3-dfad-4375-b9c4-fe9610d57457.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> 尺寸對照：&nbsp;</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> S：157~170 (身高) 54~70 (體重kg)</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> M：168~178 (身高) 70~88 (體重kg)</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> L：178~193 (身高) 88~107(體重kg)</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#999999;line-height:1.5;\"> XL：193+ (身高) 107+(體重kg)</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:14px;color:#FF9900;line-height:1.5;\">价格：4000元</span>\r\n</p>', 'uploadfiles/equip/equip_y6Ep1453707021345.jpg', 'TUSA WING BCJ-8000', '獨立式超穩定背架系統\r\n增加潛水員穿戴舒適性', 'ST01', 'ET08', null, '4000', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', 'SZ003,SZ004,SZ005,SZ006');
INSERT INTO `dive_equip` VALUES ('ce650883-403a-419d-b399-fa4d40612762', '2015-10-25 19:54:44', 'EB02', '<span style=\"font-size:18px;font-family:\'Microsoft YaHei\';color:#337FE5;\"><strong>Poseidon 3D</strong></span><br />\r\n<p>\r\n	<span style=\"font-size:16px;font-family:\'Microsoft YaHei\';\"><span style=\"font-size:12px;color:#999999;\"> 可配光学</span><span style=\"font-family:\'Microsoft YaHei\';font-size:12px;color:#999999;\">镜片（</span><span style=\"color:#999999;font-family:\'Microsoft YaHei\';font-size:12px;line-height:19px;background-color:#FFFFFF;\">-1.5 至 -­6.0</span><span style=\"font-family:Microsoft YaHei;\"></span><span style=\"font-family:\'Microsoft YaHei\';font-size:12px;color:#999999;\">）</span></span> \r\n</p>\r\n<span style=\"font-size:12px;font-family:\'Microsoft YaHei\';color:#999999;\"> 超轻聚合物外框及坚固的镜框</span><br />\r\n<p>\r\n	<span style=\"font-size:12px;font-family:\'Microsoft YaHei\';color:#999999;\">柔软的硅胶裙边</span> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/11/12/8e9a2ecc-adcb-488b-911a-4ec5028f1567.png\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/11/12/f7e2ead8-2251-4dd9-9942-f004a93c97d3.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/11/12/f5504623-5d00-481f-abfe-55bed5be8405.png\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/12/17/fc99554e-764f-49ad-9159-4ac3183149c4.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:12px;line-height:24px;color:#999999;\">价格：460元</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:12px;color:#999999;\">Poseidon(海神，又名波塞冬) 是一家来自瑞典，具有58年历史的潜水器材生产商，其产品素以品质闻名于世，深受专业潜水人士的喜爱。各国的军事单位无不采用她的产品，甚至美国太空总署也是合作伙伴。是高品质、高可靠性的代名词。</span> \r\n</p>', 'uploadfiles/equip/equip_GCIz1447334970370.jpg', 'Poseidon 3D', '超轻聚合物外框\r\n柔软的硅胶裙边', 'ST01', 'ET01', null, '460', null, '6a040c09-5758-41f8-8910-bf556692e824', null, null);
INSERT INTO `dive_equip` VALUES ('d0217351-7336-4190-9b26-cd6ff0e64da3', '2015-10-25 19:52:27', 'EB02', '<p>\r\n	<img src=\"/attached/image/2015/12/17/5563b8ef-bab2-4b06-95bd-31927a9d81c9.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:medium;line-height:24px;\">价格：780元</span>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	Poseidon(海神，又名波塞冬) 是一家来自瑞典，具有58年历史的潜水器材生产商，其产品素以品质闻名于世，深受专业潜水人士的喜爱。各国的军事单位无不采用她的产品，甚至美国太空总署也是合作伙伴。是高品质、高可靠性的代名词。\r\n</p>', 'uploadfiles/equip/equip_iOcp1445773947003.jpg', 'Poseidon Diving Fin', '自由潜和浮潜的理想选择\r\n非常好的推进力', 'ST01', 'ET06', null, '780', null, '6a040c09-5758-41f8-8910-bf556692e824', null, null);
INSERT INTO `dive_equip` VALUES ('d097df02-e217-4ca7-8fb0-c52b8f00c802', '2015-12-19 03:05:28', 'EB04', '<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\">Aquatics SV2</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">Atomic Aquatics的SV系列呼吸管，賣點就是SV這兩個字，它是Scupper Valve，設計原理來自船用的單向排水閥。在呼吸管裡海水只能單向流動，只會排出，不會在同一位置被吸入。</span> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/12/19/cb28bd80-65e4-4264-9b98-4b619948fc7a.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">所有的Atomic Aquatics的SV系列呼吸管，在真正投產前，都會採集潛水夫的回饋意見，根據大家的要求，把呼吸管做得儘量體積小，重量輕而且富有彈性。所以SV系列的呼吸管，都是體積小而且非常符合流體力學的，在水中的阻力是盡可能的小。</span> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/12/19/3e84b012-67b7-46a8-a594-f17383c2ace5.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/19/0d9ca3c3-861d-42f6-8221-d60b89407e10.jpg\" alt=\"\" /> \r\n</p>\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">Atomic Aquatics的SV系列呼吸管的設計是炫的，遠比市面其他品牌的產品要更吸引他人的眼球。</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 最重要的一點，Atomic Aquatics的SV系列呼吸管採用的咬嘴都是跟其旗下的二級頭的咬嘴一樣的，非常舒服。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#FF9900;\">价格：520元</span>\r\n</p>', 'uploadfiles/equip/equip_s2681450465527625.jpg', 'Atomic Aquatics SV2', ' 半干式呼吸管', 'ST01', 'ET02', null, '520', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', 'CL002,CL015,CL020,CL014,CL017,CL021,CL022', '');
INSERT INTO `dive_equip` VALUES ('d1d4c7d5-e221-4e2b-8751-e16294335aaf', '2015-10-25 16:05:30', 'EB01', '<p>\r\n	<span style=\"font-size:16px;font-family:\'Microsoft YaHei\';\"><span style=\"font-size:18px;line-height:1.5;color:#337FE5;font-family:\'Microsoft YaHei\';\"><strong>Mares DUAL呼吸管</strong></span><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';line-height:1.5;color:#999999;\"><span style=\"font-family:\'Microsoft YaHei\';font-size:14px;line-height:1.5;color:#999999;\">Code：421454</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;font-family:\'Microsoft YaHei\';\"><span style=\"font-size:14px;line-height:1.5;color:#999999;\"></span><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';line-height:1.5;color:#999999;\">自由潜软橡胶湿管 柔软舒适</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';line-height:1.5;color:#999999;\"> 流体力学设计 适合长时间使用</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';line-height:1.5;color:#999999;\"> \"D\"形设计减轻了对太阳穴的压力</span> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/11/12/19ead213-b39f-45ea-ac7e-cd815927fcc7.png\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/11/12/12d72b2f-328d-4ae1-be17-3603e83cf05f.png\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/11/12/7d3a6616-f38a-4082-a617-42d8da27db28.png\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;line-height:1.5;color:#FF9900;\">价格：180元</span> \r\n</p>', 'uploadfiles/equip/equip_m6sO1445760330352.jpg', 'Mares DUAL', '自由潜软橡胶湿管 柔软舒适\r\n流体力学设计 适合长时间使用', 'ST01', 'ET02', null, '180', null, '6a040c09-5758-41f8-8910-bf556692e824', 'CL001,CL007,CL034', '');
INSERT INTO `dive_equip` VALUES ('d1ea6d49-b84d-4e37-87d9-df48ae00252a', '2015-12-19 14:00:53', 'EB06', '<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>Mask bandcover wide 面镜保护带</strong></span> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/12/19/91e82b66-0e53-40b1-85de-182f0a6a8dcf.jpg\" alt=\"\" /> \r\n</p>\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">mm厚潜水物料面镜护发带，印有GULL字样图案，时尚美观；</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 专为展发潜水员而设的面镜垫，面镜垫中央设一开口，避免压着头发引起不适；</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 面镜配戴更容易，避免拉扯或纠缠头发；</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 伸缩魔术贴设计，便于调节面镜护发带；</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 双面双色设计，让你轻松配搭不同颜色之装备，有型时尚；</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#FF9900;\">价格：150元</span>\r\n</p>', 'uploadfiles/equip/equip_mSNO1450504853405.jpg', 'Gull mask bandcover wide 面镜保护带', '', 'ST01', 'ET01', null, '150', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', 'CL001,CL002,CL011,CL005,CL008,CL003', 'SZ040');
