INSERT INTO `dive_equip` VALUES ('d738f96c-0259-45d0-909f-f0cd5b5cccc4', '2015-12-18 23:47:50', 'EB01', '<p>\r\n	<span style=\"font-size:18px;color:#337FE5;line-height:1.5;font-family:\'Microsoft YaHei\';\"><strong>Mares Gloves</strong></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;color:#337FE5;line-height:1.5;font-family:\'Microsoft YaHei\';\"><strong>AMARA 2mm</strong></span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">Code: 412702</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#999999;line-height:1.5;\">防护保暖</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\"> 氯丁橡胶材质制作的手套，尼龙搭扣皮带固定手套</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\"> 适合水肺潜水、风筝冲浪、冲浪、皮划艇、滑水&nbsp;</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\"> 事实上几乎所有户外活动需要灵活性和抓地力都适用</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\"> 量度的方法是用软尺从手掌宽的量法</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\"> 即从\"生命线及智慧线的交会点.横向量至小指侧\"</span><br />\r\n<img src=\"/attached/image/2015/12/18/946e6f9d-4ae5-47eb-94a8-e9119541e5ba.jpg\" alt=\"\" /><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\"> 产品详情</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">Sizes: XXS-XS-S-M-L-XL</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#FF9900;line-height:1.5;\">价格：220元</span> \r\n</p>', 'uploadfiles/equip/equip_Isjj1450453669621.jpg', 'Mares Gloves AMARA 2mm', '2mm氯丁橡胶\r\n适合几乎所有水上运动', 'ST01', 'ET05', null, '220', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', 'SZ001,SZ002,SZ003,SZ004,SZ005,SZ006');
