INSERT INTO `dive_equip` VALUES ('d8bc5fdd-5227-4e13-9243-ad71a04c39c8', '2015-12-20 12:46:41', 'EB10', '<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>Water Pro</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>Wetsuit 3.5mm 潜水衣服</strong></span> \r\n</p>\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 杜邦Neoprene超柔软发泡潜水布,衣服厚度不因使用时间久,或水下深度过深变薄而减低保暖度,在高海拔从事活动尤其适合.</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 商品尺寸：XXS,XS,S,M,L</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 商品颜色：黑粉 黑蓝 黑红</span><br />\r\n<img src=\"/attached/image/2015/12/20/dbc3abdb-8b75-4e2b-b507-e8fe40cc6944.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/20/060e1606-8323-439b-ac69-e1399f530d07.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/20/52abcdba-89dc-45c3-93d2-9d7946c13066.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> ◎3.5MM</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> ◎超耐磨强韧的内外材质不易变形</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> ◎颈部.手腕.脚踝采用无接缝半干式防寒衣的水密设计,贴合性佳,提供最佳的防进水保暖效果</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> ◎整件式3D腰身剪裁设计 全弹性材质,膝盖则有加强防护</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> ◎使用银色YKK塑钢拉链,独特的设计当拉上时能更密合手脚背部一共五条拉链.</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> ◎适合潜水专业人士使用,属于超专业顶级防寒衣,上衣附裤裆黏贴，无须担心衣服在水中因浮力会往上的问题。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> ◎ 冬潜不可缺少的专业防寒衣</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 保暖效果棒又不影响动作，无须担心长时间泡水得感冒而烦恼了.</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 水上活动(游泳,冲浪,风浪板,风帆,摩托车,独木舟,,,)专用防寒衣最佳选择.</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 主要避免失温且保护身体在水中</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 活动时不被刮伤、刺伤或擦伤。</span><br />\r\n<span style=\"color:#FF9900;\">价格：1200元</span>', 'uploadfiles/equip/equip_uZWA1450586801192.jpg', 'Water Pro Wetsuit 3.5mm 潜水衣服', '3.5mm男女款潜水衣\r\n半干式防寒衣的水密设计', 'ST01', 'ET03', null, '1200', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', 'CL015,CL023,CL020', 'SZ002,SZ003,SZ004,SZ005,SZ006,SZ007');
INSERT INTO `dive_equip` VALUES ('da2c716f-9d42-4e59-bd8c-9e6d658140e0', '2016-01-25 13:58:45', 'EB03', '<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>Aqua Lung</strong></span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>Shot FX</strong></span><br />\r\n<img src=\"/attached/image/2016/02/15/53471c99-3452-4a88-afde-e20358c97aed.jpg\" alt=\"\" /><img src=\"/attached/image/2016/02/15/0c7c6185-a85b-446a-8729-639317911c53.jpg\" alt=\"\" /><span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"></span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> Shot FX 是结合了最新科技和最新材料的高效能蛙鞋，它采用了 SlingShot 和 HotShot 的设计理念并在其基础进行了发展。</span><br />\r\n<br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 蛙鞋的动力区按常规设计在鞋套的前部，但在结合了“V 型推进区”后使潜水员即使用更少的力也能有更大的动力。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 新型弹簧鞋带配合带扣让你感觉更加舒服</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 鞋套上配备了“动力传动区”，无需刻意伸展足部也能获得强劲的推动力</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 鞋面使用了新型材料以增加弹力并增进推动力</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 弹性鞋面加入了“汤勺效应”可使每一次向下踢动更具效率</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 鞋套设计更贴合潜水员足型，不会浪费任何踢动所产生的动力</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 鞋套完全按照女性潜水员舒适性的要求进行设计</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> Shot FX 的鞋面比一般的蛙鞋稍微短一些，并更柔软一些，方便踢动</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#FF9900;\">价格：2200元</span>\r\n</p>', 'uploadfiles/equip/equip_mUyp1453701525346.jpg', 'Aqua Lung Shot FX', '开口式\r\n短小偏柔 适合多数人', 'ST01', 'ET06', null, '2200', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', 'CL001,CL010', 'SZ002,SZ003,SZ009,SZ006');
INSERT INTO `dive_equip` VALUES ('dad70ce4-cb5a-4d03-9ec6-2f466037a60e', '2015-12-20 12:56:04', 'EB10', '<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>WATER PRO</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>浮潜水肺两用&nbsp;</strong></span><span style=\"line-height:1.5;font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>开放式脚蹼</strong></span> \r\n</p>\r\n<img src=\"/attached/image/2016/06/13/257e3332-cc1f-4f7b-88bd-f29eb5d0ba30.jpg\" alt=\"\" /><img src=\"/attached/image/2016/06/13/5c68c886-4f40-49f6-886b-8f8c0af6c6d9.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 可选颜色：黑灰色，粉红色，荧光黄色，蓝色</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 可选码数：S码：36-41码，L码：42-47码</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 脚蹼长度：S码：51cm，L码：57cm（测量有1-2cm误差）</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 开放式脚蹼</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 可搭配潜水靴穿</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#FF9900;\">价格：480元</span> \r\n</p>', 'uploadfiles/equip/equip_RGw81450587364057.jpg', 'WATER PRO 浮潜水肺两用 开放式脚蹼', '开放式脚蹼\r\n可搭配潜水靴穿', 'ST01', 'ET06', null, '480', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', 'CL003,CL006,CL008,CL011', 'SZ003,SZ005');
INSERT INTO `dive_equip` VALUES ('db4ee654-e65c-4ffb-9760-0d15ada83ce6', '2015-12-20 00:40:05', 'EB07', '<p>\r\n	<strong><span style=\"font-family:\'Microsoft YaHei\';font-size:18px;color:#337FE5;\">Scubapro Nova Light 230</span></strong> \r\n</p>\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 中等尺寸的高亮度 LED 潜水灯，电池使用寿命可长达 14-18 小时</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 休闲潜水的最佳选择，也可作为技术潜水时的备用潜水灯</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 结实的铝制灯身，经久耐用</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 使用 3 节 2 号碱性电池</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 光照亮度：230 流明</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 灯泡寿命长达 100,000 小时</span><br />\r\n<p>\r\n	<img src=\"/attached/image/2015/12/20/dc2ed67f-738a-4ea4-8fc5-6c9938391951.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">参数</span> \r\n</p>\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">功率（瓦）：Hi-LED</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 电池寿命：14-18 小时</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 电池数：3</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 电池型号：2 号（C batteries）</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 能否充电：否</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 灯泡类型：LED</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 尺寸：193 mm 长 / 43 mm 直径</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 重量（不含电池）：109g</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 最大潜水深度：120 米 / 393 英尺</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 材质：铝</span>\r\n</p>\r\