INSERT INTO `dive_equip` VALUES ('e149ddd9-0308-4d46-b036-879462a37313', '2015-10-25 19:55:42', 'EB02', '<p>\r\n	<span style=\"font-size:18px;font-family:\'Microsoft YaHei\';color:#337FE5;\"><strong>Poseidon Black Line Mask</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;font-family:\'Microsoft YaHei\';color:#999999;\">轻微向下的角度提供前所未有的视野</span> \r\n</p>\r\n<span style=\"font-size:16px;font-family:\'Microsoft YaHei\';color:#999999;\"> 时尚的外表也会让你与众不同</span><br />\r\n<p>\r\n	<span style=\"font-size:16px;font-family:\'Microsoft YaHei\';color:#999999;\"> 独特且轻量的框架设计</span> \r\n</p>\r\n<span style=\"font-size:16px;font-family:\'Microsoft YaHei\';color:#999999;\"> 适合大多数人脸类型</span><br />\r\n<p>\r\n	<span style=\"font-size:16px;font-family:\'Microsoft YaHei\';color:#999999;\">低容积使清水更容易</span> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/10/25/8f64cf79-e900-4387-a76d-1356d490bd31.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/10/25/1b3b1ba9-57d7-4e57-bf5e-ef07f18d03d4.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:medium;line-height:24px;color:#999999;\">价格：760元</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"color:#999999;\">Poseidon(海神，又名波塞冬) 是一家来自瑞典，具有58年历史的潜水器材生产商，其产品素以品质闻名于世，深受专业潜水人士的喜爱。各国的军事单位无不采用她的产品，甚至美国太空总署也是合作伙伴。是高品质、高可靠性的代名词。</span>\r\n</p>', 'uploadfiles/equip/equip_246u1445774141672.jpg', 'Poseidon Black Line', '前所未有的视野\r\n低容积使排水更容易', 'ST01', 'ET01', null, '760', null, '6a040c09-5758-41f8-8910-bf556692e824', null, null);
INSERT INTO `dive_equip` VALUES ('e1b13996-a5be-47d0-acec-8870e7545408', '2015-12-19 03:23:49', 'EB03', '<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>Aqualung Legend LX ACD Twilight</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">潜水呼吸调节器</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">Aqua Lung 屡屡获奖的优秀产品</span> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/12/19/d6c9dfd4-e2dc-47db-893f-e761a47565f0.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">这款产品，虽然至今已经是面市多年，但是，作为Aqua Lung 的顶级系列产品之一，每年都会有最新的Aqua Lung 的技术融入进去的。这个系列的产品，基本上是集中了Aqua Lung 公司现有的各种先进技术了。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 主要技术特点：</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 一级头采用了ACD专利技术，防止一级头意外进水。确保一级头内部的润滑畅顺，减少保养次数。而且能更安全地使用高氧。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 一级头采用了最新的ACD平衡隔膜式设计，有独立的完全干燥的舱室，能完全与环境隔离，所以性能稳定、出色。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">二级头采用平衡式设计，水下呼吸很轻松。&nbsp;</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">所有的中压出气口都应用了Aqua Lung 的AIR TURBO SYSTEM，气流更充足，更畅顺。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">VAS（VENTURI ADJUSTMENT SWITCH），出气量调节装置，既能防止意外Free Float 也能确保水下呼吸畅顺。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">Yoke 头接口能承受3300PSI/232Bar 最大压力，而Din头能承受高达4350PSI/300Bar 的最大压力。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">一级头有2个高压出气口和4个中压出气口。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">使用了专利设计的Comfo-Bite咬嘴。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">原厂设置就能满足使用40%高氧的要求。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">金属表面有橡胶涂层，能更好保护金属的表面。</span><span style=\"line-height:1.5;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#FF9900;\">价格：5500元</span>\r\n</p>', 'uploadfiles/equip/equip_lHfJ1450466628552.jpg', 'Aqualung Legend LX ACD Twilight', '原厂便满足40%高氧要求\r\n金属表面有保护性橡胶涂层', 'ST01', 'ET07', null, '5500', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', '');
INSERT INTO `dive_equip` VALUES ('e1cc6f90-fe14-49d8-8064-c8edaf7ef1c4', '2015-12-20 13:06:08', 'EB10', '<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>WaterPro</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>防水双肩背包</strong></span> \r\n</p>\r\n<img src=\"/attached/image/2016/06/13/04101ed1-57fb-4d5c-911e-d827ddf39c57.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 颜色：银色，黄色</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 尺寸：平铺后 33cm*20*69（手工测量存在1-3cm误差属正常）</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 体积：35L</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 材质：PVC&nbsp;</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 适合场景：登山，游泳，沙滩，休闲运动等多种场合适用的</span><br />\r\n<img src=\"/attached/image/2016/06/13/c50b8bd0-b6f0-4212-95de-7b9a0209b82d.jpg\" alt=\"\" /><img src=\"/attached/image/2016/06/13/d13330dd-4907-4ad9-b04f-27985bded8e5.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 背包正面时尚LOGO，立体形状点缀。美观大方，让旅行多了份时尚</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 背包两侧设计了网袋材料小腰袋，不仅可以透气美丽大方，而且更加时尚，可以放相机等 ，使用更方便！</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 增加内部网袋，可放手机、数码相机 ，钱包等。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#FF9900;\">价格：720元</span> \r\n</p>', 'uploadfiles/equip/equip_EnpV1450587968245.jpg', 'WaterPro防水双肩背包', '35L 防水双肩背包\r\n实用 时尚', 'ST01', 'ET14', null, '720', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', 'CL003,CL006', 'SZ040');
INSERT INTO `dive_equip` VALUES ('e235638c-7d25-4ef6-9a9c-bcf708d23839', '2015-12-19 23:53:58', 'EB07', '<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong><strong>Scubapro Mask Strap Cover</strong></strong></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>潜水面镜带</strong></span> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/12/19/2ed40e88-034f-4c31-b865-2a1b14b0c288.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">用途：保护头发免受硅胶带的粘性扯动,特别适合女生使用，免除长发被面镜带拉扯。</span> \r\n</p>\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">黄色还可提高水下明显度，使BUDDY更易找到你。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 印制银色SCUBAPRO商标图案</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 可用于各种面镜</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> Color: 黑/蓝, 黑/黄，蓝/黄</span>\r\n</p>\r\n<p>\r\n	<span><span style=\"color:#FF9900;\">价格：120元</span></span>\r\n</p>', 'uploadfiles/equip/equip_D81t1450540437623.jpg', 'SCUBAPRO Mask Strap Cover', '', 'ST01', 'ET01', null, '120', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', 'CL001,CL006,CL008', '');
INSERT INTO `dive_equip` VALUES ('e2c8f853-81f8-40d2-9290-51c9737188b8', '2015-12-18 03:53:20', 'EB01', '<p>\r\n	<span style=\"font-size:18px;color:#337FE5;line-height:1.5;font-family:\'Microsoft YaHei\';\"><strong>Mares ABYSS 22</strong></span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">Code:416134</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">Mares调节器最深潜水深度功勋者</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">具有创纪录的性能</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">金属二级调节器及旁通管</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">专利设计的网格外罩</span><span style=\"font-size:14px;color:#999999;line-height:1.5;\"></span> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/12/18/0f81a3af-78d3-43e5-af77-d4f4b7708cbf.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<br />\r\n<span style=\"font-family:Microsoft YaHei;\"><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">Abyss 22 Mares最具时代意义的一款经典调节器，打破了所有的潜水深度记录，Abyss 金属的外壳，发挥了金属的优良热传导性能，旁通管的设计使呼入的气体直接通过“VIP”通道进入口中，避免因进入二级调节器腔体内的气体而出现结冰想象的发生。MR22的一级调节器，专属独家的1/2\"中压接头,使传输的气体更足呼气更顺畅。</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">一级调节器类型：平衡膜设计</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">二级调节器类型：顺流式供气阀</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">一级调节器重量(g):781</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">二级调节器重量(g):269</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">总重量(含管子g):1208</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">HP端口： 2 HP 7/16\"</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">LP端口： 3 LP 3/8\" 其中1个1/2\"DFC接口连接</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">80cm超柔软编织护套中压软管</span></span><span style=\"font-family:Microsoft YaHei;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#FF9900;font-size:14px;line-height:1.5;\">价格：2800元</span>\r\n</p>', 'uploadfiles/equip/equip_SJFE1450381999928.jpg', 'Mares ABYSS 22', 'Mares最深潜水深度功勋者\r\n专利设计的网格外罩', 'ST01', 'ET07', null, '2800', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '', '');
