INSERT INTO `dive_equip` VALUES ('e4f29c2d-636c-4965-afe2-e7fb2acefb3e', '2015-12-20 12:34:32', 'EB10', '<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>Water Pro</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;font-size:18px;\"><strong>RGP1 UPF50+防晒裤</strong></span> \r\n</p>\r\n<img src=\"/attached/image/2015/12/20/f1477d3a-b82a-4a26-b06a-60d801a0d41e.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 可以搭配 Water Pro 系列防寒衣穿着 (例如半干, 全干, 或是 PROFLIE, TEC等级穿着. ) 更显示您的专业程度</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> ﹡商品尺寸：XS,S,M,L,XL,XXL</span><img src=\"/attached/image/2015/12/20/d42dd901-6685-4ede-8f71-1381a6a19c30.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 产造纤维弹性布并加入钛金属材质制成, 提供绝佳抗UV完善保护性及防水性, 品：</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 抗UV的绝佳好帮手,可保护水上运动者减少曝晒于紫外线之伤害。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 专业防晒等级高达 50 UPF UV rate Lycra material。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 完美的照顾您的健康, 减少晒伤机会与皮肤癌产生。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 莱卡材质的舒适度, 给您更贴合, 更容易穿脱, 更无束缚延伸P。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 快干的效果. 不会让您在岸上因失温而受凉, 衣服快干, 不容易黏在身上。</span><br />\r\n<span style=\"color:#FF9900;\">价格：280元</span>', 'uploadfiles/equip/equip_Oimw1450586072459.jpg', 'Water Pro RGP1 UPF50+防晒裤', '纤维弹性布并加入钛金属\r\n绝佳抗UV完善保护性及防水性', 'ST01', 'ET03', null, '280', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', 'CL020,CL015', 'SZ002,SZ003,SZ004,SZ005,SZ006,SZ007');
