INSERT INTO `dive_equip` VALUES ('e8cea9cc-87b8-4f3b-b229-267c367858f1', '2015-12-19 15:52:10', 'EB06', '<h2>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#337FE5;\">Gull Super Mew XX FIN</span> \r\n</h2>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">Super Mew是GULL蛙鞋中的皇牌产品</span> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/12/19/9f2f7832-3830-4bd4-bfa0-3c68c1314869.png\" alt=\"\" /><img src=\"/attached/image/2015/12/19/5bf47770-c50c-4e34-8309-14b2b8e54a23.png\" alt=\"\" /> \r\n</p>\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">立体脚套可方便赤足穿着有助减低脚部磨损，亦可配合特制的Mew Boot，舒适贴服。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 脚踭位置采用防滑设计，於进行跨步式下水时可避免滑倒。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 另特别加高蛙鞋两侧的骨干及加长蹼面长度，大大提升踢动效能。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 蹼面末端呈曲线，可借助水流的力量提升动能。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> Super Mew以速度见称，并已在多项双蹼比赛中取得多个奖项</span><br />\r\n<img src=\"/attached/image/2015/12/19/f2aacb6e-0e7e-43ae-81f1-14075e274c7e.png\" alt=\"\" /><img src=\"/attached/image/2015/12/19/8a80d346-2bbc-4382-8e5f-6e0831746e37.png\" alt=\"\" /><br />\r\n<br />\r\n<br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 本体重量 （weight） ：</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 1,015g （S size）</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 1,127g （M size）</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 1,245g （L size</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#FF9900;\">价格：1400元</span>\r\n</p>', 'uploadfiles/equip/equip_liTy1450511529804.png', 'Gull Super Mew XX FIN', 'Super Mew以速度见称\r\n开口式', 'ST01', 'ET06', null, '1400', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', 'CL005,CL008,CL009,CL004,CL001,C