INSERT INTO `dive_equip` VALUES ('e958ef5a-3f2a-4ac1-b1a8-b36392b72d1b', '2015-12-18 11:16:50', 'EB01', '<p>\r\n	<span style=\"font-size:18px;color:#337FE5;line-height:1.5;font-family:\'Microsoft YaHei\';\"><strong>Mares AVANTI&nbsp;</strong></span><strong><span style=\"font-family:\'Microsoft YaHei\';font-size:18px;color:#337FE5;\">SUPERCHANNEL</span></strong> \r\n</p>\r\n<p>\r\n	<strong><span style=\"font-family:\'Microsoft YaHei\';font-size:18px;color:#337FE5;\">OH（无后跟式）</span></strong> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#999999;line-height:1.5;\">Code:410012</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">卓越的性能，更大的舒适度 三个超级导流口设计</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">最新导流推进技术</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">坚固耐用的构造</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">具备ABS快卸扣</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">Channel THrust:导流口推力技术可以通过导流槽引导水流，使其产生的推力增大 Upper&amp;Lower stabilizers：脚蹼叶板上的垂直加强肋在打水时具有稳定叶板的作用，避免左右摆动，使脚蹼具有较强的稳定性。</span><br />\r\n<img src=\"/attached/image/2015/12/18/38ec26c0-04d2-44b2-9698-3a0b672ef6c6.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/18/cb46b4d9-56c5-4750-ab29-59700669bd26.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/18/75361206-4ccd-465c-bff3-dd5156ce34b1.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">鞋肚类型：无后跟式鞋肚</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">材质：热塑性橡胶 聚丙烯</span><br />\r\n<span style=\"font-size:14px;color:#999999;line-height:1.5;\">叶板长度（size R）:37cm</span><br />\r\n<img src=\"/attached/image/2015/12/18/782e231a-1d1c-4e23-a474-25cbf14864cf.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#FF9900;line-height:1.5;\">价格：685元</span> \r\n</p>', 'uploadfiles/equip/equip_mfIy1450408609884.j