INSERT INTO `dive_equip` VALUES ('ee4ab7c4-2e65-433c-9f07-b4f7254246c4', '2015-12-19 23:18:44', 'EB07', '<p>\r\n	<strong><span style=\"font-family:\'Microsoft YaHei\';font-size:18px;color:#337FE5;\">Scubapro Everflex 3MM</span></strong> \r\n</p>\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 这款潜水衣创新使用了超弹性的EverFlex橡胶，能够提供无以伦比的舒适度，灵活度，与更加舒适的弹性。</span><br />\r\n<img src=\"/attached/image/2015/12/19/6db9b4e1-38e5-479d-8943-d722dcc9804f.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 此款防寒衣的伸缩度极大，能提供各种不同体型的运动员极度服贴的感受。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 此EverFlex系列将提供给您无可比拟的极度舒适。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 100% Ever-Flex橡胶布料</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 增加耐久度与舒适度，表布使用面双向盲缝(不会留印子)，内里使用单项盲缝。EverFlex缝合是几乎不透水的。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 直接切割的领口十分服贴脖子的线条(短脖子的也不会磨皮肤哦! )</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 背后的设计在拉链处提供了极佳的防水效果，而且能提供您的背部更佳的保护</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 直接贴合于布料上的护膝增加了抗磨的能力</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 金属的拉链头较坚固且好拉</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 手脚类似半干式防寒衣设计,更不容易从此处进水,给您全新的感受.</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">全新的I-SAFE贴合在手腕处提供充足的空间容纳潜水计算机表，为潜水计算机表提供了额外的保护</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">颜色：黑色（男式） &nbsp;蓝色（女式） &nbsp;粉色（女式）</span>\r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2015/12/19/9f442bb3-2720-4754-8e02-b78ec90e7f65.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/19/869f375a-c4c8-465e-bff1-064ef1061871.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"color:#FF9900;\">价格：2600元</span>\r\n</p>'