INSERT INTO `dive_equip` VALUES ('f4943f7d-b590-467d-ba82-eefb56002bd2', '2015-10-25 16:28:56', 'EB01', '<p>\r\n	<span style=\"font-size:18px;font-family:\'Microsoft YaHei\';line-height:1.5;color:#337FE5;\"><strong>Mares STAR Elite</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;font-family:\'Microsoft YaHei\';line-height:1.5;color:#337FE5;\"><strong>反光镜面镜</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';line-height:1.5;color:#999999;\"><span style=\"font-family:\'Microsoft YaHei\';font-size:14px;line-height:1.5;color:#999999;\">Code：421401</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;font-family:\'Microsoft YaHei\';\"><span style=\"font-size:14px;line-height:1.5;color:#999999;\"></span><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';line-height:1.5;color:#999999;\">忍者神龟款自由潜面镜</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';line-height:1.5;color:#999999;\"> 减少眼睛到面镜镜片的距离</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';line-height:1.5;color:#999999;\"> 提供较小的内部容量 开阔了视野</span> \r\n</p>\r\n<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';line-height:1.5;color:#999999;\"> 裙边设置低搭扣 方便使用</span><br />\r\n<p>\r\n	<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';line-height:1.5;color:#999999;\"> 颜色：黑色</span> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/06/14/c8690121-e793-4b97-b43b-1f0f5d2f31af.jpg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;line-height:1.5;color:#FF9900;\">价格：200元</span> \r\n</p>', 'uploadfiles/equip/equip_lwRp1445761735775.jpg', 'Mares STAR反光款', 'STAR反光镜面镜\r\n较小的内部容量 开阔的视野', 'ST01', 'ET01', null, '200', null, '6a040c09-5758-41f8-8910-bf556692e824', '', '');
INSERT INTO `dive_equip` VALUES ('f5a13d56-b891-4ad0-a741-2b09bad22b96', '2015-10-25 16:32:19', 'EB01', '<p>\r\n	<span style=\"font-size:16px;font-family:\'Microsoft YaHei\';\"><span style=\"font-size:18px;color:#337FE5;line-height:1.5;font-family:\'Microsoft YaHei\';\"><strong>Mares X-VU SF渔猎款</strong></span><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#999999;line-height:1.5;\"><span style=\"font-family:\'Microsoft YaHei\';font-size:14px;line-height:1.5;color:#999999;\">Code：42141</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;font-family:\'Microsoft YaHei\';\"><span style=\"font-size:14px;color:#999999;line-height:1.5;\"></span><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#999999;line-height:1.5;\">美学和光学的完美结合</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;font-family:\'Microsoft YaHei\';\"><span style=\"font-family:\'Microsoft YaHei\';font-size:14px;line-height:1.5;color:#999999;\">重量轻 &nbsp;更舒适</span><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#999999;line-height:1.5;\">液态双硅胶裙边 更贴合面部轮廓</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#999999;line-height:1.5;\">单一镜片与裙边粘合在一起</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#999999;line-height:1.5;\">面镜带安置在裙边上 并有快速调整扣</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#999999;line-height:1.5;\"> 减低镜框对面部的压力 但不降低密封性</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#999999;line-height:1.5;\">裙边可以卷折 方便携带</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:\'Microsoft YaHei\';color:#999999;line-height:1.5;\"> 颜色：黑色 棕色</span> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/06/14/edd8b68b-61fe-416d-ba4d-872740ec3890.jpg\" alt