INSERT INTO `dive_equip` VALUES ('f6a5d19d-37fc-4e5e-b548-428396364726', '2015-12-18 14:49:52', 'EB01', '<p>\r\n	<span style=\"font-size:18px;color:#337FE5;font-family:\'Microsoft YaHei\';\"><strong>X-VISION LiquidSkin</strong></span><br />\r\n<span style=\"font-size:14px;color:#999999;\">Code: 411045</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">新升级 最时尚</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">视野超广 增加20%</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">超出想象的配色</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">裙边设计人性化升级 舒适性密封性升级</span><br />\r\n<span style=\"font-size:14px;color:#999999;\">可配光学镜片</span> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/06/14/3e7a93c7-eae5-4617-80d4-88574bf596b3.jpg\" alt=\"\" /><img src=\"/attached/image/2016/06/14/73685b10-8336-418f-9896-87da727c905c.jpg\" alt=\"\" /><img src=\"/attached/image/2016/06/14/92ee4d90-2129-4cd8-8a0e-fb964ac64423.jpg\" alt=\"\" /><img src=\"/attached/image/2016/06/14/5e9c42f4-a4fe-447f-9c0d-dac9521128a9.jpg\" alt=\"\" /><img src=\"/attached/image/2016/06/14/1d106a9b-41af-4b26-adc4-2e8775beee30.jpg\" alt=\"\" /><img src=\"/attached/image/2016/06/14/b8aaea86-027f-479d-9bcf-a9b76d63f198.jpg\" alt=\"\" /><img src=\"/attached/image/2016/06/14/441fcaea-46dc-4f0b-a23c-9072b5038134.jpg\" alt=\"\" /><img src=\"/attached/image/2016/06/14/042de257-37b5-41c6-80d6-c892b817fd50.jpg\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span style=\"color:#999999;font-size:14px;line-height:1.5;\">创新设计的X-WISION更具吸引力。双硅胶LIQWIDSKIN是由两种硬度的硅胶组成裙边。靠近镜框硅胶质地偏硬，在水下起到支撑面部空间作用。贴近面部的裙边更柔软舒适。超乎想象的色彩搭配更具表现力。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#FF9900;\">价格：460元</span> \r\n</p>', 'uploadfiles/equip/equip_zLgB1450421392214.jpg', 'Mares X-VISION LiquidSkin', '超出想象的新配色\r\n裙边设计人性化升级', 'ST01', 'ET01', null, '460', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', 'CL001,CL013,CL015,CL017,CL025,CL027,CL028,CL030', 'SZ040');
