INSERT INTO `dive_equip` VALUES ('f7f5cc7d-df7b-46c1-ac23-5aa4e7745416', '2015-12-19 02:42:00', 'EB04', '<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';font-size:18px;color:#337FE5;\"><strong>AQUATICS VENOM</strong></span><span style=\"font-family:\'Microsoft YaHei\';font-size:18px;color:#337FE5;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">无以伦比的光学性能。</span> \r\n</p>\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 专利设计与流体力学的造型及独特的无框架设计。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 双层、双色硅胶裙边使面镜舒适安全。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 单片式、低容量设计畅通无阻的前视和宽视角。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 简单的紧缩来调整面镜带。</span><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 针对框架破损终生保修。</span><br />\r\n<img src=\"/attached/image/2015/12/19/a1a7ae22-ea45-4d74-a4ad-fd6e416fbc6f.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/19/3a18a221-6128-4698-9c79-2fcdd776155d.jpg\" alt=\"\" /><img src=\"/attached/image/2015/12/19/acc3389c-71b3-4d29-88c8-f7c4e4cd4361.jpg\" alt=\"\" /><br />\r\n<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\"> 在潜水面镜这个领域，Atomic Aquatica的Ultra Clear 镜片是一个前无古人的首创。之前的普遍的玻璃面镜所采用的玻璃，在生产阶段过程中都有肉眼可见的绿色的铁粒子残留。这些绿色的铁粒子残留对光线的穿透造成了极大的阻碍。</span><br />\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#999999;\">所有Atomic Aquatics面罩利用更高质量的光学玻璃，由德国肖特玻璃公司生产，极大地提升了水下的视觉感受。它的光线穿透率能达到96%，是最高的玻璃纯净度等级了。用户能享受到难以置信的清晰度和高透光度。这种本质上的改进，不是其它品牌面镜所宣称的那种清晰度所能媲美的。</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#FF9900;\">价格：2400元</span>\r\n</p>', 'uploadfiles/equip/equip_sbXz1450464120023.jpg', 'ATOMIC AQUATICS VENOM', '无以伦比的光学性能', 'ST01', 'ET01', null, '2400', null, '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', 'CL019,CL015,CL014', '');
