INSERT INTO `dive_store` VALUES ('000f7338-2f60-49d5-a31a-e1fb80366324', '2016-06-03 16:45:46', '亚洲_马尔代夫', 'uploadfiles/store/store_N0Mc1464946359733.jpg', '马尔代夫-卓美亚德瓦娜芙希岛酒店', '', '', '✈ Jumeirah Dhevanafushi', '<p>\r\n	<img src=\"/attached/image/2016/06/03/017fb558-cf57-4353-9c46-469b5c407454.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:24px;font-family:NSimSun;color:#337FE5;\"><strong>Jumeirah Dhevanafushi</strong></span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span style=\"font-family:NSimSun;line-height:1.5;color:#333333;\"><strong>度假村名称</strong></span><span style=\"font-family:NSimSun;line-height:1.5;color:#64451D;\">：</span><span style=\"font-family:NSimSun;line-height:1.5;color:#64451D;\">卓美亚德瓦娜芙希岛酒店</span>\r\n</p>\r\n<hr />\r\n<p>\r\n	<strong><span style=\"color:#333333;\">地点</span></strong><span style=\"color:#337FE5;font-family:NSimSun;line-height:1.5;\"><span style=\"color:#64451D;\">：马尔代夫 加弗阿利夫环</span><span style=\"color:#64451D;\">礁</span></span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<strong><span style=\"color:#333333;\">开业时间：</span></strong><span style=\"color:#64451D;\">2011年</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<span style=\"font-family:NSimSun;line-height:1.5;color:#333333;\"><strong>星级</strong></span><span style=\"color:#337FE5;font-family:NSimSun;line-height:1.5;\"><span style=\"color:#333333;\">：</span><span style=\"color:#64451D;\">6星</span></span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<span style=\"font-family:NSimSun;line-height:1.5;color:#333333;\"><strong>交通信息</strong></span><span style=\"color:#333333;font-family:NSimSun;line-height:1.5;\">：<span style=\"color:#64451D;\">距离马累机场300公里</span></span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<span style=\"color:#337FE5;font-family:NSimSun;line-height:1.5;\"><span style=\"color:#333333;\"><strong></strong></span><span style=\"color:#333333;\"><strong>教授语言：</strong><span style=\"color:#64451D;\">英语、中文</span></span></span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<span style=\"color:#333333;font-family:NSimSun;line-height:1.5;\"><strong>其它信息：<br />\r\n</strong></span> \r\n</p>\r\n<p class=\"p\" style=\"text-indent:0pt;vertical-align:baseline;\">\r\n	<span style=\"font-family:Tahoma;color:#64451D;font-size:9pt;\">卓美亚德瓦娜芙希岛酒店位于印度洋中心的马尔代夫南端，是体验宁静、愉悦生活的一方净土。</span><span style=\"font-family:Tahoma;color:#64451D;font-size:9pt;\">这处迷人的</span><span style=\"font-family:Tahoma;color:#64451D;font-size:9pt;\">避世圣地</span><span style=\"font-size:9pt;line-height:1.5;font-family:Tahoma;color:#64451D;\">横越两个岛屿，让您远离喧嚣纷扰，品味珍贵时光。苍翠繁茂的植物、晶莹剔透的海水和绵延迂回</span><span style=\"font-size:9pt;line-height:1.5;font-family:Tahoma;color:#64451D;\">白色沙滩，营造出</span><span style=\"font-size:9pt;line-height:1.5;font-family:Tahoma;color:#64451D;\">如诗如画的绝美环境。卓美亚德瓦娜芙希岛酒店堪称一片宁静天堂。</span><span style=\"font-size:9pt;line-height:1.5;font-family:宋体;color:#64451D;\">酒店</span><span style=\"font-size:9pt;line-height:1.5;font-family:Tahoma;color:#64451D;\">提供各种舒适的</span><span style=\"font-size:9pt;line-height:1.5;font-family:Tahoma;color:#64451D;\">设施和服务，为您打造与众</span><span style=\"color:#64451D;font-family:Tahoma;font-size:9pt;line-height:1.5;\">不同的入住体验。您可在泰丽丝水疗中心的精致餐厅享用丰富美食，或仅仅在珊瑚礁中畅游一番，恢复活力。</span>\r\n</p>\r\n<hr />\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/06/03/df194d38-d8c6-4db2-b4c4-d9544e5b71cb.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/06/03/f178a772-ae3c-4bda-952d-b78305dd6dfb.jpg\" alt=\"\" /> \r\n</p>\r\n<p class=\"p\" style=\"text-indent:0pt;vertical-align:baseline;\">\r\n	<strong></strong> \r\n</p>\r\n<p class=\"p\" style=\"text-indent:0pt;vertical-align:baseline;\">\r\n	<span style=\"font-family:微软雅黑;color:#333333;font-size:10.5pt;\"></span> \r\n</p>\r\n<p class=\"p\" style=\"text-indent:0pt;vertical-align:baseline;\">\r\n	<span style=\"font-family:微软雅黑;color:#333333;font-size:10.5pt;\"></span> \r\n</p>\r\n<img src=\"/attached/image/2016/06/03/cd53fa8f-3c91-4f14-910d-15d19