INSERT INTO `dive_store` VALUES ('1c37cf6a-483b-4bb8-8d6e-9833be63c67e', '2016-04-25 13:42:37', '亚洲_马来西亚', 'uploadfiles/store/store_KNwc1464852654928.jpg', '马来西亚-兰卡央潜水度假村', '', '', '✈ Lankayan Island Dive Resort', '<div style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/02/94651054-fce3-4f12-b1ac-b694b1bd1415.jpg\" alt=\"\" /><br />\r\n</div>\r\n<h1 style=\"text-align:center;\">\r\n	<span style=\"color:#337FE5;\">Lankayan Island Dive Resort</span> \r\n</h1>\r\n<p>\r\n	<span style=\"line-height:36px;color:#999999;\"><span style=\"font-family:\'Microsoft YaHei\';line-height:1;\"><strong><span style=\"color:#333333;font-family:NSimSun;\"><br />\r\n</span></strong></span></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:36px;color:#999999;\"><span style=\"font-family:\'Microsoft YaHei\';line-height:1;\"><strong><span style=\"color:#333333;font-family:NSimSun;\">度假村名称</span><span style=\"color:#333333;font-family:NSimSun;\">：</span></strong><span style=\"color:#333333;font-family:NSimSun;\"><span style=\"color:#64451D;\">兰卡央岛潜水度假村</span></span></span></span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<strong>地点：</strong><span style=\"color:#64451D;\">马来西亚、沙巴州山打根、兰卡央小岛</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<b><span style=\"color:#333333;font-family:NSimSun;\">开业时间</span><span style=\"color:#333333;font-family:NSimSun;\">：</span></b><span style=\"line-height:12px;color:#333333;font-family:NSimSun;\"></span><span style=\"line-height:12px;color:#333333;font-family:NSimSun;\"></span><span style=\"line-height:12px;font-family:NSimSun;color:#64451D;\">暂无</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<b><b><span style=\"color:#333333;font-family:NSimSun;\">星级</span></b></b><span style=\"color:#333333;font-family:NSimSun;\"></span><span style=\"color:#333333;font-family:NSimSun;\">：<span style=\"color:#64451D;\">5</span></span><span style=\"color:#64451D;font-family:NSimSun;\">星</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<b><span style=\"color:#333333;font-family:NSimSun;\">交通信息：</span></b><span style=\"color:#64451D;font-family:NSimSun;\">度假村距山打根</span><span style=\"font-family:NSimSun;color:#64451D;\"><span style=\"color:#64451D;\">国际机场52公里</span></span> \r\n</p>\r\n<hr />\r\n<b><b><span style=\"color:#333333;font-family:NSimSun;\">教授语言</span></b><span style=\"color:#999999;\"></span><span style=\"color:#333333;font-family:NSimSun;\">：</span></b><span style=\"line-height:1.5;color:#64451D;\"><span style=\"font-family:NSimSun;\">英语 中文\r\n<hr />\r\n</span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span><span></span> \r\n<p>\r\n	<span style=\"line-height:36px;color:#999999;\"><span style=\"line-height:1;color:#333333;font-family:NSimSun;\"><strong>其它信息</strong></span></span><strong>：</strong> \r\n</p>\r\n<p>\r\n	<span style=\"color:#64451D;font-family:tahoma, arial, sans-serif;line-height:22px;\"><span style=\"color:#64451D;font-family:tahoma, arial, sans-serif;line-height:22px;\"> </span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:9pt;color:#64451D;font-family:NSimSun;\">兰卡央岛是位于沙巴州山打根市外海的一个天然小岛。兰卡央是国家保护的岛屿，岛内有驻兵点。度假村属于一岛一酒</span><span style=\"color:#64451D;font-family:NSimSun;font-size:9pt;line-height:1.5;\">店的模式，属于私人开发，但要按国家要求进行周边的生态保育，所以度假村有小型博物馆，有海龟培育基地。兰卡央</span><span style=\"font-size:9pt;line-height:1.5;color:#64451D;font-family:NSimSun;\">度假村</span><span style=\"font-size:9pt;line-height:1.5;color:#64451D;font-family:NSimSun;\">小岛上被丛林覆盖并且有美丽的白色沙滩，提供２３间宽敞的海滨客房供度假使用。</span><span style=\"font-size:9pt;line-height:1.5;color:#4F4F4F;\"><span style=\"color:#64451D;font-family:NSimSun;\">这里无论是浮潜还是深潜都</span></span><span style=\"font-size:9pt;line-height:1.5;color:#64451D;font-family:NSimSun;\">非常方便，几分钟的船程即可到达潜点，每年的３</span><span style=\"font-size:9pt;line-height:1.5;color:#64451D;font-family:NSimSun;\">-</span><span style=\"font-size:9pt;line-height:1.5;color:#64451D;font-family:NSimSun;\">５月更有机会与鲸鲨亲密接触，这是每个潜水员来到这里的梦想。每年６</span><span style=\"font-size:9pt;line-height:1.5;color:#64451D;font-family:NSimSun;\">-</span><span style=\"color:#64451D;font-family:NSimSun;font-size:9pt;line-height:1.5;\">９月这里是海龟繁育的时候，你可以经常的看到小海龟破沙而出，游向大海。</span> \r\n</p>\r\n<hr />\r\n<p>\r\n	<img src=\"/attached/image/2016/06/02/dc2b830e-2dd0-4e28-9d57-1f05903abb67.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/02/a7fb0681-9639-4b15-bb76-0875126ade2b.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/02/bb1bf01b-5560-41f6-aab7-2d2fbd114367.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/02/d9e07ca9-ef60-4427-843b-fb1e95735837.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/02/0a8ccdb7-9b86-46cb-8fe9-b7a3a761acb7.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/02/41874b02-00c3-4426-8a37-e07a323e3ea7.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/02/36a6b70d-0486-4f04-afff-2dea0003cc5f.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/02/1b407443-cb21-45d0-84b7-b07fdcf7786f.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<img src=\"/attached/image/2016/06/02/586ae6bf-0e15-457c-b399-ba30192395a4.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<img src=\"/attached/image/2016/04/25/b1b3a2d4-a1ec-4ade-b310-a10b40f25faa.jpg\" alt=\"\" /> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', '1fc35d88-2a75-44ec-9e6c-8ade2f5f941e', '0');
